float PI = 3.14159265358979323846;
float d1;
float t1;

float soc(vec3 p) {
    vec3 n = normalize(sign(p+1e6));
    return min(min(dot(p.xy, n.xy), dot(p.yz, n.yz)), dot(p.xz, n.xz));
}

float sinc(float x, float k) {
    float a = PI * (float(k)*x-1.0);
    return sin(a)/a;
}

mat2 r2d(float a) {
    float sa=sin(a);
    float ca=cos(a);
    return mat2(ca,sa,-sa,ca);
}

vec2 mo(inout vec2 p, vec2 d) {
    vec2 q = p;
    q.x = abs(q.x) - d.x;
    q.y = abs(q.y) - d.y;
    if (q.y > q.x) q = q.yx;
    return q;
}

vec2 amod(vec2 p, float m) {
    float a=mod(atan(p.x,p.y), m)-m*.5;
    return vec2(cos(a), sin(a))*length(p);
}

float map(vec3 p) {
    float d = 1.0;
    float a = abs(p.y);
    p.yz *= r2d(sign(a) * 1.2);
    p.xz *= r2d(sign(a) * 0.0);
    p.xz = mo(p.xz, vec2((-d1 * 35.8) - 1., (d1 * 65.8) - 1.));
    p.zx = mo(p.xz, vec2((d1 * 2.) - 4., 0.0282));
    p.xz = amod(p.xz, (PI * 1.0) / 2.0);
    p.xz = max(abs(p.xz) - 2.2569, -1.29);
    p.x = mod(p.x, 0.3198 + (d1 * .21))-(0.3198 + (d1 * .21) *.5);
    p.y = mod(p.y + -sin(t1 * .023) * 1.2, 6.752) - 5.;
    d = min(d, soc(max(abs(p) - 0.172, 0.0096)));
    return (length(p * 0.0) - 0.0) * 1.0 - (d * -1.0);
}

vec3 calcNormal(in vec3 p, in int type, in float m1, in float m2, in float m3) {
    vec2 e = vec2(m2, m3) * m1;
    return normalize( e.xyy * map(p + e.xyy) + e.yyx * map(p + e.yyx) + e.yxy * map(p + e.yxy) + e.xxx * map(p + e.xxx) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    t1 = iTime;
    //t1 = (sin((iTime + 174.) * .1) * 15.) + 21.;
    d1 = sin(t1 * .013);
    vec2 st = (fragCoord.xy / iResolution.xy) * 2.05 - 1.;
    st.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3(st, 3.7287);
    vec3 rd = normalize(vec3(st + vec2(0.), -0.8572));
    vec3 mp;
    mp = ro;
    float md;
    for(int i=0; i<45; i++) {
        md = map(mp);
        mp += (rd * .6 + (-d1 * .1)) * md;
    }
    float b = length(ro - mp);
    float dA = 0.4799 - (b * 0.02) * 0.5232;
    float dB = 0.479 - (b * 0.03) * 0.523;
    dA = sinc(dA, 1.0);
    dB = sinc(dB, 1.0);
    vec3 p = ro + rd * (mp);
    vec3 lt = vec3(0.0, 0.0, 0.0);
    vec3 l;
    vec3 nm = calcNormal(p, 0, 4.5201, 1.0, -1.0);
    if (md < 0.015) {
        float dif = clamp(dot(nm, normalize(lt - p)), 0., 1.);
        dif *= 5.0 / dot(lt - p, lt - p);
        l = vec3(pow(dif, 0.1854));
    }
    vec3 lt2 = vec3(0.0, 0.0, 13.0);
    vec3 nm2 = calcNormal(p, 0, 2.8964, -1.0, -1.339);
    float dif2 = clamp(dot(nm2, normalize(lt2 - p)), 0., 1.);
    dif2 *= 4.0 / dot(lt2 - p, lt2 - p);
    vec3 lb = vec3(pow(dif2, 0.4545));
    float src1 = 1. * 0.0;
    float src2 = nm.x;
    float src3 = l.x;
    float src4 = lb.x * 0.4;
    vec3 c;
    c = mix(vec3(0.0), vec3(0.6117647,0.019607844,0.8627451), src1) + mix(vec3(0.0), vec3(1.0,0.0,0.0), src2);
    c = c + mix(vec3(0.0), vec3(0.31764707,0.5568628,1.0), src3);
    c = c + mix(vec3(0.0), vec3(0.05490196,0.0,1.0), src4);
    fragColor = vec4(c, 1.);
}

float PI = 3.14159265;
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
vec2 path(float z){
    float x = sin(z) + 2.0 * cos(z * 0.3) - 1.5 * sin(z * 0.12345);
    float y = cos(z) + 1.5 * sin(z * 0.3) + 2.0 * cos(z * 0.12345);
    return vec2(x,y);
}

float map1(vec3 p){
	//p = fract(p/2.0) * 6.0 - 3.0;
    vec2 o = path(p.z) / 4.0;
	float tBase = max(-length(p.xy - o) + 1.0 
                      //* (1.0 + sin(p.z) * 0.5)
                      ,length(p.xy - o) - 1.2 
                      //* (1.0 + sin(p.z) * 0.5)
                     );
    float tdonut = max(-length(p.xy - o) + 0.5
                      ,length(p.xy - o) - 0.6 
                     );
    tdonut = max(tdonut,abs(fract(p.z) - 0.5));
    float tTube1 = length(p.xy - o + vec2(0.3,0.3)) - 0.025;
    float tTube2 = length(p.xy - o + vec2(0.5,0.2)) - 0.025;
    float tTube3 = length(p.xy - o + vec2(0.2,0.5)) - 0.025;    
    float tTube4 = length(p.xy - o + vec2(-0.3,-0.3)) - 0.025;
    float tTube5 = length(p.xy - o + vec2(-0.5,-0.2)) - 0.025;
    float tTube6 = length(p.xy - o + vec2(-0.2,-0.5)) - 0.025;
    float tTube = min(min(tTube1,tTube2),tTube3);
    tTube = min(min(tTube,tTube4),min(tTube5,tTube6));

    float bound = 2.0;
    float tSplit = (fract(p.z)-0.5);
    tSplit = min(tSplit, abs((p.x - o.x))-0.15);
    tSplit = min(tSplit, abs((p.y - o.y))-0.15);

    return min(min(max(tBase,-tSplit),tdonut),tTube);
}
float temp(){
    return (floor(iTime) + min(sin(fract(iTime) * PI / 2.0) * 2.0,1.0));
}
float map2(vec3 p){
    p = mod(abs(p), 1.5) - 0.75;

    float rot = temp() * PI / 2.0;
    float size = sin(rot * 2.0 + PI / 4.0);
    p.xz *= mat2(cos(rot),-sin(rot),sin(rot),cos(rot));
    float sphere = length(p) - 0.5;
	float cube = max(max(abs(p.x) - 0.5,abs(p.y) - 0.5),abs(p.z) - 0.5);
    float obj = mix(sphere,cube,0.5 - size * 0.5);
    float poleY = length(p.xz) - 0.1 * size;
    float holes = 100000000.0;
    for(float i = -0.25; i < 0.50; i += 0.25){
        for(float j = -0.25; j < 0.50; j += 0.25){
        	float holeZ = length(p.xy - vec2(i,j)) - 0.075 * (1.0 - size);
    		float holeX = length(p.yz - vec2(i,j)) - 0.075 * (1.0 - size);
    		holes = min(holes,holeX);
        	holes = min(holes,holeZ);
        }
    }

    return max(min(obj,poleY),-holes);


}

float trace1 (vec3 o, vec3 r){
	float t = 0.0;
    for(int i = 0; i < 32; ++i){
        vec3 p = o + r * t;
        float d = map1(p);
        t += d * 0.5;
    }
    return t;
}
float trace2 (vec3 o, vec3 r){
	float t = 0.0;
    for(int i = 0; i < 32; ++i){
        vec3 p = o + r * t;
        float d = map2(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,1.0));

    float z = iTime * 4.0 ;
	vec2 a = path(z);
    vec3 o = vec3(a / 4.0,z);
    float t1 = trace1(o,r);
    float t2 = trace2(o,r);

    float fog1 = 1.0 / (1.0 + t1 * t1 * 0.2); 
    float fog2 = 1.0 / (1.0 + t2 * t2 * 0.2);

    vec3 fc = rand(floor(vec2(uv.x +rand(vec2(uv.x,floor(iTime * 5.0)))+ uv.y,uv.y * 10.0)) * floor(iTime * 16.0)) > 0.5 ? vec3(fog1,0,0) : vec3(0,0,fog2);

    // Output to screen
    fragColor = vec4(fc,1.0);
}

#define MAX_ITERS 10
#define MAX_MARCH 512
vec3 sq3 (vec3 v) {
    return vec3(
        v.x*v.x-v.y*v.y-v.z*v.z,
        2.*v.x*v.y,
        2.*v.x*v.z
    );
}
float squishy (float x) {
    return x*x*x;
}
float julia (vec3 p, vec3 c) {
    vec3 k = p;
    for(int i = 0; i<MAX_ITERS; i++) {
        k = sq3(k) + c;
        if(length(k)>2.) return 1.-squishy(float(i)/float(MAX_ITERS));
    }
    return -1.;
}
vec2 rotate2D (vec2 p, float angle){
    return vec2(p.x*cos(angle)-p.y*sin(angle), p.y*cos(angle)+p.x*sin(angle));\
}
float sdf (vec3 p) {
  	vec3 r = p;
    r.xz = rotate2D(r.xz, iTime*0.3);
    return julia(p,
                 r
                 //vec3(sin(iTime*0.3)*0.7, cos(iTime*0.4)*0.7, p.z));
    );
    //return length(p)-1.;
}
vec4 trace (vec3 o, vec3 r) {
    vec3 p = o;
    float t = 0.;
    float s;
    int i;
    for(i = 0; i<MAX_MARCH; i++) {
        p = o+r*t;
        s = sdf(p);
        t+=s*0.01;
		if(s<0.001||t>10.) break;
    }
    return vec4(p, float(i));
}
const float E = 0.0001;
vec3 estimateNormal (vec3 p) {
    return normalize(vec3(
        sdf(vec3(p.x+E, p.y, p.z))-sdf(vec3(p.x-E, p.y, p.z)),
        sdf(vec3(p.x, p.y+E, p.z))-sdf(vec3(p.x, p.y-E, p.z)),
        sdf(vec3(p.x, p.y, p.z+E))-sdf(vec3(p.x, p.y, p.z-E))
    ));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse-=0.5;
    float aspect = iResolution.y/iResolution.x;
    uv.x/=aspect;
    mouse.x/=aspect;
    vec3 col = vec3(1.);
    vec3 cam = vec3(0., 0., -2.);
    vec3 ray = normalize(vec3(uv.xy*1.3, 1.));

    vec3 rot = vec3(mouse.y*5., -mouse.x*3., 0.);

    cam.xz = rotate2D(cam.xz, rot.y);
    ray.xz = rotate2D(ray.xz, rot.y);

    cam.zy = rotate2D(cam.zy, rot.x);
    ray.zy = rotate2D(ray.zy, rot.x);


    vec4 t = trace(cam, ray);
    vec3 end = t.xyz;
    vec3 light = vec3(0., 0., -5.);
    vec3 toLight = normalize(light-end);
    vec3 norm = estimateNormal(end);
    float diffuse = max(0.,dot(toLight, norm))*1.0;
    vec3 refl = reflect(ray, norm);
    float specular = pow(max(0.0, dot(refl, toLight)), 16.0)*1.0;
    float d = length(end-cam);
    float fog = 1.0 / (1.0 + d*d*0.5);
    col.gb -= t.w/float(MAX_MARCH);
    col *= (diffuse+specular) + 1.2;
    col *= fog;

    // Output to screen
    fragColor = vec4(col,1.0);
}

