vec3 path(float t){
	float x = sin(t) + cos(2.0 * t);
    float y = cos(t) + cos(2.0 * t);
    float z = cos(t) + sin(2.0 * t);
    return vec3(x,y,z);
}
float map(vec3 p){

	float sphere = length(p - path(iTime)) - 0.5;
    float trail = 100000.0;
    for(float i = 0.0; i < 5.0; i += 0.1){
    	trail = min(trail,length(p - path(iTime - i)) - 0.1 * (5.0 - i));
    }
    return min(sphere,trail);
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


