       PrCylDf (vec3 (p.xy, p.z - 1.65 * rBot), 0.25 * rBot, 0.25 * rBot), 10. * w);
  } else {
    d = PrCylDf (vec3 (p.xy, p.z - 1.8 * rBot), 0.25 * rBot - 4. * w, 0.25 * rBot);
    DMIN (idCork);
    d = PrCylDf (vec3 (p.xy, p.z - 2. * rBot), 0.25 * rBot + 4. * w, 0.1 * rBot);
    DMIN (idCap);
