// Parameters
#define GRID_DIV    13.
#define ROT_PERIOD  45.
#define RECT_SIZE   vec2(0.6, 0.06)

// Constants
#define GRID_DIVN1  (GRID_DIV - 1.)
#define M_PI        3.1415926535
#define R           iResolution

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float rect(vec2 p, vec2 c) {
    vec2 d = abs(p) - c;
    return smoothstep(1., -1., max(d.x, d.y) * R.y);
}

#define triWave(n) abs(mod(n + GRID_DIVN1, 2. * GRID_DIVN1) - GRID_DIVN1) / max(GRID_DIV - 1., 1.)

float rects(vec2 p, float mode) {
    p += mode / GRID_DIV / 4.;   
    vec2 a = abs(p);
    if (max(a.x, a.y) > .5)
        return 0.;

    p *= GRID_DIV;   
    vec2 pi = round(p + .5 * GRID_DIVN1);

    float n = mode < 0. ? pi.x + pi.y : pi.x - pi.y;
    float angle = M_PI * (2. / ROT_PERIOD * iTime + triWave(n));
    if (mode > 0.)
        angle -= M_PI / 2.;
    p = rot(angle) * (fract(p + .5 * mod(GRID_DIV, 2.)) - .5);

    return rect(p / GRID_DIV, .5 * RECT_SIZE / GRID_DIV);
}

void mainImage(out vec4 o, vec2 p)
{
    vec2 pp = 1.1 * vec2(-1., 1.) * (p - .5 * R.xy) / R.y;

    float val = .008 + rects(pp, 1.) + rects(pp, -1.);

    val = pow(val, 1. / 2.2);

    vec2 uv = p.xy / R.xy;
    val *= 0.5 + 0.5 * pow(16.0 * uv.x * uv.y *
                           (1.0 - uv.x) * (1.0 - uv.y), 0.2);

    o = vec4(val, val, val, 1.0);
}

mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
float PI = 3.14159265;
vec3 path(float t){
	float x = clamp(sin(t) * pow(2.0,0.5),-1.0,1.0) * 0.75;
    float y = clamp(cos(t) * pow(2.0,0.5),-1.0,1.0) * 0.75;
    float z = 0.;
    return vec3(x,y,z);
}
float map(vec3 p){
    p.xz *= genRot(PI / 4.);
    p.yz *= genRot(PI / 4.);
    p.xy *= genRot(iTime);
	p.x = fract(abs(p.x) * 0.5) * 2.0;
    p.y = fract(abs(p.y) * 0.5) * 2.0;
    p.z = fract(abs(p.z) * 1.0) * 1.0;
    p -= vec3(1.0,1.0,0.5);
    float sphere = length(p - path(iTime)) - 0.25;
    float trail = 100000.0;
    for(float i = 0.0; i <4.0; i += 0.25){
    	trail = min(trail,length(p - path(iTime - i)) - 0.1);
    }
    return min(sphere,trail);
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 R = iResolution,
    r = normalize(vec3((2.*U - R.xy )/  R.y,1.2)),
    o = vec3(0,0,-2.5);
    vec4 data = trace(o,r);
    vec3 n = vec3(data.xyz);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.1);
    vec3 fc = t > 10000.0 ? vec3(0.8) : mix((vec3(data.x,data.y,data.z) + 1.0)/1.5
                                            ,vec3(0.0), - pow(dot(n,r),1.0));
	fc = mix(fc,vec3(1.0),1.0 - fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

float divScotFlag( vec2 uv) {
    float botRight = step( uv.x + uv.y, 1. );
    float botLeft = step( uv.x + 1. - uv.y, 1. );

    // Scottish flag ( sorta )
    float scotFlag = abs(botRight - botLeft);
    float divider = step( uv.x, .5);

    // Divided scottish flag
    return abs( divider - scotFlag);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y; // Aspect Ratio adjustment

     if (iMouse.z <= 0.){

    if (uv.x < -0.5) uv=(uv+0.6)*rotate2d(iTime/5.); //rotate

    else if (uv.x >= -.5 && uv.x < 0.)  uv.x+=sin(iTime/5.+uv.y);  //wave

    else if (uv.x >=0. && uv.x < 0.5)  uv*=(2./1.+sin(iTime/2.)); //zoom

    else if ( uv.x >= 0.5)    uv.y/=((uv.x))+cos(iTime/2.)+0.8; // flower

         }
          //mod instead of "pow" here is nice too
    else uv=(pow(abs(rotate2d(3.14*.25*sin(iTime/5.))*uv),iMouse.xy/iResolution.y)-iTime/20.);

    uv.y *= 5./3.; // adjust height width ratio

    // Checker pattern
	fragColor = vec4(
        vec3( divScotFlag( fract(uv * 4.) ) )+.4, 1.0);
}

