		0,0,1,-sph.center.z,
		0,0,0,1);

	mat4 T=transpose(TT);
    ray.o=vec3(T*vec4(ray.o,1.0));

    float a = dot( ray.d, ray.d );
    float b = 2.0*dot( ray.d, ray.o );
    float c = dot( ray.o, ray.o )-sph.radius*sph.radius;
