}


/////////////////////////////////////////////////////////////////////////////
// Computes (I_a * k_a) + k_shadow * I_source * [ k_d * (N.L) + k_r * (R.V)^n ].
// Input vectors L, N and V are pointing AWAY from surface point.
// Assume all vectors L, N and V are unit vectors.
/////////////////////////////////////////////////////////////////////////////
vec3 PhongLighting( in vec3 L, in vec3 N, in vec3 V, in bool inShadow, 
                    in Material_t mat, in Light_t light )
{
    if ( inShadow ) {
        return light.I_a * mat.k_a;
    }
    else {
        vec3 R = reflect( -L, N );
        float N_dot_L = max( 0.0, dot( N, L ) );
        float R_dot_V = max( 0.0, dot( R, V ) );
        float R_dot_V_pow_n = ( R_dot_V == 0.0 )? 0.0 : pow( R_dot_V, mat.n );

        return light.I_a * mat.k_a + 
               light.I_source * (mat.k_d * N_dot_L + mat.k_r * R_dot_V_pow_n);
    }
}


/////////////////////////////////////////////////////////////////////////////
// Casts a ray into the scene and returns color computed at the nearest
// intersection point. The color is the sum of light from all light sources,
// each computed using Phong Lighting Model, with consideration of
// whether the interesection point is being shadowed from the light.
// If there is no interesection, returns the background color, and outputs
// hasHit as false.
// If there is intersection, returns the computed color, and outputs
// hasHit as true, the 3D position of the intersection (hitPos), the
// normal vector at the intersection (hitNormal), and the k_rg value
// of the material of the intersected object.
/////////////////////////////////////////////////////////////////////////////
vec3 CastRay( in Ray_t ray, 
              out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg ) 
{
    // Find whether and where the ray hits some object. 
    // Take the nearest hit point.

    bool hasHitSomething = false;
    float nearest_t = DEFAULT_TMAX;   // The ray parameter t at the nearest hit point.
    vec3 nearest_hitPos;              // 3D position of the nearest hit point.
    vec3 nearest_hitNormal;           // Normal vector at the nearest hit point.
    int nearest_hitMatID;             // MaterialID of the object at the nearest hit point.

    float temp_t;
    vec3 temp_hitPos;
    vec3 temp_hitNormal;
    bool temp_hasHit;

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Try interesecting input ray with all the planes and spheres,
    //   and record the front-most (nearest) interesection.
    // * If there is interesection, need to record hasHitSomething,
    //   nearest_t, nearest_hitPos, nearest_hitNormal, nearest_hitMatID.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    for(int i=0;i<NUM_PLANES;i++)
    {
        temp_hasHit=false;
        temp_hasHit=IntersectPlane(Plane[i],ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal);
        if((temp_t<nearest_t) && temp_hasHit)
        {
            hasHitSomething = true;
            nearest_t=temp_t;
			tt=temp_t;
            nearest_hitNormal=temp_hitNormal;
            nearest_hitPos=temp_hitPos;
            nearest_hitMatID=Plane[i].materialID;
        }
    }
    for(int i=0;i<NUM_SPHERES;i++)
    {
        temp_hasHit=false;

        temp_hasHit=IntersectSphere(Sphere[i],ray, DEFAULT_TMIN , DEFAULT_TMAX , temp_t , temp_hitPos , temp_hitNormal);
        if((temp_t<nearest_t) && temp_hasHit)
        {
            hasHitSomething = true;
            nearest_t=temp_t;
			tt=temp_t;
            nearest_hitNormal=temp_hitNormal;
            nearest_hitPos=temp_hitPos;
            nearest_hitMatID=Sphere[i].materialID;
        }

    }

    // One of the output results.
    hasHit = hasHitSomething;
    if ( !hasHitSomething ) return BACKGROUND_COLOR;

    vec3 I_local = vec3( 0.0 );  // Result color will be accumulated here.

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Accumulate lighting from each light source on the nearest hit point. 
    //   They are all accumulated into I_local.
    // * For each light source, make a shadow ray, and check if the shadow ray
    //   intersects any of the objects (the planes and spheres) between the 
    //   nearest hit point and the light source.
    // * Then, call PhongLighting() to compute lighting for this light source.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////
	bool k_shadow=false;
    Ray_t shadowray;
	//vec3 I_local=vec3(0.0);

	for(int j=0;j<NUM_LIGHTS;j++)
	{
        shadowray.o=nearest_hitPos;
		shadowray.d=normalize((Light[j].position-nearest_hitPos));
		k_shadow=false;

        for(int i=0;i<NUM_SPHERES;i++)
		{
            if(k_shadow)break;

			k_shadow=IntersectSphere(Sphere[i],shadowray, DEFAULT_TMIN , distance(nearest_hitPos, Light[j].position));
		}
        if(!k_shadow)
		for(int i=0;i<NUM_PLANES;i++)
		{
             if(k_shadow)break;

			k_shadow=IntersectPlane(Plane[i],shadowray, DEFAULT_TMIN , distance(nearest_hitPos, Light[j].position));
		}

        vec3 L=shadowray.d;
		vec3 N=normalize(nearest_hitNormal);
		vec3 V=nearest_hitPos;
		I_local+=PhongLighting(  L,  N	,  -normalize(ray.d), k_shadow, 
        		Material[nearest_hitMatID],Light[j] );

	}


    // Populate output results.
    hitPos = nearest_hitPos;
    hitNormal = nearest_hitNormal;
    k_rg = Material[nearest_hitMatID].k_rg;

    return I_local;
}
//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
float insideCone(vec3 direction, float angle, vec3 o)
{
    float oz = dot(o, direction);
    vec3 oxy = o - direction * oz;
    float c = dot(oxy, oxy) / (angle * angle) - (oz * oz);
    return smoothstep(20.0, -50.0, c);
}

Range cone(vec3 direction, float angle, Ray_t ray)
{
    float dz = dot(ray.d, direction);
    float oz = dot(ray.o, direction);
    vec3 dxy = ray.d - direction * dz;
    vec3 oxy = ray.o - direction * oz;

    float a = dot(dxy, dxy) - (dz * dz * angle * angle);
    float b = dot(dxy, oxy) - (dz * oz * angle * angle);
    float c = dot(oxy, oxy) - (oz * oz * angle * angle);

    float p = 2.0 * b / a;
    float q = c / a;

    float r = p * p / 4.0 - q;

    Range result;
    result.start = BIG;
    result.end = -BIG;

    if (r >= 0.0)
    {
        float m = -p / 2.0;
        float sr = sqrt(r);

        if (c < 0.0)
        {
			// Inside
            if (m + sr < 0.0)
            {
                // Both solutions behind us
                result.start = 0.0;
                result.end = BIG;
            }
            else if (m - sr < 0.0)
            {
                // One solution behind us
                result.start = 0.0;
                result.end = m + sr;
            }
            else
            {
                // Both solutions ahead
                result.start = 0.0;
            	result.end = m - sr;
            }
        }
        else
        {
            // Outside
            if (m + sr < 0.0)
            {
                // Both solutions behind us
                return result;
            }
            else if (m - sr < 0.0)
            {
                // One solution behind us
                result.start = m + sr;
                result.end = BIG;
            }
            else
            {
                // Both solutions ahead
                result.start = m - sr;
            	result.end = m + sr;
            }
        }
    }

    return result;
}

Result plane(vec3 pos, vec3 normal, Ray_t ray)
{
    ray.o -= pos;

    float rdn = dot(ray.d, normal);
    float ron = dot(ray.o, normal);

    Result result;
    result.start.normal = normal;
    result.end.normal = normal;

    if (ron > 0.0)
    {
        // Outside
        result.start.dist = BIG;
        result.end.dist = -BIG;

        if (abs(rdn) > EPSILON)
        {
            float d = -ron / rdn;

            if (d > 0.0)
            {
                result.start.dist = d;
                result.end.dist = BIG;
            }
            else
            {
                result.start.dist = -BIG;
                result.end.dist = d;
            }
        }
    }
    else
    {
        // Inside
        result.start.dist = -BIG;
        result.end.dist = BIG;

        if (abs(rdn) > EPSILON)
        {
            float d = -ron / rdn;

            if (d > 0.0)
            {
                result.start.dist = -BIG;
                result.end.dist = d;
            }
            else
            {
                result.start.dist = d;
                result.end.dist = BIG;
            }
        }
    }
    return result;
}

float inverseSquare(vec3 p)
{
    return 1.0 / dot(p, p);
}

float inverseSquareAntiderivative(Ray_t ray, float t)
{
    vec3 o = ray.o;
    vec3 d = ray.d;

    // Shoutout to Wolfram Alpha
    float a = t * dot(d, d) + dot(d, o);
    float b1 = d.x * d.x * dot(o.yz, o.yz);
    float b2 = 2.0 * d.x * o.x * dot(o.yz, d.yz);
    float b3 = o.x * o.x * dot(d.yz, d.yz);
    float b4 = (o.y * d.z - d.y * o.z) * (o.y * d.z - d.y * o.z);
    float b = sqrt(b1 - b2 + b3 + b4);
    return atan(a / b) / b;
}

float inverseSquareIntegral(Ray_t ray, float start, float end)
{
    return inverseSquareAntiderivative(ray, end) - inverseSquareAntiderivative(ray, start);
}

vec3 getLight(vec3 pos)
{
    vec3 color = vec3(inverseSquare(pos) * OMNI_LIGHT * 2.0);
    for (int i = 0; i < NUM_LIGHTS; i++)
    {
        color += Light[i].c * inverseSquare(pos) * insideCone(Light[i].d, Light[i].a, pos);
    }
    return color;
}

vec3 renderVolumetric(Ray_t ray, float maxDist)
{
    vec3 color = vec3(inverseSquareIntegral(ray, 0.0, maxDist) * OMNI_LIGHT);

    for (int i = 0; i < NUM_LIGHTS; i++)
    {
        Range r = cone(Light[i].d, Light[i].a, ray);
        r.end = min(r.end, maxDist);

        if (r.end > r.start)
        {
            float boost = mix(1.0, 18.0, insideCone(Light[i].d, Light[i].a, ray.o));

            color += inverseSquareIntegral(ray, r.start, r.end) * Light[i].c * boost;
        }
    }

    return color;
}

vec3 floorTexture(vec3 pos)
{
    pos.z += pos.x * 0.25;
    return fract(pos.x * 0.1) > fract(pos.z * 0.1) ? vec3(1.0) : vec3(0.7);
}

float floorGloss(vec3 pos)
{
    pos.x += pos.z * 2.0;
    return texture(iChannel1, pos.xz * 0.2).x * 0.5 + 0.75;
}

vec3 renderScene(Ray_t ray)
{
    Result r = plane(vec3(0.0, -18.0, 0.0), vec3(0.0, 1.0, 0.0), ray);

    if (r.start.dist > 0.0 && r.start.dist < r.end.dist)
    {
        vec3 pos = ray.o + ray.d * r.start.dist;

        Ray_t reflectedRay;
        reflectedRay.o = pos;
        reflectedRay.d = ray.d * vec3(1, -1, 1);

        vec3 volumetric = renderVolumetric(ray, r.start.dist);
        vec3 reflectedVolumetric = renderVolumetric(reflectedRay, BIG);

        vec3 color = -normalize(pos).y * getLight(pos) * 30.0 * floorTexture(pos);
        float gloss = floorGloss(pos);

        return volumetric + mix(color, reflectedVolumetric, FLOOR_REFLECTION * gloss);
    }
    else
    {
    	return renderVolumetric(ray, BIG);
    }
}

vec3 toneMap(vec3 color)
{
    return 1.0 - exp(-color * EXPOSURE);
}

void setUpLights()
{
    mat4 m = rotateX(TAU * iTime * 0.05) * rotateY(TAU * iTime * 0.09);

    Light[0].d = normalize(m * vec4(1, 1, 1, 0)).xyz;
    Light[1].d = normalize(m * m * vec4(1, 1, -1, 0)).xyz;
    Light[2].d = normalize(m * vec4(1, -1, 1, 0)).xyz;
    Light[3].d = normalize(m * m * vec4(1, -1, -1, 0)).xyz;
    Light[4].d = normalize(m * vec4(0, INV_THETA, THETA, 0)).xyz;
    Light[5].d = normalize(m * m * vec4(0, INV_THETA, -THETA, 0)).xyz;
    Light[6].d = normalize(m * vec4(INV_THETA, THETA, 0, 0)).xyz;
    Light[7].d = normalize(m * m * vec4(INV_THETA, -THETA, 0, 0)).xyz;
    Light[8].d = normalize(m * vec4(THETA, 0, INV_THETA, 0)).xyz;
    Light[9].d = normalize(m * m * vec4(-THETA, 0, INV_THETA, 0)).xyz;

    Light[0].c = normalize(vec3(1, 1, 1) * 0.5 + 0.7);
    Light[1].c = normalize(vec3(1, 1, -1) * 0.5 + 1.0);
    //Light[2].c = normalize(vec3(1, -1, 1) * 0.5 + 3.0);
    Light[3].c = normalize(vec3(1, -1, -1) * 0.5 + 3.0);
    Light[4].c = normalize(vec3(0, INV_THETA, THETA) * 0.5 + 1.0);
    //Light[5].c = normalize(vec3(0, INV_THETA, THETA) * 0.7 + 0.8);
    Light[6].c = normalize(vec3(INV_THETA, THETA, 0) * 0.5 + 0.7);
    //Light[7].c = normalize(vec3(2.0, 1, 0.5) * 0.5 + 0.5);
    Light[8].c = normalize(vec3(0.3, 0.9, 0.9) * 0.5 + 0.3 );
    /*Light[9].c = normalize(vec3(-THETA, 0, INV_THETA) * 0.5 + 1.0);*/

    for (int i = 0; i < NUM_LIGHTS; i++)
    {
       Light[i].a = texture(iChannel0, vec2(float(i) * 0.18, 0.0)).x * 0.3 + 0.05;
    }
}


