/////////////////////////////////////////////////////////////////////////////
// Execution of fragment shader starts here.
// 1. Initializes the scene.
// 2. Compute a primary ray for the current pixel (fragment).
// 3. Trace ray into the scene with NUM_ITERATIONS recursion levels.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    	//get coords and direction
	vec2 uv=fragCoord.xy/iResolution.xy-.5;
	uv.y*=iResolution.y/iResolution.x;
	vec3 dir=vec3(uv*zoom,1.);
	float time=iTime*speed+.25;

	//mouse rotation
	float a1=.5+iMouse.x/iResolution.x*2.;
	float a2=.8+iMouse.y/iResolution.y*2.;
	mat2 rot1=mat2(cos(a1),sin(a1),-sin(a1),cos(a1));
	mat2 rot2=mat2(cos(a2),sin(a2),-sin(a2),cos(a2));
	dir.xz*=rot1;
	dir.xy*=rot2;
	vec3 from=vec3(1.,.5,0.5);
	from+=vec3(time*2.,time,-2.);
	from.xz*=rot1;
	from.xy*=rot2;

	//volumetric rendering
	float s=0.1,fade=1.;
	vec3 v=vec3(0.);
	for (int r=0; r<volsteps; r++) {
		vec3 p=from+s*dir*.5;
		p = abs(vec3(tile)-mod(p,vec3(tile*2.))); // tiling fold
		float pa,a=pa=0.;
		for (int i=0; i<iterations; i++) { 
			p=abs(p)/dot(p,p)-formuparam; // the magic formula
			a+=abs(length(p)-pa); // absolute sum of average change
			pa=length(p);
		}
		float dm=max(0.,darkmatter-a*a*.001); //dark matter
		a*=a*a; // add contrast
		if (r>6) fade*=1.-dm; // dark matter, don't render near
		//v+=vec3(dm,dm*.5,0.);
		v+=fade;
		v+=vec3(s,s*s,s*s*s*s)*a*brightness*fade; // coloring based on distance
		fade*=distfading; // distance fading
		s+=stepsize;
	}
	v=mix(vec3(length(v)),v,saturation); //color adjust
    v=v*0.003;

    InitScene (fragCoord);

    Material[4].k_d = v;


	//////////////////////////////////////////////////////////////////////
	setUpLights();



	//////////////////////////////////////////////////////////////////////
    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // Position the camera.
    vec3 cam_pos = vec3( 7.5*sin(0.25*iTime), 2.0+sin(0.25*iTime),7.5*cos(0.25*iTime)) ;
    vec3 cam_lookat = vec3( 0.25, 1.0, 0.0 );
    vec3 cam_up_vec = vec3( 0.0, 1.0, 0.0 );

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize( cam_pos - cam_lookat );
    vec3 cam_x_axis = normalize( cross(cam_up_vec, cam_z_axis) );
    vec3 cam_y_axis = normalize( cross(cam_z_axis, cam_x_axis));

    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay,Ray;
    pRay.o = cam_pos;
    pRay.d = normalize( pixel_pos.x * cam_x_axis  +  pixel_pos.y * cam_y_axis  +  pixel_pos_z * cam_z_axis );
	Ray.o = vec3(sin(iTime * 0.5) * 5.0, -12.5 + sin(iTime * 0.6) * 2.5, -25.0);
   	Ray.d = normalize(vec4((fragCoord.xy - iResolution.xy * 0.5) / iResolution.y, 0.7, 0.0)).xyz;

    // Start Ray Tracing.
    // Use iterations to emulate the recursion.

    vec3 I_result = vec3( 0.0 );
    vec3 compounded_k_rg = vec3( 1.0 );
    Ray_t nextRay = pRay;

    for ( int level = 0; level <= NUM_ITERATIONS; level++ ) 
    {
        bool hasHit;
        vec3 hitPos, hitNormal, k_rg;

        vec3 I_local = CastRay( nextRay, hasHit, hitPos, hitNormal, k_rg );

        I_result += compounded_k_rg * I_local;

        if ( !hasHit ) break;

        compounded_k_rg *= k_rg;

        nextRay = Ray_t( hitPos, normalize( reflect(nextRay.d, hitNormal) ) );
    }
	vec3 color = renderScene(Ray);
    color = toneMap(color);

	fragColor = vec4(color, 1.0);
    fragColor += vec4( I_result, 1.0 );
}

const float innerh = .17;
const float PI = 3.14159;

vec3 dtoa(vec3  d, float amount){
    vec3 a = clamp(1.0 / (clamp(d, 1.0/amount, 1.0)*amount), 0.,1.);
    return a;
}
mat2 rot2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}
float nsin(float x) { return sin(x)*.5+.5;}

void mainImage( out vec4 O, in vec2 P)
{
    float t = iTime + 100.;
    vec2 R = iResolution.xy;
    P -= R*.5;// center at origin
    vec2 uv = P / R.y;// aspect correct uv

    float ang = atan(uv.x, uv.y);
    // find a base height around the circle
    float h = 0.;
    float f = 1.;
    for (float i = 0.; i < 5.; ++ i) {
        h += nsin((ang+f)*i + t*f*.1);
        f *= -1.37;
    }
    h = h*h;

    // find ind height for 3 separate components
    vec3 h3 = h + .4*sin(t*vec3(1.,1.44,1.77) + ang*4.);
    vec3 d3 = length(uv) - innerh - h3*.02; // distance
    d3 = max(d3, -(length(uv) - innerh - h3 * .011));// donut
    d3 = min(d3, length(uv) - h3*0.01); // center

    vec2 shuv = uv+.06;
    vec3 dsh = length(shuv) - innerh - h3*.02; // distance
    dsh = max(dsh, -(length(shuv) - innerh - h3 * .011));// donut
    dsh = min(dsh, length(shuv) - h3*0.01); // center
    vec3 ash = dtoa(dsh, 30.)*.2;

    O = vec4(1);
    O *= 1.-min(ash.r, min(ash.g, ash.b));
    vec3 a3 = dtoa(d3, 30.);
    a3.rg *= rot2D(iTime*.4);
    a3 = clamp(a3,0.,1.);
    O.rgb = mix(O.rgb, a3, a3);
    O.rgb = mix(vec3(O.r+O.g+O.b)/3.,O.rgb,.5);
    vec2 N = P/R;
    O = pow(O, vec4(4.));
    O *= 1.-dot(N,N);
    O += (fract(sin(dot(R+t,N))*1e5)-.5)*.05;
    O.a = 1.;
}

