#define PI 3.14159265359
#define rot(a) mat2(cos(a + PI*0.5*vec4(0,1,3,0)))

float hash13(vec3 p3) {
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 scene(vec2 fragCoord, float time) {
    vec2 uv = fragCoord - iResolution.xy*0.5;
    uv /= iResolution.y;
    uv *= 3.0;
    uv *= rot(time*10.0 + (sin(time*2.0)*0.5+0.5)*10.0);
    uv = abs(uv);
    float sd = max(uv.x-0.5, uv.y-1.5);
    return vec3(smoothstep(0.0, 0.04, sd));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 result = vec3(0);

    bool motionBlur = true; // change this
    if ( motionBlur ) {
        #define BLUR 30
        for (int i = 0 ; i < BLUR ; i++) {
            float rnd = hash13(vec3(fragCoord, iFrame*100+i));
            float time = iTime + rnd / 60.0;
            result += scene(fragCoord, time);
        }
        result /= float(BLUR);
    } else {
        result = scene(fragCoord, iTime);
    }

    fragColor.rgb = pow(result, vec3(1.0/2.2));
    fragColor.a = 1.0;
}

#define IT 64
#define SD .001
#define MD 15.

mat2 rotate(float a) {
    float s = sin(radians(a));
    float c = cos(radians(a));
    return mat2(c,-s,s,c);
}

float dist(vec3 p) {
    vec3 d = abs(p)-2.;
    float b = length(max(d,0.)) + min(max(d.x,max(d.y,d.z)),0.)-.5;
    float s = length(p-vec3(sin(iTime*0.5)*2.5,sin(iTime*0.3),cos(iTime)*0.125)) - 3.;
    return max(b,-s);
}

vec3 raymarch(vec3 ro, vec3 rd) {
    vec3 p = ro;
    float td;
    for (int i=0; i < IT; i++) {
        float d = dist(p);
        td += d;
        p += rd*d;
        if (d < SD || td > MD) break;
    }
    return p;
}

vec3 normal(vec3 p) {
    vec2 o = vec2(SD,0);
    return normalize(vec3(
    dist(p+o.xyy)-dist(p-o.xyy),
    dist(p+o.yxy)-dist(p-o.yxy),
    dist(p+o.yyx)-dist(p-o.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,3,-8);
    vec3 rd = normalize(vec3(uv,.9));
    rd.yz *= rotate(22.);
    rd.xz *= rotate(iTime*-5.);
    ro.xz *= rotate(iTime*-5.);
    vec3 p = raymarch(ro,rd);
    vec3 c = length(p) < 5. ? normal(p)*.5+.5 : rd*.4+.4;
    fragColor = vec4(c,1.0);
}

float hash( vec2 p ) {return fract(sin(dot(p,vec2(127.1,311.7)))*43758.5453123);} //Pseudo-random
float smoothNoise( in vec2 p) { //Bilinearly interpolated noise (4 samples)
    vec2 i = floor( p ); vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    float a = hash( i + vec2(0.0,0.0) );
	float b = hash( i + vec2(1.0,0.0) );
	float c = hash( i + vec2(0.0,1.0) );
	float d = hash( i + vec2(1.0,1.0) );
    return float(a+(b-a)*u.x+(c-a)*u.y+(a-b-c+d)*u.x*u.y)/4.;
}
//Funciton to make the noise continuous while wrapping around angle 
float rotatedMirror(float t, float r){
    //t : 0->1
    t = fract(t+r);
    return 2.*abs(t-0.5);
}
//Some continous radial perlin noise
const mat2 m2 = mat2(0.90,0.44,-0.44,0.90);
float radialPerlinNoise(float t, float d){
    const float BUMP_MAP_UV_SCALE = 44.2;
    d = pow(d,0.01); //Impression of speed : stretch noise as the distance increases.
    float dOffset = -floor(iTime*10.)*0.1; //Time drift (animation)
    vec2 p = vec2(rotatedMirror(t,0.1),d+dOffset);
    float f1 = smoothNoise(p*BUMP_MAP_UV_SCALE);
    p = 2.1*vec2(rotatedMirror(t,0.4),d+dOffset);
    float f2 = smoothNoise(p*BUMP_MAP_UV_SCALE);
    p = 3.7*vec2(rotatedMirror(t,0.8),d+dOffset);
    float f3 = smoothNoise(p*BUMP_MAP_UV_SCALE);
    p = 5.8*vec2(rotatedMirror(t,0.0),d+dOffset);
    float f4 = smoothNoise(p*BUMP_MAP_UV_SCALE);
    return (f1+0.5*f2+0.25*f3+0.125*f4)*3.;
}
//Colorize function (transforms BW Intensity to color)
vec3 colorize(float f){
    f = clamp(f*.95,0.0,1.0);
    vec3 c = mix(vec3(0,0,1.1), vec3(0,1,1), f); //Red-Yellow Gradient
         c = mix(c, vec3(1,1,1), f*4.-3.0);      //While highlights
    vec3 cAttenuated = mix(vec3(0), c, f+0.1);       //Intensity ramp
    return cAttenuated;
}
