    float d = dot(uv,uv); //Squared distance
    float t = 0.5+atan(uv.y,uv.x)/6.28; //Normalized Angle
    float v = radialPerlinNoise(t,d);
    //Saturate and offset values
    v = -2.5+v*4.5;
