#define AA    0  // optional antialiasing

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv3 (vec3 p);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec2 Hashv2v2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

mat3 flMat;
vec3 flPos, sunDir, qHit;
vec2 gId, obOff, obRand, csProp;
float dstFar, tCur, trSzFac, hgSize, treeHt;
int idObj;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec3 TrackPos (float t)
{
  return vec3 (8. * cos (0.1 * t) + 0.1 * t, 0., t);
}

vec3 TrackVel (float t)
{
  return vec3 (-0.1 * 8. * sin (0.1 * t) + 0.1, 0., 1.);
}

vec3 TrackAcc (float t)
{
  return vec3 (-0.1 * 0.01 * 8. * cos (0.1 * t), 0., 0);
}

float ObjCDf (vec3 p)
{
  vec3 q;
  float dMin, d, r, y;
  dMin = dstFar;
  if (trSzFac > 0.) {
    dMin /= trSzFac;
    p.xz -= HexToPix (gId * hgSize) + obOff;
    p /= trSzFac;
    q = p;
    q.y -= treeHt;
    y = 0.5 * (1. + q.y / treeHt);
    r = 0.35 * (1. - 0.5 * obRand.y) * (1. - 0.9 * y * y);
    r *= 1. + 0.1 * sin (24. * (1. + obRand.y) * pi * y);
    r += 0.2 * (1. - smoothstep (0., 0.1, y));
    q.xz += Rot2D (vec2 (0.2 + 0.2 * obRand.x, 0.),
       12. * pi * sign (obRand.x - 0.5) * (0.3 + 0.7 * obRand.y) * y + pi * obRand.x);
    q.x += 0.5 * (0.4 + 0.6 * obRand.y) * y;
    q.xz = abs (q.xz) - 0.3 * r;
    d = PrCylDf (q.xzy, r, treeHt);
    DMINQ (1);
    q = p;
    q.y -= 0.05;
    q.xz += Rot2D (vec2 (0.2 + 0.2 * obRand.x, 0.), pi * obRand.x);
    q.xz = abs (q.xz) - 0.3 * r - 0.4;
    d = max (PrSphDf (q, 0.12), - q.y);
    DMINQ (2);
    dMin *= 0.6 * trSzFac;
  }
  return dMin;
}

float PathDst (vec2 p)
{
  float s;
  s = abs (p.x - TrackPos (p.y).x) - 1.8;
  return s * step (0., s) * step (0.5, Fbm2 (0.5 * p));
}

void SetObjConf ()
{
  float h;
  obRand = Hashv2v2 (73.3 * gId + 77.);
  h = PathDst (HexToPix (gId * hgSize));
  if (h == 0. || obRand.x + obRand.y < 0.6) trSzFac = 0.;
  else {
    trSzFac = hgSize * 0.5 * sqrt3 * (0.6 + 0.4 * smoothstep (0., 1., h)) *
       (0.75 - 0.25 * clamp (3. * (obRand.x - 0.5), -1., 1.));
    obOff = hgSize * max (0., 0.4 * sqrt3 - trSzFac) * obRand.x *
       sin (2. * pi * obRand.y + vec2 (0.5 * pi, 0.));
  }
}

float ObjCRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = 0; j < 220; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetObjConf ();
    }
    d = ObjCDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < 0. || p.y > 2. * treeHt) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjCNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjCDf (p + e.xxx), ObjCDf (p + e.xyy), ObjCDf (p + e.yxy), ObjCDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjCSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  gIdP = vec2 (-99.);
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetObjConf ();
    }
    h = ObjCDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.1, 0.3);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d;
  const float flSzFac = 0.5;
  dMin = dstFar;
  dMin /= flSzFac;
  qq = flMat * (p - flPos) / flSzFac;
  qq.xz = vec2 (qq.x - qq.z, qq.z + qq.x) / sqrt (2.);
  q = qq;
  q.y -= 0.05;
  d = PrRoundCylDf (q.xzy, 0.2, 0.03, 0.07);
  DMIN (3);
  q -= vec3 (-0.7, -0.05, 0.7);
  d = PrRoundCylDf (q.xzy, 0.07, 0.04, 0.12);
  DMIN (4);
  q = qq;
  q.xz = abs (q.xz) - 0.7;
  d = min (PrCylAnDf (q.xzy, 0.5, 0.05, 0.05), PrCylDf (q.xzy, 0.1, 0.03));
  DMIN (3);
  q.xz = Rot2Cs (q.xz, csProp);
  d = PrCylDf (q, 0.02, 0.415);
  DMIN (6);
  q = qq;
  q.xz = abs (q.xz);
  q -= vec3 (0.3, -0.07, 0.3);
  d = PrRoundCylDf (q.xzy, 0.05, 0.03, 0.15);
  DMIN (3);
  q -= vec3 (-0.3, 0.13, -0.3);
  q.xz = vec2 (q.x - q.z, q.z + q.x) / sqrt (2.);
  d = min (PrRoundCylDf (q, 0.05, 0.02, 1.), PrRoundCylDf (q.zyx, 0.05, 0.02, 1.));
  DMIN (3);
  q = qq;
  q.y -= -0.05;
  d = PrSphDf (q, 0.17);
  DMIN (5);
  return flSzFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.1, 0.3);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = 0.7 * mix (vec3 (0.4, 0.5, 0.7), vec3 (0.3, 0.45, 0.55),
       smoothstep (-0.02, 0.01, rd.y));
  } else if (rd.y > 0.) {
    q = 0.005 * (ro.xz + tCur * vec2 (0.5, 2.) + ((200. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.1, 0.8, ff);
    fd = smoothstep (0.1, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.8 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    fd = smoothstep (0.01, 0.1, rd.y);
    col = mix (mix (vec3 (0.8, 0.8, 0.75), vec3 (0.4, 0.5, 0.8), 0.3 + 0.7 * fd), clCol,
       0.1 + 0.9 * f * fd);
  } else {
    col = vec3 (0.1, 0.2, 0.1);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, roo, vn;
  vec2 vf;
  float dstObj, dstObjC, dstObjM, dstGrnd, sh, f, a;
  int idObjC;
  bool isSky, isGrnd;
  isSky = false;
  isGrnd = false;
  sh = 1.;
  csProp = sin (3.3 * 2. * pi * tCur + vec2 (0.5 * pi, 0.));
  dstObjC = ObjCRay (ro, rd);
  idObjC = idObj;
  roo = ro;
  dstGrnd = dstFar;
  dstObjM = ObjRay (ro, rd);
  dstObj = min (dstObjM, dstObjC);
  vf = vec2 (0.);
  if (dstObj < dstFar) {
    if (dstObjM < dstObjC) {
      ro += dstObjM * rd;
      vn = ObjNf (ro);
      if (idObj == 3) col4 = vec4 (1., 0.9, 0.1, 0.5);
      else if (idObj == 4) col4 = mix (vec4 (0.3, 0.3, 1., 0.2),
         vec4 (1., 0., 0., 0.2), step (0., sin (10. * tCur)));
      else if (idObj == 5) col4 = vec4 (0.1, 0.1, 0.1, 1.);
      else if (idObj == 6) col4 = vec4 (1., 1., 0.5, 0.5);
    } else {
      ro += dstObjC * rd;
      gId = PixToHex (ro.xz / hgSize);
      vn = ObjCNf (ro);
      a = atan (qHit.z, - qHit.x) / pi;
      if (idObjC == 1) {
        f = smoothstep (-0.9, -0.7, qHit.y / treeHt);
        col4 = mix ((1. - 0.6 * obRand.x) * vec4 (0.6, 0.4, 0., 0.),
           vec4 (0., 0.4 + 0.6 * obRand.y, 0.1, 0.05), f) *
           (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.05, mod (8. * a, 1.)));
        vf = vec2 (16., (3. - 2.5 * f) * (1. - smoothstep (0.1, 0.3, dstObjC / dstFar)));
      } else if (idObjC == 2) {
        col4 = mix (vec4 (0.8, 0.1, 0., 0.3), vec4 (0.7, 0.7, 0., 0.3), step (0.6, obRand.y)) *
           (0.5 + 0.5 * SmoothBump (0.1, 0.9, 0.05, mod (4. * a, 1.)));
        vf = vec2 (32., 1.);
      }
    }
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    col4 = mix (vec4 (0.3, 0.1, 0., 0.), vec4 (0.05, 0.3, 0., 0.), Fbm2 (2. * ro.xz));
    vn = vec3 (0., 1., 0.);
    vf = vec2 (4., 12. * (1. - smoothstep (0.1, 0.3, dstGrnd / dstFar)));
    isGrnd = true;
  } else isSky = true;
  if (! isSky) {
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    sh = min (ObjCSShadow (ro, sunDir), ObjSShadow (ro, sunDir));
    col = col4.rgb * (0.2 + 0.1 * max (dot (vn, - sunDir), 0.) +
       0.8 * sh * max (dot (vn, sunDir), 0.)) + col4.a * smoothstep (0.8, 1., sh) *
       pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col = mix (col, BgCol (roo, rd), smoothstep (0.8, 0.95, min (dstGrnd, dstObj) / dstFar));
  } else col = BgCol (ro, rd);
  return clamp (col, 0., 1.);
}

void FlyerPM (float t)
{
  vec3 vd, cb, sb;
  float flYaw, flRol;
  flPos = TrackPos (t);
  vd = TrackVel (t);
  flYaw = -0.5 * pi - atan (vd.z, - vd.x);
  flRol = 60. * TrackAcc (t).x;
  cb = cos (vec3 (-0.1 * pi, flYaw, flRol));
  sb = sin (vec3 (-0.1 * pi, flYaw, flRol));
  flMat = mat3 (1., 0., 0., 0., cb.x, - sb.x, 0., sb.x, cb.x) *
          mat3 (cb.z, - sb.z, 0., sb.z, cb.z, 0., 0., 0., 1.) *
          mat3 (cb.y, 0., - sb.y, 0., 1., 0., sb.y , 0., cb.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvs, ori, ca, sa, mMid, ut, mSize;
  float el, az, asp, winHt, vuId, zmFac, sr, spd, s;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  winHt = 0.85;
  mSize = (1./5.) * vec2 (asp, 1.) * winHt;
  mMid = vec2 (asp * (1. - mSize.y), - winHt + mSize.y);
  ut = abs (uv - mMid) - mSize;
  vuId = 0.;
  if (max (ut.x, ut.y) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuId = 1.;
  }
  tCur = mod (tCur, 2400.) + 30. * floor (dateCur.w / 7200.);
  hgSize = 1.;
  treeHt = 10.;
  spd = 5.;
  ro = TrackPos (spd * tCur);
  ro.y = 0.3 * treeHt;
  vd = TrackVel (spd * tCur);
  el = -0.05 * pi;
  az = atan (vd.x, vd.z);
  if (mPtr.z > 0.) {   
    el += 0.4 * pi * mPtr.y;
    az += 2.2 * pi * mPtr.x;
  }
  if (vuId == 1.) az += pi;
  s = abs (az) > 0.5 * pi ? -1. : 1.;
  FlyerPM (spd * tCur + 6. * s);
  flPos.y = (0.25 + 0.1 * s * sin (0.1 * tCur)) * treeHt;
  ori = vec2 (clamp (el, -0.5 * pi, 0.45 * pi), az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  dstFar = 100.;
  sunDir = normalize (vec3 (0., 1., -0.7));
  sunDir.xz = Rot2D (sunDir.xz, 0.3 * pi * sin (0.1 * tCur));
  zmFac = 2.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  if (abs (uvs.y) < winHt) {
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = 0.; a < naa; a ++) {
      rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), zmFac));
      col += (1. / naa) * ShowScene (ro, rd);
    }
  }
  if (max (ut.x, ut.y) < 0. && min (abs (ut.x), abs (ut.y)) * canvas.y < 2.)
     col = vec3 (0., 0., 0.4);
  if (vuId == 0.) col *= 1. - smoothstep (0.99 * winHt, winHt, abs (uvs.y));
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

const int n = 800;
const float rate = 7.;
const float lineThickness = 2.2;
const float colours = 0.05; // proportion of cells to colour in
const bool zoom = true;

const float phi = 1.6180339887498948;
const float tau = 6.2831853071795865;

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    float penOut = lineThickness/iResolution.y;
    float penIn = (lineThickness-2.8)/iResolution.y;

    float t = iTime*rate;

    fragColour = vec4(0,0,0,1);

    float scale = sqrt(float(n));
    if ( zoom ) scale = min( scale, pow((iTime+7.)*rate*.5,.6) ); // keep the edgemost points in shot as we zoom

    float closest = 1e38;
    float closest2 = 1e38;
    for ( int i=0; i < n; i++ )
    {
        float f = float(i);
        f += fract(t);
        float r = sqrt(f/128.);
        r *= 13./scale;
        float a = fract((f-t)*phi)*tau;
        vec2 pos = r*vec2(sin(a),cos(a));

        vec3 col = sin(vec3(3,1,6)*(float(i)-floor(t)))*.5+.5;
        if ( fract(col.y*64.) > colours ) col = vec3(1);

        float l = length(pos-uv);

        // add a ring to help me track size (so it doesn't look like we're zooming out)
        //col *= smoothstep(penIn,penOut,abs(l/scale-.001)*scale);

        if ( i == 0 ) l += smoothstep(1.,0.,fract(t))*1.2/scale; // grow the new point
		if ( l < closest )
        {
            if ( closest < closest2 ) closest2 = closest;
            closest = l;
			fragColour.rgb = col; // *(1.-l*sqrt(float(n)));
        }
        else if ( l < closest2 )
        {
            closest2 = l;
        }
        fragColour.rgb = mix(fragColour.rgb,vec3(0),smoothstep(penOut,penIn,length(pos-uv)));
    }

    // cell borders
    fragColour.rgb *= smoothstep(penIn,penOut,(closest2-closest));//*scale);
}

// Commutative smooth minimum function. Provided by Tomkh and 
// taken from Alex Evans's (aka Statix) talk: 
// http://media.lolrus.mediamolecule.com/AlexEvans_SIGGRAPH-2015.pdf
// Credited to Dave Smith @media molecule.
float smin(float a, float b, float r)
{
   float f = max(0., 1. - abs(b - a)/r);
   return min(a, b) - r*.25*f*f;
}

void mainImage( out vec4 c, vec2 g )
{
	vec2 v = g / iResolution.y;
    vec2 p = g /= iResolution.y / 3., kp = vec2(0), kid = vec2(0);;

	g += iTime * .25;

    float d = 1e5, kd = d, ld = d;

    for(int x=-3;x<=3;x++)
    for(int y=-3;y<=3;y++)
    {	
        vec2 p = vec2(x,y);
        vec2 id = fract(sin((floor(g)+p)*mat2(2,5,5,2)));
        vec2 an = sin( iTime * 0.5 + 10. * id );
		p += .5 + .35 * an - fract(g);
        p *= 10.;

		d = smin(d, dot(p,p), 100.);
		if (d < 10.)
		{
			d = dot(p,p);
			float t = iTime * 5.*0.;
			kd = sin(ld * 0.5 - t) * ld;
			kid = id;
        }
		ld = d * 2.;
    }

	c = kd * vec4(1, floor(kid + 1.5) * 0.05, 1);
}

#define mouse (iMouse.xy/iResolution.xy-.5)
const float det=.003;
const float maxdist=30.;
float l=0.;
mat2 rotm;
vec3 basecol=vec3(.5,.5,1.);

mat3 lookat(vec3 dir, vec3 up){
    dir=normalize(dir);vec3 rt=normalize(cross(dir,normalize(up)));
    return mat3(rt,cross(rt,dir),dir);
}

mat2 rot2D(float a) {
    a=radians(a);
	float s=sin(a);
    float c=cos(a);
    return mat2(c,s,-s,c);
}


float de(vec3 p) {
    p.xz*=rot2D(mouse.x*360.);
    p=abs(5.-mod(p+5.,10.));
    float md=100.;
    float s=1.25;
    float sc=1.;
    vec3 pc;
	vec3 mp=vec3(100.);
    float rot=sin(iTime*.1)*20.;
    for (int i=0; i<8; i++) {
        p.xy=abs(p.xy); 
        p=p*s-1./sc;
        sc*=s;
        p.xz*=rotm;
        p.yz*=rot2D(30.+rot);
        float d=length(p.xz+sin(p.y)*.5)-.2/sc;
		mp=min(mp,abs(p));
        if (d<md) {
        	md=d;
			pc=p;
        }
    }
    l=mod(pc.y*.05-iTime*.1,.5)*2.;
    return md/sc;
}

vec3 march(vec3 from, vec3 dir) {
	vec3 p, col=vec3(0.);
    float totdist=0., d;
    for (int i=0; i<100; i++) {
    	p=from+totdist*dir;
        d=de(p);
    	totdist+=max(det,d);
        if (totdist>maxdist||length(col)>.3) break;
        col+=max(0.,det-d)*l;
    }
	col=.96-col*2.5*vec3(3.,2.,1.);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy-.5;
    uv.x*=iResolution.x/iResolution.y;
    rotm=rot2D(-90.);
    vec3 dir=normalize(vec3(uv,.7));
    vec3 from=vec3(1.,2.,-5.);
    from.xz*=rot2D(iTime*3.);
    from.yz*=rot2D(iTime);
    dir=lookat(-from,vec3(.5,1.,0.))*dir;
	vec3 col=march(from, dir);   
	col=mix(vec3(1.),col,min(1.,iTime*.2));
    col=min(col,1.-smoothstep(.85,1.,abs(1.-mod(uv.y*60.,2.)))*.4);
    fragColor = vec4(col,1.0);
}

//Rewritten as of 27-09-2019

#define it 1024.
#define pr .00001
#define md 200.

mat2 rotate(float a) {
    float s = sin(radians(a));
    float c = cos(radians(a));
    return mat2(c,-s,s,c);
}

//Menger sponge SDF by fb39ca4
//https://www.shadertoy.com/user/fb39ca4

float sdCross(vec3 p) {
	p = abs(p);
	vec3 d = vec3(max(p.x, p.y),
				  max(p.y, p.z),
				  max(p.z, p.x));
	return min(d.x, min(d.y, d.z)) - (1.0 / 3.0);
}

float sdCrossRep(vec3 p) {
	vec3 q = mod(p + 1.0, 2.0) - 1.0;
	return sdCross(q);
}

float sdCrossRepScale(vec3 p, float s) {
	return sdCrossRep(p * s) / s;	
}

float sdScene(vec3 p) {
    float s = 1.0;
	float d = 0.0;
	for (int i = 0; i < 5; i++) {
		d = max(d, -sdCrossRepScale(p, s));
		s *= 3.0;
	}
	return d;
}

vec4 raymarch(vec3 ro, vec3 rd) {
    vec3 p = ro;
    float d;
    float i;
    for (i=0.; i < it; i++) {
        d = sdScene(p);
        p += rd*d;
        if (d < pr) break;
    }
    return vec4(p,i);
}

vec3 normals(vec3 p) {
    vec2 o = vec2(pr,0.0);
    return normalize(vec3(
        sdScene(p+o.xyy)-sdScene(p-o.xyy),
        sdScene(p+o.yxy)-sdScene(p-o.yxy),
        sdScene(p+o.yyx)-sdScene(p-o.yyx)));
}

float ambientOcclusion(vec3 p, vec3 n, float s, float iterations, float intensity) {
    float ao = 0.0;
    for (float i = 1.0; i <= iterations; i++) {
        float d = s * i;
        ao += max((d - sdScene(p + n * d)) / d, 0.0);
    }
    return 1.0 - ao * intensity;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.y *= iResolution.y/iResolution.x;

    vec4 mouse = vec4(iMouse.xy / iResolution.xy * 2.0 - 1.0,iMouse.zw); //Mouse input

    vec3 ro = vec3(0.0,0.0,mod(iTime,2.0));
    vec3 rd = normalize(vec3(uv,0.6));
    rd.xy *= rotate(iTime*15.);

    vec3 col = vec3(1.);

    vec4 rm = raymarch(ro,rd);
    vec3 p = rm.xyz;
    vec3 n = normals(p);
    col = mix(vec3(ambientOcclusion(p, n, 0.4, 5.0, 0.2)), vec3(0.6,0.2,0.8), length(p-ro)/5.0) + rm.w / it;

    fragColor = vec4(col,1.0);
}

struct Sphere{
    vec3 pos;
    vec3 col;
    float radius;
};

struct Plane{
    float height;
    vec3 col;
};

struct Ray{
    vec3 col;
    vec3 origin;
    vec3 direction;
    vec3 pos;
};

int nearestSphere(Sphere S, Plane P, Ray R){
    if(distance(R.pos, S.pos) - S.radius < (R.pos.z < 90. ? abs(R.pos.y - P.height) : 1000.)){
        return 0;
    }
    else{
        return 1;
    }
}

vec3 lightDirection = vec3(1., 1., -1.) * 250.;
// vec3 lightDirection = vec3(1.) * 2.;

void df(Sphere S, Plane P, inout Ray R){
    float depth = 0.;
    for(int i = 0; i < 200; i++){
        R.pos = R.origin + R.direction * depth;

        int index = nearestSphere(S, P, R);
        // index = -1;
        // then sphere < plane
        if(index == 0){
            float dist = distance(R.pos, S.pos) - S.radius;
            depth += dist;
            if(dist < 0.0001){
                R.col = S.col * clamp(dot(R.pos - S.pos, normalize(lightDirection) * 1.5), .05, 1.);
                break;
            }
        }   // then plane < sphere
        else{
            float dist = abs(R.pos.y - P.height);
            depth += dist;
            if(dist < 0.0001){
                R.col = P.col;
                R.col *= (1. - (mod(floor(R.pos.x * 6.) + floor(R.pos.z * 6.), 2.) * .2));
                float a = pow(length(R.pos - lightDirection), 2.);
                float b = 2. * dot(lightDirection - S.pos, R.pos - lightDirection);
                float c = pow(length(lightDirection - S.pos), 2.) - pow(S.radius, 2.);
                float d = pow(b, 2.) - 4. * a * c;
                if(d >= 0.){
                    // R.col *= .7;
                    // R.col *= clamp(clamp(1. / (d * d * d), 0., 1.)
                    //        * pow(distance(R.pos, S.pos), 2.), 0., 1.);
                    R.col *= clamp(distance(R.pos, S.pos) * .7 - 1., 0.05, 1.);
                }
                R.col *= clamp(dot(lightDirection, vec3(0, 1, 0)), 0., 1.) * .9;
                // if(R.pos.z > 3. && 3.1 > R.pos.z) R.col = vec3(1.);
            }
        }
    }
}


float rand(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

mat3 rotateY(float t){
    return mat3(
        cos(t), 0, sin(t),
        0, 1, 0,
        -sin(t), 0, cos(t)
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 p = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);

    // float screenZ = sin(iTime) - 1.;
    float screenZ = -1.;
    Ray ray;
    ray.origin = vec3(0, 0, screenZ);
    ray.direction = normalize(vec3(p, 0) - ray.origin) * rotateY(sin(iTime * .3));
    ray.pos = ray.origin;
    ray.col = vec3(0, 0, .05);

    Sphere sphere;
    sphere.col = vec3(1.);
    sphere.pos = vec3(1., 0, 2.);
    sphere.radius = 1.;

    Plane plane;
    plane.height = -1.;
    plane.col = vec3(1.);

    // if((rand(p) + rand(p+vec2(1.)) + rand(p+vec2(2.))) * .333 > .95){
    //     ray.col = vec3(1.);
    // }

    df(sphere, plane, ray);

    fragColor = vec4(ray.col, 1.);
}

vec2 rotate(vec2 v, float a) {
    float s = sin(a); float c = cos(a);
    return vec2(
        c * v.x - s * v.y,
        s * v.x + c * v.y
		);
}

float rect(vec2 p) {
#if 1
    p = abs(p) - vec2(0.7,mix(0.01,0.7,sin(iTime)*0.5+0.5));
    return step(max(p.x,p.y), 0.0);
#else
    // shape that when rotated produces an approximate gaussian
    // thanks to sean barrett for this
    float d = length(p);
    float width = exp(-d*d)*d*2.0;
    return abs(p.y) < width/2.0 ? 1.0 : 0.0;    
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    uv *= 2.0;

    const int N = 127;
    float d = 0.0;
    for (int i = 0; i < N; ++i) {
        vec2 p = rotate(uv, radians(float(i)*360.0/float(N)));
        d += rect(p);
    }
    d /= float(N);
    // Output to screen
    fragColor = vec4(mix(vec3(0.0,0.0,1.0),vec3(1.0,0.0,0.0),d),1.0);
}

