vec2 path(float z){
    float x = sin(z) + 2.0 * cos(z * 0.3) - 1.5 * sin(z * 0.12345);
    float y = cos(z) + 1.5 * sin(z * 0.3) + 2.0 * cos(z * 0.12345);
    return vec2(x,y);
}

float map(vec3 p){
	//p = fract(p/2.0) * 6.0 - 3.0;
    vec2 o = path(p.z) / 4.0;
	float tBase = max(-length(p.xy - o) + 1.0 
                      //* (1.0 + sin(p.z) * 0.5)
                      ,length(p.xy - o) - 1.2 
                      //* (1.0 + sin(p.z) * 0.5)
                     );
    float tdonut = max(-length(p.xy - o) + 0.5
                      ,length(p.xy - o) - 0.6 
                     );
    tdonut = max(tdonut,abs(fract(p.z) - 0.5));
    float tTube1 = length(p.xy - o + vec2(0.3,0.3)) - 0.025;
    float tTube2 = length(p.xy - o + vec2(0.5,0.2)) - 0.025;
    float tTube3 = length(p.xy - o + vec2(0.2,0.5)) - 0.025;    
    float tTube4 = length(p.xy - o + vec2(-0.3,-0.3)) - 0.025;
    float tTube5 = length(p.xy - o + vec2(-0.5,-0.2)) - 0.025;
    float tTube6 = length(p.xy - o + vec2(-0.2,-0.5)) - 0.025;
    float tTube = min(min(tTube1,tTube2),tTube3);
    tTube = min(min(tTube,tTube4),min(tTube5,tTube6));

    float bound = 2.0;
    float tSplit = (fract(p.z)-0.5);
    tSplit = min(tSplit, abs((p.x - o.x))-0.15);
    tSplit = min(tSplit, abs((p.y - o.y))-0.15);

    return min(min(max(tBase,-tSplit),tdonut),tTube);
}
float trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 32; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r1 = normalize(vec3(uv,0.5));
	vec3 r2 = normalize(vec3(uv - vec2(0.05 * sin(iTime * 8.0),0.0),0.5));
	vec3 r3 = normalize(vec3(uv + vec2(0.05 * sin(iTime * 8.0),0.0),0.5));
    float z = iTime * 4.0 ;

    r1.xy *= mat2( sin(iTime),cos(iTime),
    				-cos(iTime),sin(iTime));
    r2.xy *= mat2( sin(iTime),cos(iTime),
    				-cos(iTime),sin(iTime));
    r3.xy *= mat2( sin(iTime),cos(iTime),
    				-cos(iTime),sin(iTime));
	vec2 a = path(z);
    vec3 o = vec3(a / 4.0,z);
    float data1 = trace(o,r1);
    float data2 = trace(o,r2);
    float data3 = trace(o,r3);

    float t1 = data1;
    float t2 = data2;
    float t3 = data3;
    float fog1 = 1.0 / (1.0 + t1 * t1 * 0.3);
    float fog2 = 1.0 / (1.0 + t2 * t2 * 0.3);
    float fog3 = 1.0 / (1.0 + t3 * t3 * 0.3);
    vec3 fc = vec3(fog1,fog2,fog3);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

vec2 path(float z){
