mat3 rotationX( float x) 
{
	return mat3(1.0,0.0,0.0,0.0,cos(x),sin(x),0.0,-sin(x),cos(x));
}

mat3 rotationY( float y) 
{
	return mat3(cos(y),0.0,-sin(y),0.0,1.0,0.0,sin(y),0.0,cos(y));
}

bool InsideTetrahedronI(vec3 v1, vec3 v2, vec3 v3, vec3 v4, vec3 p, out vec3 color)
{
    p=rotationY(iTime)*rotationX(iTime)*p;
	mat4 D0 = mat4(v1[0],v1[1],v1[2],1.0f,v2[0],v2[1],v2[2],1.0f,v3[0],v3[1],v3[2],1.0f,v4[0],v4[1],v4[2],1.0f);
	mat4 D1 = mat4(p[0],p[1],p[2],1.0f,v2[0],v2[1],v2[2],1.0f,v3[0],v3[1],v3[2],1.0f,v4[0],v4[1],v4[2],1.0f);
	mat4 D2 = mat4(v1[0],v1[1],v1[2],1.0f,p[0],p[1],p[2],1.0f,v3[0],v3[1],v3[2],1.0f,v4[0],v4[1],v4[2],1.0f);
	mat4 D3 = mat4(v1[0],v1[1],v1[2],1.0f,v2[0],v2[1],v2[2],1.0f,p[0],p[1],p[2],1.0f,v4[0],v4[1],v4[2],1.0f);
	mat4 D4 = mat4(v1[0],v1[1],v1[2],1.0f,v2[0],v2[1],v2[2],1.0f,v3[0],v3[1],v3[2],1.0f,p[0],p[1],p[2],1.0f);
	float a = determinant(D0);
	float b = determinant(D1);
	float c = determinant(D2);
	float d = determinant(D3);
	float e = determinant(D4);
	if ( (sign(a)==sign(b)) &&  (sign(a)==sign(c)) && (sign(a)==sign(d)) && (sign(a)==sign(e)) )
    {
        color = 0.33-p;
        return true;
    }  
    else
    {
        color = vec3(0.0);
        return false;
    }    
}

bool InsideTetrahedronII(vec3 a, vec3 b, vec3 c, vec3 d, vec3 p, out vec3 color)
{
    p=rotationY(iTime)*rotationX(iTime)*p;
    vec3 vap = p - a;
    vec3 vbp = p - b;
    vec3 vab = b - a;
    vec3 vac = c - a;
    vec3 vad = d - a;
    vec3 vbc = c - b;
    vec3 vbd = d - b;
    float va6 = dot(vbp, cross(vbd, vbc));
    float vb6 = dot(vap, cross(vac, vad));
    float vc6 = dot(vap, cross(vad, vab));
    float vd6 = dot(vap, cross(vab, vac));
    float v6 = 1.0 / dot(vab, cross(vac, vad));
    vec4 k =  vec4(va6*v6, vb6*v6, vc6*v6, vd6*v6);
    if ((k.x >= 0.0) && (k.x <= 1.0) && (k.y >= 0.0) && (k.y <= 1.0) && (k.z >= 0.0) && (k.z <= 1.0) && (k.w >= 0.0) && (k.w <= 1.0))
    {
        color = k.rgb;
        return true;
    }
    else
    {
        color = vec3(0.0);
        return false;
    }         
}

vec4 raymarch (vec3 ro, vec3 rd)
{
    vec3 color = vec3(0.0);
	for (int i = 0; i < 512; i++)
	{
 		bool hit = InsideTetrahedronII(vec3(0.943, 0, -0.333 ),vec3( -0.471, 0.816, -0.333), vec3( -0.471, -0.816, -0.333), vec3(0, 0, 1 ),ro,color);
 		if (hit) return vec4(color,1.0); 
    	ro += rd * 0.01;
     }
	return vec4(0,0,0,1);
}

void mainImage( out vec4 o, in vec2 f )
{
	vec2 uv = (2. * f.xy - iResolution.xy) / iResolution.y;
	o = raymarch(vec3 (0,0.0,-3.),normalize(vec3(uv,2.)));
}

