// params
// MODE : 0,1,2
#define MODE 1
#define SLIDE_SPAN 8.0
#define ITR 8
#define SCALE 1.2
#define COLOR_SMOOTH_MIN 0.1
#define COLOR_SMOOTH_MAX 0.4

const float PI = acos(-1.);

vec3 hsv(in float h, in float s, in float v) { return ((clamp(abs(fract(h+vec3(0., 2., 1.)/3.0)*6.-3.)-1., 0., 1.)-1.)*s+1.)*v; }

float easeIn(in float t) { return pow(t, 8.); }
float easeOut(in float t) { return 1.0-easeIn(1.0-t); }
float easeInOut(in float t) { return t<0.5 ? 0.5*easeIn(t*2.0) : 0.5+0.5*easeOut(t*2.0-1.0); }

mat2 rotate(in float r) { float c=cos(r), s=sin(r); return mat2(c, -s, s, c); }
vec2 rotate(in vec2 p, in float r) { return rotate(r) * p; }
vec3 rotate(in vec3 p, in vec3 r) {
  vec3 q = p;
  q.xy = rotate(q.xy, r.z);
  q.yz = rotate(q.yz, r.x);
  q.zx = rotate(q.zx, r.y);
  return q;
}

float hash(in float x) { return fract(sin(x) * 43237.5324); }
float hash(in vec2 x) { return fract(sin(dot(x, vec2(12.9898, 78.233)))*43237.5324); }
vec3 hash3(in float x) { return vec3(hash(x), hash(x+100.), hash(10000.)); }
float noise(in vec2 p) {
  vec2 f = fract(p);
  vec2 i = floor(p);
  vec2 u = f*f*(3.-2.*f);
  return mix(
      mix(hash(i+vec2(0., 0.)), hash(i+vec2(1., 0.)), u.x),
      mix(hash(i+vec2(0., 1.)), hash(i+vec2(1., 1.)), u.x),
      u.y
    );
}
float noise(in vec3 p) {
  vec3 f = fract(p);
  vec3 i = floor(p);
  vec3 u = f*f*(3.-2.*f);
  float n = i.x + i.y*53.0 + i.z*117.0;
  return mix(
      mix(mix(hash(n+0.), hash(n+1.), u.x), mix(hash(n+53.0), hash(n+54.0), u.x), u.y),
      mix(mix(hash(n+117.), hash(n+118.), u.x), mix(hash(n+170.0), hash(n+171.0), u.x), u.y),
      u.z
    );
}
float fbm(in vec3 p) {
  float res = 0.;
  res += 0.5000*noise(p); p = rotate(p*2.02, vec3(PI*0.125));
  res += 0.2500*noise(p); p = rotate(p*2.03, vec3(PI*0.125));
  res += 0.1250*noise(p); p = rotate(p*2.01, vec3(PI*0.125));
  res += 0.0625*noise(p);
  return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = (fragCoord.xy*2.0-iResolution.xy) / min(iResolution.x, iResolution.y);

    float t = iTime*(1./SLIDE_SPAN);
    uv.x += floor(t) + easeInOut(fract(t));
    vec2 fuv = fract(uv)*2.0-1.0;
    vec2 iuv = floor(uv);

    // render
    vec3 col = vec3(1.);
    vec3 v = vec3(fuv + iuv, 0.);
    for(int i=0;i<ITR;i++) {
        float fi = float(i);
        float f = fbm(v);
        vec2 vxy = MODE==0 ? v.xy*SCALE : (MODE==1 ? v.xy*SCALE+f : v.xy*SCALE*f);
    	v = vec3(vxy, fi+f+iTime*0.1);
        vec3 c = hsv(iuv.x*0.3+f+0.02*fi, 1.0-f*hash(iuv), 1.);
        col = mix(c, col, smoothstep(COLOR_SMOOTH_MIN, COLOR_SMOOTH_MAX, f));
    }

    col = pow(col, vec3(0.4545));

    // frame
    uv = abs(fuv*vec2(iResolution.x/iResolution.y, 1.));
    float r=0.9, w=0.01;
    vec3 frameCol = vec3(0.98);
    col = mix(frameCol, col, smoothstep(r, r-w, length(uv)));
    col = mix(frameCol, col, smoothstep(r, r-w, max(uv.x, uv.y)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float map(vec3 p){

        p.xz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    p.xy *= mat2(cos(0.3),-sin(0.3)
             ,sin(0.3),cos(0.3));
    p.yz *= mat2(cos(0.3),-sin(0.3)
             ,sin(0.3),cos(0.3));
            float edge = p.x;
    float box1 = length(max(abs(p)-vec3(0.5,0.5,0.5),0.0))-0.1;
            p.xz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
        float box2 = length(max(abs(p - vec3(0.5,0.5,0.5))-vec3(0.5,0.5,0.5),0.0))-0.1;
            p.xz *= mat2(cos(-3.0 * iTime),-sin(-3.0 * iTime)
             ,sin(-3.0 * iTime),cos(-3.0 * iTime));
        float box3 = length(max(abs(p - vec3(-0.5,-0.5,-0.5))-vec3(0.5,0.5,0.5),0.0))-0.1;
	return max(length(p) - 1.0,-min(min(box2,box3),length(p) - 0.5));
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 R = iResolution,
    r = normalize(vec3((2.*U - R.xy )/  R.y,1.2)),
    o = vec3(0,0,-1.5);
    vec4 data = trace(o,r);
    vec3 n = vec3(data.xyz);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.2);
    vec3 fc = t > 10000.0 ? vec3(0.8) : mix((vec3(data.x,data.y,data.z) + 1.0)/1.5
                                            ,vec3(0.0), - pow(dot(n,r),1.0));
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

/*
Shader coded live on youtube:
https://www.youtube.com/watch?v=zakmqumh8Rc
The shader was made using Bonzomatic.
You can find the original shader here: http://lezanu.fr/LiveCode/ThroughTheBars.glsl
(yes I still put [twitch] as a generic tag for live streaming ...)
*/

#define time iTime
#define rep(p,s) (fract(p/s-0.5)-0.5)*s

float box(vec3 p, vec3 s) {
  p=abs(p)-s;
  return max(p.x, max(p.y,p.z));
}

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);  
}

float donut(vec3 p, float r1, float r2) {

  return length(vec2(length(p.xy)-r1,p.z))-r2;
  //return max(abs(length(p.xy)-r1)-r2,abs(p.z)-r2*0.5);

}

float grid(vec3 p) {


  vec3 p2 = p;
  p2.y = (fract(p.y)-0.5);
  float d = box(p2, vec3(10.0,0.2,0.05));

  p.x = (fract(p.x)-0.5);

  d = min(d, box(p, vec3(0.2,5,0.05)));

  return d;
}

float chain(vec3 p) {

  vec3 p2 = p-vec3(0,1.5,0);
  p.y = rep(p.y,0.6);
  p2.y = rep(p2.y,0.6);
  float d = donut(p, 0.2,0.05);
  d = min(d, donut(p2.zyx, 0.2,0.05));

  return d;
}

float map(vec3 p) {

  float d = 10000.0;//length(p-vec3(0,-1.5,-1.5))-0.8;

  // floor
  d = min(d, -box(p-vec3(0,-2.5,0), vec3(10,4,10)));

  d = min(d, grid(p));

  // kifs for chains
  for(int i=0; i<4; ++i) {    
    float t=0.7 + float(i)*13.742;
    p.xy *= rot(t);    
    p.yz *= rot(0.2);
    p.xy=abs(p.xy);
    p.x-=1.7;
  }
  p.x = rep(p.x, 1.4);

  //p.z -= 1;

  d = min(d, chain(p));

  p.z += 1.0;


  return d;
}

float rnd(vec2 uv) {
  return fract(dot(sin(uv*754.655+uv.yx*942.742),vec2(3847.554)));
}

float rnd(float t) {
  return fract(sin(t*472.355)*655.644);
}

float curve(float t) {
  return mix(rnd(floor(t)), rnd(floor(t)+1.0), smoothstep(0.0,1.0,fract(t)));  
}

// unused in the final shader
vec3 volumetric(vec3 p) {

  vec2 uv = p.xz * 0.5;

  vec3 col = vec3(0);
  vec2 grid = smoothstep(0.31,0.3,abs(fract(uv)-0.5));
  vec2 cell = floor(uv);
  col += min(grid.x, grid.y);
  col *= vec3(rnd(cell),rnd(cell+72.23),rnd(cell+14.37));

  col *= curve(time*4.0 + rnd(cell)*98.62);

  col *= pow(clamp((p.y+1.0)*0.5,0.0,1.0),2.0);

  return col;
}

void cam(inout vec3 p) {

  p.yz *= rot(sin(time*0.4)*0.4+0.2);
  p.xz *= rot(sin(time*0.13)*1.3);

}

float shadow(vec3 p, vec3 l, float maxdist, int stepcount, float limit) {
  float shad=1.0;
  float dd=0.0;
  for(int i=0; i<stepcount; ++i) {
    float d=min(map(p), maxdist);
    if(d<limit) {
      shad=0.0;
      break;
    }
    if(dd>=maxdist) {
      break;
    }
    p += l*d;
    dd+= d;
  }
  return shad;
}

float getao(vec3 p, vec3 n, float d) {
  return clamp(map(p+n*d)/d,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  float rand=rnd(uv);

  vec3 s=vec3(0,0,-5);
  vec3 r=normalize(vec3(-uv, 1));

  cam(s);
  cam(r);

  vec3 lightpos = vec3(2,-1.0,0);
  float t1 = time + curve(time*0.2)*12.7;
  lightpos.yz *= rot(sin(t1*0.7)*0.7);
  lightpos.xz *= rot(sin(t1*1.3)*0.7-1.5);  

  // main raymarch  
  vec3 p=s;
  float dd=0.0;
  const float maxdist = 100.0;
  for(int i=0; i<100; ++i) {
    float d=map(p);
    if(d<0.001) break;
    if(dd>maxdist) { dd=maxdist; break;}
    p+=r*d;
    dd+=d;
  }

  // volumetric raymarching
  const int volsteps=50;
  float voldist = 15.0;
  float stepsize = voldist/float(volsteps);
  vec3 lr=r*stepsize;
  vec3 lp=s + lr*rand;
  float stepdist=rand*stepsize;
  vec3 atcol=vec3(0);
  for(int i=0; i<volsteps; ++i) {
    if(stepdist>dd) {
      break;
    }
    vec3 lv = lightpos-lp;
    float ldistvol = length(lv);
    lv = normalize(lv);
    float shadvol = shadow(lp, lv, ldistvol, 20, 0.01);
    atcol += 0.3/(0.05+(ldistvol*ldistvol)) * shadvol;
    //atcol += volumetric(lp)*0.1;
    lp+=lr;
    stepdist+=stepsize;
  }

  float fog = 1.0-clamp(dd/maxdist,0.0,1.0);

  vec2 off=vec2(0.01,0);
  vec3 n=normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));

  vec3 l=normalize(lightpos - p);
  vec3 h=normalize(l-r);
  float spec = max(dot(n,h),0.0);
  float ldist = length(lightpos - p);
  float fade = 1.0/(ldist*ldist);

  float shad = shadow(p + n * 0.2, l, ldist, 30, 0.01);

  vec3 col=vec3(0);
  col += max(dot(n,l),0.0) * (1.7 + 5.0*pow(spec,5.0) + 10.0 * pow(spec,20.0)) * fog * fade * shad;
  //col += pow(1-abs(dot(n,r)), 5) * fog * fade * shad;

  float ao = getao(p,n, 0.1) * getao(p,n, 0.4) * getao(p,n, 1.0) * (0.5 + 0.5 * getao(p,n, 2.0));
  col += ao * 0.05;

  //col += volumetric(p);

  col += atcol;
  //col += pow(at*0.12,4);

  //col *= 1.2-length(uv);
  col = pow(col, vec3(0.4545));
  col *= 1.2-length(uv);

  fragColor = vec4(col, 1);
}

