float dopplerEffect (in vec2 uv)
{
    float pendulumFactor = 8.0 * sin (iTime);
    float fringeFactor = 0.5 * sin (iTime);

    float offset = 0.0;
    for (int i = 0; i < 3; i++)
    {
        // % needs GLSL 3.0
        float newOffset = fract (float(i)/2.0) == 0.0 ? sin(length(uv + offset * pendulumFactor) + iTime) : cos(length(uv + offset * fringeFactor) + iTime);
        offset = (newOffset + 1.0) * 0.5;
    }
    return offset;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 80.0 - 40.0;
    float dopplerEffect = dopplerEffect (uv);
    fragColor = vec4(vec3 (dopplerEffect), dopplerEffect * 0.1);
}

// -------------------------------------------------------------------------
// Struct definitions and scene setup
// -------------------------------------------------------------------------

// Camera parameters
vec3 lower_left_corner = vec3(-1.0, -1.0, -1.0);
vec3 horizontal = vec3(2, 0, 0);
vec3 vertical = vec3(0, 2, 0);
vec3 origin = vec3(0, 0, 0);

// Ray struct
struct Ray
{
    vec3 origin;
    vec3 direction;
    float mint;
    float maxt;
};

// Scene
struct Sphere
{
    vec3 center;
    float radius;
    vec3 color;
};

Sphere glowSphere = Sphere(vec3(0,0,0), 0.1,vec3(1,1,1));

const int NUM_SPHERES = 6;
const Sphere spheres[NUM_SPHERES] = Sphere[NUM_SPHERES](  
    Sphere(vec3(0.0, -10000.5, -1.0), 10000.,vec3(1,1,1)),
    Sphere(vec3(0.0, 10000.5, -1.0), 10000.,vec3(1,1,1)),
    Sphere(vec3(-10000.5, 0, -1.0), 10000.,vec3(1,0.2,0.2)),
    Sphere(vec3(10000.5, 0, -1.0), 10000.,vec3(0.2,1,0.2)),
    Sphere(vec3(0.0, 0.0, -10001.0), 10000.,vec3(1,1,1)),
    Sphere(vec3(0, -.35, -.7), 0.15,vec3(.5,.85,1))
);    

struct Light
{
    vec3 position;
    vec3 color;
    float intensity;
};


// Hit record information
struct HitRecord
{
    float t;
    vec3 P;
    vec3 N;
    vec3 col;
};


// -------------------------------------------------------------------------
// Camera code
// -------------------------------------------------------------------------

Ray computeCameraRay(vec2 uv)
{
    float aspectRatio = iResolution.x / iResolution.y;
    Ray r;
    r.origin = origin;
    r.direction = lower_left_corner * vec3(aspectRatio, 1.0, 1.0) +
          uv.x * horizontal * aspectRatio +
          uv.y * vertical;
    r.mint = 0.0;
    r.maxt = 1e20;
    return r;
}

Ray computeLightRay(HitRecord hit)
{
    Ray r;
    r.origin = hit.P;

    vec3 distToLight = (glowSphere.center - hit.P) - glowSphere.radius * glowSphere.radius;

    r.direction = normalize(distToLight);
    r.mint = 0.0;
    r.maxt = length(distToLight);
    return r;
}

// -------------------------------------------------------------------------
// Animation
// -------------------------------------------------------------------------

void animateSphere(Sphere s)
{
    glowSphere.center = vec3(sin(iTime/2.)/3., sin(iTime/3.)*.1+.1,sin(iTime)*.15 -.5);
}

// -------------------------------------------------------------------------
// Light
// -------------------------------------------------------------------------

bool shadowDetect(Ray r, Sphere s)
{        
    vec3 oc = r.origin - s.center;
    float a = dot(r.direction, r.direction);
    float b = 2.0 * dot(oc, r.direction);
    float c = dot(oc, oc) - s.radius * s.radius;
    float det = b*b - 4.0*a*c;
    if (det < 0.0)
    	return false;


    float t = (-b - sqrt(det)) / (2.0 * a);
    if (r.mint <= t && t < r.maxt) 
        return true;

    return false;
}


float computeLightInt(HitRecord hit)
{
    Ray r = computeLightRay(hit);
	vec3 distToLight = (glowSphere.center - hit.P) - glowSphere.radius * glowSphere.radius;
    float dotL = dot(hit.N, r.direction);

    float intensity = (1.0/ length(distToLight)) * .3;


    if(shadowDetect(r,spheres[5]))
       {
           intensity *= 0.00;
       }

    return intensity * dotL;
}


// -------------------------------------------------------------------------
// Intersection code
// -------------------------------------------------------------------------



bool hitSphere(Ray r, Sphere s, out HitRecord hit)
{        
    vec3 oc = r.origin - s.center;
    float a = dot(r.direction, r.direction);
    float b = 2.0 * dot(oc, r.direction);
    float c = dot(oc, oc) - s.radius * s.radius;
    float det = b*b - 4.0*a*c;
    if (det < 0.0) {
        return false;
    }

    // first possible intersection
    float t = (-b - sqrt(det)) / (2.0 * a);
    if (r.mint <= t && t < r.maxt) { 
        hit.t = t;
        hit.P = r.origin + t * r.direction;
        hit.N = normalize(hit.P - s.center);

        hit.col = s.color * computeLightInt(hit);

        return true;
    }

    // second possible intersection
    t = (-b + sqrt(det)) / (2.0 * a);
    if (r.mint <= t && t < r.maxt) { 
        hit.t = t;
        hit.P = r.origin + t * r.direction;
        hit.N = normalize(hit.P - s.center);
        return true;
    }

    return false;
}

// intersect against all spheres and return the first hit
bool hitSpheres(Ray r, Sphere spheres[NUM_SPHERES], out HitRecord hit)
{
    bool hitSomething = false;
    for (int i = 0; i < spheres.length(); ++i) {
        if (hitSphere(r, spheres[i], hit)) {

            hitSomething = true;
            // if we hit a sphere we update maxt on the ray to only find
            // intersections that are even closer
            r.maxt = hit.t;
        }
    }


    return hitSomething;
}


// -------------------------------------------------------------------------
// Core integrator
// -------------------------------------------------------------------------


vec3 traceRay(Ray r)
{

    HitRecord hit;

    if (hitSpheres(r, spheres, hit))
    {
        return hit.col;

    }

    return vec3(0.0,0.0,0.0);
}

vec3 traceRayGlow(Ray r)
{

    HitRecord hit;

    if (hitSphere(r, glowSphere, hit))
    {
        return vec3(1,1,1);

    }

    return vec3(0.0,0.0,0.0);
}


