// Main image computation
// -------------------------------------------------------------------------


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    animateSphere(glowSphere);

    // Compute ray for pixel
    Ray r = computeCameraRay(uv);

    // Pixel Color
    vec3 diffuse = traceRay(r);
    vec3 emission = traceRayGlow(r);
    vec3 ambient = vec3(0.01,0.005,0.07);
    vec3 baseCol = diffuse + emission + ambient;

    vec3 finalCol = pow(baseCol, vec3(0.8,0.8,0.8) );

    // Output to screen
    fragColor = vec4(finalCol,1.0);
}

