    col *= drawDim(uv*vec2(1.,0.5)-vec2(0.3,-0.1), 0.25, 1.5);



    vec4 planetA = drawPlanet(uv + vec2(-0.21,-0.0), 0.17, 0.16, 0.8*normalize(vec2(1.,-0.2)));
