precision highp float;

const float M_PI = 3.1415926535897932384626433832795;

// normalized angle (a,b) [0,1]
float normAngle(vec2 a, vec2 b)
{
    return .5 + .5 * sign(cross(vec3(a,0.), vec3(b,0.)).z) * -acos(dot(normalize(a), normalize(b))) / M_PI;
}

// shitty signed dist tri
float sdtri(vec2 uv, vec2 o, float s, float a)
{
    uv = mat2(1./s,0.,0.,1./s) * (uv - o);

    vec2 oa = mat2(cos(a), -sin(a), sin(a), cos(a)) * vec2(0., -1.);
    float ua = normAngle(oa, uv);
    float ba = floor(ua * 3.);
    float r = (0.25- ba) * 2.*M_PI/3. - a;
    vec2 nn = mat2(cos(r), -sin(r), sin(r), cos(r)) * uv;
    return nn.x - .5*s*.5;
}

