void paint() {
    float t = iTime;

    p[0] = cos(t*0.79)*0.8;
    p[1] = sin(t*0.9)*0.8;
    p[2] = cos(t*0.49)*0.8;

    float d0 = (p[1] - p[0]) / 2.0;
    float d1 = (p[2] - p[1]) / 2.0;

    set_source_rgb(vec3(1.0));
    clear();

    set_line_width_px(1.3);

    set_source_rgba(0.0,0.0,0.0,1.0);
    graph1D(interpolate);
	stroke();

    set_source_rgba(1.0,1.0,1.0,0.8);
    rectangle(-3.0,-1.0,2.5,2.0);
    rectangle(0.5,-1.0,2.5,2.0);
	fill();


    set_source_rgba(0.0,0.0,0.0,0.5);
    move_to(-0.5,-1.0);
	line_to(-0.5,1.0);
    move_to(0.5,-1.0);
	line_to(0.5,1.0);
    stroke();

    set_source_rgba(1.0,0.0,0.0,0.5);
    move_to(-1.0, p[0]);
    line_to(0.0, p[1]);
	line_to(1.0, p[2]);
    stroke();

    set_source_rgba(0.0,0.0,0.0,1.0);
    circle(-1.0, p[0], 0.02);
    circle(0.0, p[1], 0.02);
    circle(1.0, p[2], 0.02);
	fill();
}

