// https://www.shadertoy.com/view/XsXSz4
// signed distance to a 2D triangle
float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

vec2 getMidpoint(vec2 a, vec2 b)
{
    vec2 dir = b - a;
    return a + 0.5 * dir;
}

vec2 getInnerPoint(vec2 p1, vec2 p2, float ratio)
{
    vec2 dir = p2 - p1;
    return p1 + (ratio * dir);
}

vec2 getRadialPoint(vec2 centre, vec2 outer, float radius)
{
    vec2 n = normalize(outer - centre);    
    return centre + radius * n;
}

vec4 drawColouredQuad(vec2 uv, vec2 a, vec2 b, vec2 c, vec2 d)
{
    vec4 outColor = vec4(0,0,0,0);
    // QUAD 2
    	// outer [i+1] -> outer[i] 
    	// inner [i+1] -> inner[i]
    	// inner[i+1], inner[i], outer[i+1]
    if (sdTriangle(a, b, c, uv) <= 0.0) {       
    	outColor += vec4(1,0,1,1);       
    }            

   	// inner[i], outer[i], outer[i+1]    
    if (sdTriangle(a, c, d, uv) <= 0.0) {       
    	outColor += vec4(1,1,0,1);       
    }        
    return outColor;
}

vec4 drawArm(vec2 centre, vec2 uv, vec2 left, vec2 right, float radius, int division)
{
    vec2 left_i = getRadialPoint(centre, left, radius);

    vec2 right_i = getRadialPoint(centre, right, radius);

    vec2 top_0 = left;
    vec2 inside_0 = left_i;

    vec4 outColor = vec4(0,0,0,0);

    float offset = 1. / float(division);
    for (int i = 1; i < division; i += 1)
    {
        vec2 top_1 = getInnerPoint(left, right, float(i) * offset);  
        vec2 inside_1 = getRadialPoint(centre, top_1, radius); 
		outColor += drawColouredQuad(uv, top_0, inside_0, inside_1, top_1);
        top_0 = top_1;
        inside_0 = inside_1;
    } 


    outColor += drawColouredQuad(uv, top_0, inside_0, right_i, right);
    return outColor;
}

vec4 drawCorner3(vec2 centre, vec2 uv, vec2 right, vec2 mid, vec2 left, float radius, int noOfQuads)
{ 
    // QUAD 1
    return drawArm(centre, uv, left, mid, radius, noOfQuads / 2)
   	 + drawArm(centre, uv, mid, right, radius, noOfQuads / 2);        
   // return outColor;
}

vec4 drawDot(vec2 pt, vec2 uv)
{
    vec2 diff = uv - pt;
    if (dot(diff, diff) <= 0.005)
    {
        return vec4(0,1,0,1);
    }
    return vec4(0,0,0,0);
}

vec4 drawArcOnCorner(vec2 centre, vec2 uv, in vec2[MAX_N] points,
                     int left, int mid, int right,
                     float innerRadius, int division)
{
    vec2 mid_l = getMidpoint(points[mid], points[left]);
    vec2 mid_r = getMidpoint(points[mid], points[right]);      

    return drawCorner3(centre, uv, mid_r, points[mid], mid_l, innerRadius, division);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    // SCREEN CORRECTION TO SQUARE
    float fov = (iResolution.y / iResolution.x);
	vec2 uv = (fragCoord.xy / iResolution.xy);
    uv -= 0.5;
    uv.y *= fov;

    // SCALE 
    uv *= UNIT_SCALE;  	     

    float radius = 2.0;        

    float halfHeight = radius * sqrt(3.)/4.;
    float halfWidth = radius * .5;    

    vec2 a =  vec2(0.,  halfHeight); 
    vec2 b = vec2(-halfWidth, -halfHeight);    
    vec2 c = vec2(halfWidth, -halfHeight);  

    // INTERSECTION OF MIDPOINT
    // CENTRE CAN BE ANYTHING
    vec2 centre = vec2(0.);  

    vec2 mid_a_b = getMidpoint(a, b);
    vec2 mid_b_c = getMidpoint(b, c);
    vec2 mid_c_a = getMidpoint(c, a);     

    // TOP: subdividing sections by value within int array           
    float gapTimeStep = mod(iTime, SUBDIVISION_LOOP_IN_SECS) / SUBDIVISION_LOOP_IN_SECS;         

    int division = sections[int(gapTimeStep * float(NO_OF_DIVISIONS))];  
   // int division = 6;
    float innerRadius = MID_POINT + RANGE_OF_TRIG_FUNC * sin( FREQUENCY * TWO_PI * iTime);    


    //fragColor += drawCorner3(centre, uv, mid_c_a, a, mid_a_b, innerRadius, division / 3);
    //fragColor += drawCorner3(centre, uv, mid_a_b, b, mid_b_c, innerRadius, division / 3);
    //fragColor += drawCorner3(centre, uv, mid_b_c, c, mid_c_a, innerRadius, division / 3);    

    // 2ND from top: switching color values within array
    float timeStep = mod(iTime, LOOP_IN_SECS) / LOOP_IN_SECS;                     

    int count = MIN_N + int(timeStep * float(MAX_N - MIN_N + 1));
    //int count = 4;

    float angleOffset = TWO_PI / float(count);

    float angle = 0.;  

    if (count % 2 == 0)
    {
        // 45 degrees
        angle += angleOffset / 2.;
    }

    fragColor += drawDot(centre, uv);

    // DRAW CIRCLES ON POLYGON CORNERS
    vec2 points[MAX_N];
    for (int i = 0; i < count; i++)
    {       
        points[i] = vec2(sin(angle), cos(angle));
        fragColor += drawDot(points[i], uv);
        angle += angleOffset;
    }    

    // DRAW FACES FOR EACH CORNER
    int leftIndex = count - 1;
    int midIndex = 0;
    int rightIndex = 1;
    for (int i = 0; i < count - 1; i++)
    {       
       // vec2 left = points[leftIndex];
       // vec2 mid = points[midIndex];
       // vec2 right = points[rightIndex];

    	//vec2 mid_l = getMidpoint(mid, left);
    	//vec2 mid_r = getMidpoint(mid, right);      

        fragColor += drawArcOnCorner(centre, uv, points, leftIndex, midIndex, rightIndex, innerRadius, division);
        leftIndex = i;
        midIndex++;
        rightIndex++;            
    }        

    rightIndex = 0;
    fragColor += drawArcOnCorner(centre, uv, points, leftIndex, midIndex, rightIndex, innerRadius, division);    
}

vec3 flag(vec2 uv) {
    if(uv.y < 0.25) {
     	return vec3(1.0, 0.96, 0.04);
    } else if(uv.y < 0.5) {
        return vec3(1.0);
    } else if(uv.y < 0.75) {
     	return vec3(0.61, 0.35, 0.83);   
    } else {
        return vec3(0.0);
    }
}

vec2 warp(vec2 uv) {
    vec2 uv1 = uv + vec2(
    	uv.y*0.1,
        0.0
    );
    vec2 d = vec2(
        0.0,
        0.23*sin(iTime + round(0.017*(uv1.x * 2.0 - 1.0)*4000.0))
    );
	return uv + d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.y = 1.0 - uv.y;
    uv = warp(uv);

    vec3 c0 = flag(uv);

    // Output to screen
    fragColor = vec4(c0, 1.0);
}

// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// compute barycentric coordinates from three points and a coordinate
// this is equivalent to the distance of a point to each of the three planes,
// normalized by the determinant
vec3 barycentric(vec2 p[3], vec2 t) {
    vec2 u = p[1] - p[2];
    vec2 v = p[2] - p[0];
    vec2 nu = vec2(-u.y, u.x);
    vec2 nv = vec2(-v.y, v.x);
    float det = 1.0 / dot(u, nv);
    t -= p[2];
    float w0 = dot(t, nu)*det;
    float w1 = dot(t, nv)*det;
    // computing the third coordinate this way adds numerical stability
    float w2 = 1.0 - w0 - w1;
    return vec3(w0,w1,w2);
}

vec2 from_barycentric(vec2 p[3], vec3 w) {
    return p[0]*w.x + p[1]*w.y + p[2]*w.z;
}

vec2 normal_from_barycentric(vec2 p[3], vec3 w) {
    vec2 d = (p[1] - p[2])*w.x + (p[2] - p[0])*w.y;
    d = vec2(d.y, -d.x);
    return d;
}

float bary_quadratic(vec3 w, vec3 c, vec3 m) {
    return w.x*w.x*c.x
         + w.y*w.y*c.y
         + w.z*w.z*c.z
         + 2.0*m.x*w.y*w.z
         + 2.0*m.y*w.x*w.z
         + 2.0*m.z*w.x*w.y;
}

vec3 weight(vec3 p) {
    return vec3(
		(p[0]+p[1])*0.5,
        p[1],
        (p[1]+p[2])*0.5);
}

float interpolate2d_bicubic(mat3 p, vec2 o) {
    vec3 s = vec3(
    	(1.0 - o.x)*(1.0 - o.x),
    	2.0*(1.0 - o.x)*o.x,
    	o.x*o.x);
    vec3 t = vec3(
        (1.0 - o.y)*(1.0 - o.y),
    	2.0*(1.0 - o.y)*o.y,
    	o.y*o.y);
    vec3 q = vec3(
    	dot(weight(p[0]), s),
    	dot(weight(p[1]), s),
    	dot(weight(p[2]), s));

	return dot(weight(q), t);
}

float interpolate2d(mat3 p, vec2 o) {
    vec3 bc;
    bc[0] = (p[0][0]+p[0][1]+p[1][0]+p[1][1])*0.25;
    bc[2] = (p[1][1]+p[1][2]+p[2][1]+p[2][2])*0.25;
    vec3 bw = vec3(0.0);
    float c = 
          p[0][0] * -3.0 + p[0][1] *  2.0 + p[0][2]
        + p[1][0] *  2.0 + p[1][1] * 28.0 + p[1][2] *  2.0
        + p[2][0]        + p[2][1] *  2.0 + p[2][2] * -3.0;
    c /= 32.0;    
    bw[1] = c;
    vec2 tri[3];
    tri[0] = vec2(0,0);
    tri[2] = vec2(1,1);
    if (o.x < o.y) {
	    tri[1] = vec2(0,1);
	    bc[1] = (p[1][0]+p[1][1]+p[2][0]+p[2][1])*0.25;
        bw[0] = (p[1][1] + p[2][1])*0.5;
        bw[2] = (p[1][1] + p[1][0])*0.5;
    } else {
	    tri[1] = vec2(1,0);
	    bc[1] = (p[0][1]+p[0][2]+p[1][1]+p[1][2])*0.25;
        bw[0] = (p[1][1] + p[1][2])*0.5;
        bw[2] = (p[1][1] + p[0][1])*0.5;
	}

    vec3 b = barycentric(tri, o);
    float q = bary_quadratic(b, bc, bw);

    return q;
}

