vec2 anglevec(float a) {
    return vec2(cos(a),sin(a));
}

vec2 transform(vec3 p) {
    float o = 20.0;
    vec2 dx = anglevec(radians(o));
    vec2 dy = anglevec(radians(o+120.0));
    vec2 dz = vec2(0.0, 0.33);

    return p.x * dx + p.y * dy + p.z * dz;
}

void paint() {
    float t = iTime;

    mat3 p;
    mat2 m;
    float e[4];

    p[0] = vec3(cos(t*0.79), sin(t*0.9), cos(t*0.51));
    p[1] = vec3(sin(t*0.63), cos(t*0.85), sin(t*0.51));
    p[2] = vec3(cos(t*0.55), sin(t*0.81), cos(t*0.72));

    for (int y = 0; y < 2; ++y) {
        for (int x = 0; x < 2; ++x) {
            float p0 = p[y][x];
            float p1 = p[y][x+1];
            float p2 = p[y+1][x];
            float p3 = p[y+1][x+1];            
            m[y][x] = (p0 + p1 + p2 + p3) / 4.0;
        }
    }

    // compute one level of catmull-clark for visualization
    e[0] = p[0][0] + p[0][1] * 6.0 + p[0][2] 
         + p[1][0] + p[1][1] * 6.0 + p[1][2];
    e[0] /= 16.0;
    e[1] = p[1][0] + p[1][1] * 6.0 + p[1][2] 
         + p[2][0] + p[2][1] * 6.0 + p[2][2];
    e[1] /= 16.0;
    e[2] = p[0][0] + p[1][0] * 6.0 + p[2][0] 
         + p[0][1] + p[1][1] * 6.0 + p[2][1];
    e[2] /= 16.0;
    e[3] = p[0][1] + p[1][1] * 6.0 + p[2][1]
         + p[0][2] + p[1][2] * 6.0 + p[2][2];
    e[3] /= 16.0;

    float c = 
          p[0][0]       + p[0][1] *  6.0 + p[0][2]
        + p[1][0] * 6.0 + p[1][1] * 36.0 + p[1][2] * 6.0
        + p[2][0]       + p[2][1] *  6.0 + p[2][2];    
    c /= 64.0;

    scale(0.7);

    set_line_width_px(1.3);

    set_source_rgba(0.0,0.0,0.0,0.2);
    move_to(transform(vec3(-0.5,-0.5,0.0)));
    line_to(transform(vec3( 0.5,-0.5,0.0)));
    line_to(transform(vec3( 0.5, 0.5,0.0)));
    line_to(transform(vec3(-0.5, 0.5,0.0)));
    close_path();
    stroke();
    move_to(transform(vec3(-1.0,-1.0,0.0)));
    line_to(transform(vec3( 1.0,-1.0,0.0)));
    line_to(transform(vec3( 1.0, 1.0,0.0)));
    line_to(transform(vec3(-1.0, 1.0,0.0)));
    close_path();
    stroke();

    set_source_rgba(1.0,0.0,0.0,0.5);
    for (int y = 0; y < 2; ++y) {
        for (int x = 0; x < 3; x += 2) {
            vec2 uv1 = vec2(x,y)-1.0;
            vec2 uv2 = vec2(x,y);
            move_to(transform(vec3(uv1, p[y][x])));
            line_to(transform(vec3(uv1.x, uv2.y, p[y+1][x])));
        }
    }
    for (int y = 0; y < 3; y += 2) {
        for (int x = 0; x < 2; ++x) {
            vec2 uv1 = vec2(x,y)-1.0;
            vec2 uv2 = vec2(x,y);
            move_to(transform(vec3(uv1, p[y][x])));
            line_to(transform(vec3(uv2.x, uv1.y, p[y][x+1])));
        }
    }
	stroke();


    set_source_rgba(0.0,0.0,0.0,0.3);    
    for (int y = 0; y < 3; ++y) {
        for (int x = 0; x < 3; ++x) {
            vec2 uv = vec2(x,y)-1.0;
            move_to(transform(vec3(uv, 0.0)));
            line_to(transform(vec3(uv, p[y][x])));
        }
    }
	stroke();

    set_source_rgba(0.0,0.0,0.0,0.3);
    for (int y = 0; y < 2; ++y) {
        for (int x = 0; x < 2; ++x) {
            vec2 uv = vec2(x,y)-0.5;
            move_to(transform(vec3(uv, 0.0)));
            line_to(transform(vec3(uv, m[y][x])));
        }
    }
    stroke();

    set_source_rgba(0.0,0.0,0.0,0.3);
    { 	vec2 uv = vec2(0.0,-0.5);
        move_to(transform(vec3(uv, 0.0)));
        line_to(transform(vec3(uv, e[0]))); }
    { 	vec2 uv = vec2(0.0, 0.5);
        move_to(transform(vec3(uv, 0.0)));
        line_to(transform(vec3(uv, e[1]))); }
    { 	vec2 uv = vec2(-0.5,0.0);
        move_to(transform(vec3(uv, 0.0)));
        line_to(transform(vec3(uv, e[2]))); }
    { 	vec2 uv = vec2( 0.5,0.0);
        move_to(transform(vec3(uv, 0.0)));
        line_to(transform(vec3(uv, e[3]))); }
	stroke();


    set_source_rgba(0.0,0.0,0.0,1.0);    
    const float r = 0.02;
    for (int y = 0; y < 3; ++y) {
        for (int x = 0; x < 3; ++x) {
            vec2 uv = vec2(x,y)-1.0;
		    circle(transform(vec3(uv, p[y][x])), r);
        }
    }
	fill();

