const float pi = 3.1415;
const float tau = pi * 2.0;

// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float rand2(vec2 n) {
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

vec3 rgb(int r, int g, int b)
{
    return vec3(float(r)/256.0, float(g)/256.0, float(b)/256.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;    

    vec3 col1 = 2.0*rgb(52, 20, 5);
    vec3 col2 = 2.0*rgb(3, 3, 50);
    // vec3 col3 = 2.0*rgb(1, 1, 24);

    uv = rotate(uv, -iTime*.3);

    float radius = .5;
    float size = 0.06;

    float angle = atan(uv.y, uv.x);

    // angle += rand2(uv);
    float disp = sin(angle*sin(iTime/3.0)*16.0+3.0*pi/2.0);
    float disp2 = sin(angle*sin(iTime/10.0)*15.0+3.0*pi/2.0);
    float disp3 = sin(iTime+angle*5.0+2.0*pi/2.0);

    // col2 *= disp; // debug

    float dist = length(uv) + disp / 48.0;

    float sigma = abs(dist-radius) + smoothstep(size+.47, size+0.99, dist*1.1) * 1.2 * disp2 * disp3;

    float tor = smoothstep(size, size+0.01, sigma);
    vec3 col = mix(col2, col1, tor);

    fragColor = vec4(col,1.0);
}

/* Timothy Michael Walsh */
mat2 r2(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(s, c, -c, s);
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
        vec2(12.9898,78.233)))*43758.5453123);
}

vec3 effect(vec2 uv, float zoomSpeed, float ringCount, float size, float rotationSpeed){
    float spacing = 1.3;
    vec2 id = floor(uv/spacing-.5); //*spacing;
    float rrr = random(id.yy);
    rrr *= rrr<.5 ? 1. : -1.;
    uv.x+=rrr*iTime*1.5;

    id = floor(uv/spacing-.5);
    uv = (fract(uv/spacing-.5)-.5)*spacing;

    float r = random(id);
    float rr = random(id);

    uv*=r2(rr*rotationSpeed*iTime+(sin(iTime*r)*3.*r));
    float s = length(uv)-size; //*(sin(iTime*zoomSpeed)*.5);

    float f = smoothstep(s-.005, s, .2935);
    f -= smoothstep(s,s + 0.005, .27);
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(r+rr+iTime+uv.xyx+vec3(r*f,3.-rr+r,r+1.)-iTime*2.);

    float a = atan(uv.x,uv.y);
    //f = f * step(a, sin(iTime-a*18.0)+cos(3.*iTime+a*18.0));
    f = f * smoothstep(f,  cos(a*(ringCount+(sin(iTime*2.*r)*8.*rotationSpeed*r))*sin(r+iTime)+cos(-iTime*11.75*rotationSpeed*rr)) +1., .02);   //+cos(iTime-s*10.)

    return col*f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy)-.5;
    uv.x*=iResolution.x/iResolution.y;
    uv*=2.25;
    uv*=r2(sin(iTime/2.));
    uv *= 1.0 + dot(uv,uv)*.5;
    uv.x+=sin(iTime/5.)*13.;
    uv.y+=sin(iTime/5.5)*11.;

    vec3 col = effect(uv, 4., 11., .125, .75);
    col += effect(uv, 2.5, 8., .2, 1.);
    col += effect(uv, 1.5, 6., .25, 0.5);
    col += effect(uv, 0.5, 7., .34, 0.35);
    col += effect(uv, 0.25, 5., .081, 0.865);
    col += effect(uv, 0.25, 5., .03, 01.1865);


    //f = f * step(f,sin(a*12.));
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    vec2 uv;
    vec2 p;
    vec2 r = iResolution.xy;

    for (int i = 0; i < 3; i++) {
        uv = fragCoord/r;
        p = uv;

        uv -= 0.5;

        p.y -= 5. * sin(iTime * 0.15);
        p.x -= 5. * cos(iTime * 0.15);

        uv.y += sin(iTime) * 0.15;
        uv.x += cos(iTime) * 0.15;

        col[i] += p.x / (uv.x * 0.01);
        col[i] += p.y / (uv.y * 0.01);

        col[i] += cos(p.x / uv.x) / 0.001;
        col[i] += sin(p.y / uv.y) / 0.001;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

//	toggle whether within screen or square region
#define CLAMP_RANGE 0

//	sample scale modifier
#define SAMPLE_SCALE 1.

//	shape of the visualizer
//		0 - line
//		1 - waveform
#define VISUALIZER_SHAPE 0

void mainImage( out vec4 t_out_colour, in vec2 t_coord )
{
    // normalize
    vec2 uv = (t_coord - iResolution.xy * .5) / (iResolution.y * .5);

    // output colour, is purty
    vec3 c = vec3(sin(uv.x + iTime) * .5 + .5, cos(uv.y + iTime * .5) * .5 + .5, sin(iTime * .33));
    c = max(c, vec3(.2, .2, .2));

#if CLAMP_RANGE
    // coord within box
    float cd = texture(iChannel0, vec2((uv.x / SAMPLE_SCALE) * .5 + .5, 0.)).r;

    // clamp range
    cd *= step(abs(uv.x), 1.);
#else
    // coord within screen
    float cd = texture(iChannel0, vec2(((t_coord.x / SAMPLE_SCALE) / iResolution.x), 0.)).r;
#endif

    // scale amplitude
    cd *= .4;

#if VISUALIZER_SHAPE == 1
    // distance from coord
    float d = distance(abs(uv.y), cd * cd * 8. + 4. / iResolution.y);

    // colour mod
    c*= smoothstep(abs(uv.y), abs(uv.y) + 4. / iResolution.y, d);
#else 
    // colour mod
    c*= smoothstep(0., distance(uv.y, cd), 1. / iResolution.y);
#endif

    // output
    t_out_colour = vec4(c, 1.);
}

const float MAT_SKY = 1.0;
const float MAT_FLOOR = 2.0;
const float MAT_WHITE = 3.0;
const float MAT_RED = 4.0;
const float MAT_GREEN = 5.0;
const float MAT_BLUE = 6.0;
const float MAT_COLUMN = 7.0;

vec2 PixelCoords(in vec2 fragCoord) {

    return (2.0 * fragCoord-iResolution.xy) / iResolution.y;
}

float Noise( in vec2 f ) {

    vec2 p = floor(f);
    f = fract(f);
    f = f*f*(3.0-2.0*f);
    float res = textureLod(iChannel0, (p+f+.5)/256.0, 0.0).x;
    return res;
}


vec3 CreateRayDir(in vec3 ray_origin, in vec3 look_at, in vec2 pixel) {

    vec3 z = normalize(look_at - ray_origin);
    vec3 x = normalize(cross(z, vec3(0.0, 1.0, 0.0)));
    vec3 y = normalize(cross(x, z));

    return normalize(pixel.x*x + pixel.y*y + 1.5*z);
}

vec3 ApplyGamma(in vec3 col) {
	return pow(col, vec3(0.4545));
}

vec2 MapSphere(in vec3 sample_pos, in vec3 pos, in float radius, in float material, in vec2 res) {

    float d = length(sample_pos - pos)-radius;
    if (d < res.x)
        return vec2(d, material);

    return res;
}

vec2 MapColumn(in vec3 sample_pos, in vec3 pos, in float radius, in float height, in float material, in vec2 res) {

    float col_y = clamp(sample_pos.y, pos.y, pos.y+height);

    float spiral_speed = 60.0;

    float d = length((sample_pos - vec3(pos.x, col_y, pos.z))*vec3(1.0,10.0,1.0))-radius;

    if (d < res.x)
        return vec2(d, material);

    return res;
}

vec2 MapScene(in vec3 pos) {

    const float DEG_TO_RAD = 3.14159/180.0;
    const vec3 ORBIT_A = normalize(vec3(sin(DEG_TO_RAD*  0.0), 2.5, cos(DEG_TO_RAD*  0.0)));
    const vec3 ORBIT_B = normalize(vec3(sin(DEG_TO_RAD*120.0), 2.5, cos(DEG_TO_RAD*120.0)));
    const vec3 ORBIT_C = normalize(vec3(sin(DEG_TO_RAD*240.0), 2.5, cos(DEG_TO_RAD*240.0)));

	const float ORBIT_DIST = 0.52;
    const float ORBIT_SPEED = 0.6;

    float time_a = (iTime + 3.14159*1.0) * ORBIT_SPEED;
    float time_b = (iTime + 3.14159*2.0) * ORBIT_SPEED;
    float time_c = (iTime + 3.14159*3.0) * ORBIT_SPEED;

    float plane_d = pos.y + 0.25;
//    float tile_d = 0.2*clamp(sin(pos.x*10.0)*sin(pos.z*10.0)*3.0+0.05, 0.01, 0.2);    
//    plane_d -= max(0.0, tile_d) * 0.1;
    vec2 res = vec2(plane_d, MAT_FLOOR);

    res = MapSphere(
        pos,
        vec3(0.0, 0.1 + sin(iTime)*0.05, 0.0),
        0.25,
        MAT_WHITE,
        res);

    res = MapSphere(
        pos,
        vec3(0.0, 0.1, 0.0) + 
        	ORBIT_A.yzx * ORBIT_DIST * sin(time_a) +
        	ORBIT_A.zxy * ORBIT_DIST * cos(time_a),
        0.1,
        MAT_RED,
        res);

    res = MapSphere(
        pos,
        vec3(0.0, 0.1, 0.0) +
        	ORBIT_B.yzx * ORBIT_DIST * sin(time_b) +
        	ORBIT_B.zxy * ORBIT_DIST * cos(time_b),
        0.1,
        MAT_GREEN,
        res);

    res = MapSphere(
        pos,
        vec3(0.0, 0.1, 0.0) +
        	ORBIT_C.yzx * ORBIT_DIST * sin(time_c) +
        	ORBIT_C.zxy * ORBIT_DIST * cos(time_c),
        0.1,
        MAT_BLUE,
        res);

    res = MapColumn(
        vec3(mod(pos.x*0.5, 1.0)-0.5, pos.y, mod(pos.z, 1.0)-0.5),
        vec3(
            0.0,
            -0.4,
            0.0
        ),
        0.04,
        1.0,
        MAT_COLUMN,
        res);    

    return res;
}

vec3 CalcNormal(in vec3 pos) {

    vec2 e = vec2(0.0001, 0.0);

    return normalize(
        vec3(
	        MapScene(pos+e.xyy).x-MapScene(pos-e.xyy).x,
   	    	MapScene(pos+e.yxy).x-MapScene(pos-e.yxy).x,
    	    MapScene(pos+e.yyx).x-MapScene(pos-e.yyx).x
    	)
   	);
}

vec2 CastRay(in vec3 ray_origin, vec3 ray_dir) {

    vec2 res = vec2(0.0, -1.0);
    float t = 0.0;

    for (int i = 0; i < 400; ++i) {

        vec3 pos = ray_origin + t * ray_dir;

        vec2 h = MapScene(pos);

        if (h.x < 0.001)
            return vec2(t, h.y);

        t += h.x;

        if (t>20.0)
            return vec2(-1.0, MAT_SKY);
    }

    return vec2(t, MAT_SKY);
}

float CalcCloud(in vec3 ray_origin, vec3 ray_dir) {
    if (ray_dir.y < 0.01)
        return 0.0;

    vec3 cloud_pos = ray_origin + ray_dir * ((10.0-ray_origin.y)/ray_dir.y);
    cloud_pos.xz += vec2(iTime*2.0);
    float cloud_color = 0.0;

    cloud_color += Noise((cloud_pos.xz+vec2(iTime*2.0))*0.1) * 0.2;
    cloud_color += mix(-1.0, 1.0, Noise((cloud_pos.xz+vec2(iTime*3.3)-ray_dir.xz*2.0)*0.2)) * 0.1;
    cloud_color += mix(-1.0, 1.0, Noise((cloud_pos.xz+vec2(iTime*3.0)+ray_dir.xz*3.0)*0.4)) * 0.05;
    cloud_color += mix(-1.0, 1.0, Noise((cloud_pos.xz+vec2(iTime*2.5)+ray_dir.xz*4.0)*0.6)) * 0.025;

    return smoothstep(max(0.0, cloud_color), 0.0, 0.1);
}

float CastSoftShadow(in vec3 ray_origin, vec3 ray_dir) {

    float res = max(0.1, 1.0-CalcCloud(ray_origin, ray_dir)*1.2);

    float t = 0.02;
    float tmax = 12.0;

    for (int i = 0; i < 25; ++i) {

        vec3 pos = ray_origin + t * ray_dir;

        float h = MapScene(pos).x;
        res = min(res, max(0.0, 16.0 * h/t));
        t += clamp(h, 0.05, 0.4);

        if (res < 0.005 || t>tmax)
            break;
    }

    return res;
}

float RaymarchPixel(in vec3 ray_origin, in vec3 ray_dir, out vec3 hit_pos, out vec3 color, out vec3 normal) {

    vec3 fog_color = vec3(1.0);

    vec2 hit = CastRay(ray_origin, ray_dir);

    if (hit.x > 0.0) {

        hit_pos = ray_origin + hit.x * ray_dir;
        normal = CalcNormal(hit_pos);

        vec3 sun_dir = normalize(vec3(0.8,0.4,0.2));
        float sun_dif = clamp(dot(normal, sun_dir), 0.0, 1.0);
        float sun_sha = CastSoftShadow(hit_pos + normal*0.001, sun_dir);
        float sky_dif = 0.5 + 0.5 * normal.y;
        float bounce_dif = 0.4 + normal.y * -0.5;

        vec3 light = vec3(0.0);

        light += vec3(10.0, 6.0, 3.0) * sun_dif*sun_sha;
        light += vec3(0.5, 0.8, 0.9) * sky_dif;
        light += vec3(0.7, 0.3, 0.2) * bounce_dif;

        vec3 material = vec3(0.2);
        float reflection = 0.45;

        if (hit.y == MAT_FLOOR) {
	        material = vec3(0.2*clamp(sin(hit_pos.x*10.0)*sin(hit_pos.z*10.0)*1.0+0.05, 0.01, 0.2));
            reflection = 0.05;
        }
        else if (hit.y == MAT_COLUMN) {
            material = vec3(0.25);
            reflection = 0.0;
        }
        else if (hit.y == MAT_WHITE) {
            material = vec3(0.18);
        }
        else if (hit.y == MAT_RED) {
            material = vec3(0.2, 0.01, 0.04);
        }
        else if (hit.y == MAT_GREEN) {
            material = vec3(0.01, 0.2, 0.04);
        }
        else if (hit.y == MAT_BLUE) {
            material = vec3(0.01, 0.04, 0.2);
        }

        color = material * light;
        // distance fog
        color = mix(color, fog_color, clamp(hit.x*hit.x*0.01, 0.0, 1.0));

        return reflection;

    } else {

        color = vec3(0.4, 0.75, 1.0) - 0.7*ray_dir.y;

        color += CalcCloud(ray_origin, ray_dir);

        color = mix(color, fog_color, clamp(exp(-8.0*ray_dir.y), 0.0, 1.0));

        hit_pos = normal = vec3(0.0);

        return 0.0;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = PixelCoords(fragCoord);

    float an = 10.*iMouse.x/iResolution.x;
    an += iTime * -0.09;

    vec3 ray_origin = vec3(sin(an), 0.1, cos(an));
    vec3 look_at = vec3(0.0, 0.1, 0.0);

    vec3 ray_dir = CreateRayDir(ray_origin, look_at, p);

    vec3 color = vec3(1.0);
    float hit_strength = 1.0;

    for (int n = 0; n < 3; ++n) {
        vec3 hit_pos, hit_color, hit_normal;

        float reflect_amount = RaymarchPixel(
            ray_origin,
            ray_dir,
            hit_pos,
            hit_color,
            hit_normal
        );

        color = mix(color, hit_color, hit_strength);
        hit_strength *= reflect_amount;

        if (hit_strength > 0.001) {
            ray_origin = hit_pos + hit_normal * 0.001;
	        ray_dir = reflect(ray_dir, hit_normal);            
        }
        else {
            break;
        }
    }

    // Output to screen
    fragColor = vec4(ApplyGamma(color),1.0);
}

