mat3 flMat;
vec3 ltDir, flPos;
vec2 gId;
float tCur, dstFar, hgSize, twDir, cCol;
int idObj;
bool isOcc;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, r, a, rad, ht, s;
  dMin = dstFar;
  if (isOcc) {
    p.xz -= HexToPix (gId * hgSize);
    p.y -= 1.;
    r = length (p.xz);
    a = (r > 0.) ? atan (p.z, - p.x) / (2. * pi) : 0.;
    rad = 1.;
    ht = 0.5;
    q = p;
    q.y -= - ht;
    s = rad * (1. + 0.2 * q.y / ht + 0.03 * sin (32. * 2. * pi * a));
    d = PrCylAnDf (q.xzy, s, 0.01, ht);
    DMIN (1);
    q.y -= ht + 0.05 - 0.15 * r * r;
    d = PrCylDf (q.xzy, s, 0.05);
    DMIN (2);
    rad = 0.3;
    ht = 1.;
    q = p;
    q.y -= ht;
    d = SmoothMin (PrCylDf (q.xzy, rad * (1. + 0.2 * sin (2. * pi * (2. * q.y / ht +
       twDir * a))) * (1. - 0.95 * q.y / ht), ht), d, 0.05);
    DMIN (3);
    dMin *= 0.5;
  }
  return dMin;
}

void SetGrObjConf ()
{
  vec2 fRand;
  float emFrac;
  emFrac = 0.1;
  fRand = Hashv2v2 (gId * vec2 (37.3, 43.1) + 27.1);
  isOcc = (fRand.y >= emFrac);
  if (isOcc) {
    fRand.y = (fRand.y - emFrac) / (1. - emFrac);
    twDir = (fRand.y < 0.5) ? -1. : 1.;
    cCol = fRand.x;
  }
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrObjConf ();
    }
    d = ObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  gIdP = vec2 (-99.);
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrObjConf ();
    }
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.05, 0.3);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  vec2 vf;
  float dstObj, sh, s;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) {
      col4 = vec4 (1., 1., 0.9, 0.);
      vf = vec2 (64., 0.5);
    } else if (idObj == 2) {
      col4 = vec4 (0.4, 0.2, 0., 0.2);
      vf = vec2 (16., 0.1);
    } else if (idObj == 3) {
      if (cCol < 0.8) {
        cCol /= 0.8;
        col4 = vec4 (HsvToRgb (vec3 (floor (8. * cCol) / 8., 0.3, 1.)), 0.1);
      } else {
        col4 = vec4 (vec3 ((cCol > 0.9) ? 0.1 : 1.2), 0.2);
      }
      vf = vec2 (32., 0.5);
    }
    vn = VaryNf (vf.x * ro, vn, vf.y);
  } else if (rd.y < 0.) {
    dstObj = - ro.y / rd.y;
    ro += dstObj * rd;
    vn = vec3 (0., 1., 0.);
    col4 = vec4 (mix (vec3 (0.7, 0.7, 0.65), vec3 (0.8, 0.8, 0.85),
       smoothstep (0.45, 0.55, Fbm2 (2. * ro.xz))), 0.3);
    if (dstObj < dstFar) {
      gId = PixToHex (ro.xz / hgSize);
      SetGrObjConf ();
      if (isOcc) col4.rgb *= 0.7 + 0.3 * smoothstep (0.8, 1.,
         length (ro.xz - HexToPix (gId * hgSize)));
    }
  } else {
    col = vec3 (0.6, 0.6, 0.6);
  }
  if (dstObj < dstFar) {
    sh = ObjSShadow (ro, ltDir);
    col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  }
  return clamp (col, 0., 1.);
}

vec3 TrackPath (float t)
{
  return t * vec3 (0.1, 0., sqrt (0.99)) + vec3 (2. * cos (0.1 * t), 0., 0.);
}

void VuPM (float t)
{
  vec3 fpF, fpB, vel, acc, va, ort, cr, sr;
  float dt;
  dt = 1.;
  flPos = TrackPath (t);
  fpF = TrackPath (t + dt);
  fpB = TrackPath (t - dt);
  vel = (fpF - fpB) / (2. * dt);
  vel.y = 0.;
  acc = (fpF - 2. * flPos + fpB) / (dt * dt);
  acc.y = 0.;
  va = cross (acc, vel) / length (vel);
  ort = vec3 (0.2, atan (vel.z, vel.x) - 0.5 * pi, 5. * length (va) * sign (va.y));
  cr = cos (ort);
  sr = sin (ort);
  flMat = mat3 (cr.z, - sr.z, 0., sr.z, cr.z, 0., 0., 0., 1.) *
     mat3 (1., 0., 0., 0., cr.x, - sr.x, 0., sr.x, cr.x) *
     mat3 (cr.y, 0., - sr.y, 0., 1., 0., sr.y, 0., cr.y);
}

#define AA  1 

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 dateCur;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  tCur = mod (tCur, 2400.) + 30. * floor (dateCur.w / 7200.) + 11.1;
  hgSize = 2.;
  VuPM (0.9 * tCur);
  az = 0.;
  el = -0.08 * pi;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  flPos.y += 8.;
  ro = flPos;
  zmFac = 6. + 2. * sin (0.02 * 2. * pi * tCur);
  dstFar = 50.;
  ltDir = normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd = vuMat * rd;
    rd = rd * flMat;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// Converts a color from linear light gamma to sRGB gamma
// from https://gamedev.stackexchange.com/a/148088
vec4 fromLinear(vec4 linearRGB)
{
    bvec4 cutoff = lessThan(linearRGB, vec4(0.0031308));
    vec4 higher = vec4(1.055)*pow(linearRGB, vec4(1.0/2.4)) - vec4(0.055);
    vec4 lower = linearRGB * vec4(12.92);

    return mix(higher, lower, cutoff);
}
// Converts a color from sRGB gamma to linear light gamma
vec4 toLinear(vec4 sRGB)
{
    bvec4 cutoff = lessThan(sRGB, vec4(0.04045));
    vec4 higher = pow((sRGB + vec4(0.055))/vec4(1.055), vec4(2.4));
    vec4 lower = sRGB/vec4(12.92);

    return mix(higher, lower, cutoff);
}

struct material {
    vec4 color;
    bool light;
    float reflectivity;
};

const float tau = 6.28318;

material blue = material(vec4(.1, .2, .9, 1.), false, .2);
material mirror = material(vec4(1., 1., 1., 1.), false, 1.);
material redLight = material(vec4(4., .0, 0., 1.), true, 0.1);
material ground = material(vec4(.4, .3, .1, 1.), false, 0.0);

struct hit {
    float dist;
    material mat;
    vec3 hitNorm;
};

const float epsilon = 0.001;

float minNZ(float a, float b) {
    if (a < 0.)
        return b;
    if (b < 0.)
        return a;
    return min(a, b);
}

hit minHit(hit a, hit b) {
    if (a.dist < 0.)
        return b;
    if (b.dist < 0.)
        return a;
    if (a.dist < b.dist)
        return a;
    else
        return b;
}

hit intersectPlane(vec3 rd, vec3 ro, vec3 n, float d, material mat) {
    float denom = dot(n, rd);
    if (denom == 0.) return hit(-1., mat, n);
    float t = -((dot(n, ro) + d) / denom);
    return hit(t > epsilon ? t : -1., mat, n);   
}

hit intersectSphere(vec3 rd, vec3 roWorld, vec3 centre, float radius, material mat) {
    vec3 ro = roWorld - centre;
    float b = 2. * dot(rd, ro);
    float c = dot(ro, ro) - (radius * radius);

    float root = sqrt(b*b - 4.*c);

    float t1 = (-b + root) / 2.;
    float t2 = (-b - root) / 2.;
    float t = minNZ(t1, t2);
    vec3 hitNorm = normalize(ro + rd*t);
	return hit(t > epsilon ? t : -1., mat, hitNorm);
}

hit intersect(vec3 rd, vec3 ro) {
    material whiteLight = material(vec4(vec3((sin(iTime*2.)+1.) * 2. + 0.2), 1.), true, 0.);

    return minHit(minHit(
        minHit(minHit(intersectPlane(rd, ro, normalize(vec3(0, 1, 0)), 0., ground),
               intersectSphere(rd, ro, vec3(3, 1, 10), 3., whiteLight)),
               intersectSphere(rd, ro, vec3(7.*sin(iTime) + 3., .5, 7.*cos(iTime) + 10.), 2., blue)),
               intersectSphere(rd, ro, vec3(-10., 4, 10.), 4., redLight)),
               intersectSphere(rd, ro, vec3(-7.*sin(iTime) + 3., .5, -7.*cos(iTime) + 10.), 2., mirror));


}


