//from https://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html
uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
float getRand( float f ) {
    const uint mantissaMask = 0x007FFFFFu;
    const uint one          = 0x3F800000u;

    uint h = hash( floatBitsToUint( f ) );
    h &= mantissaMask;
    h |= one;

    float  r2 = uintBitsToFloat( h );
    return r2 - 1.0;
}


vec3 sampleSphere(float seed) {
    float u1 = getRand(seed);
    float u2 = getRand(seed+1.);
    float r = sqrt(1. - u1*u1);
    float phi = tau * u2;
    return vec3(cos(phi) * r, sin(phi) * r, 2. * u1);
}

vec3 sampleHemisphere(vec3 normal, float seed) {
    vec3 v = sampleSphere(seed*2.);
    if (dot(normal, v) < 0.)
        return -v;
    else
        return v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - .5) * vec2(1, iResolution.y / iResolution.x);

    vec3 camPos = vec3(0, 3, -10);
    vec3 lookAt = vec3(0, 2.9, -9);


    vec3 skyCol = vec3(.05, .05, .06);
    vec3 col = vec3(0.);
    int maxBounces = 3;
    int rpp = 500;
    for(int rays = 0; rays<rpp; rays++) {
        vec3 filmPos = lookAt + vec3(uv, 0) + 0.005*vec3(getRand(float(rays)));
    	vec3 rd = normalize(filmPos - camPos);
    	vec3 ro = filmPos;

        vec3 rayCol = vec3(1. / float(rpp));
        int bounces = 0;
        for(; bounces<maxBounces; bounces++) {
            hit h = intersect(rd, ro);
            if (h.dist > epsilon) {
                vec3 hitPos = ro + h.dist*rd;
                vec3 perfectReflect = reflect(rd, h.hitNorm);
                float seed = getRand(getRand(fragCoord.x * iResolution.x + fragCoord.y) + float(rays)) + float(bounces);
                vec3 randReflect = sampleHemisphere(h.hitNorm, seed);
                rd = normalize(mix(randReflect, perfectReflect, h.mat.reflectivity));
                ro = hitPos;
                rayCol *= h.mat.color.rgb;
                if (h.mat.light) {
                    break;
                }
            } else {
                rayCol *= skyCol;
                break;
            }
        }
        if (bounces == maxBounces)
            rayCol = vec3(0);

        col += rayCol;
    }

    fragColor = fromLinear(vec4(col,1.0));
    //fragColor = texture(iChannel1, fragCoord / iResolution.xy);
}

// COLORS
// src : nvidia
float saturate(float x)
{
    return max(0.0, min(1.0, x));
}


vec3 saturate(vec3 v)
{
    return vec3(
        max(0.0, min(1.0, v.x)),
        max(0.0, min(1.0, v.y)),
        max(0.0, min(1.0, v.z))
        );
}

// src reefwirrax
vec3 Hue(float h)
{
    float r = abs(h*6.0-3.0)-1.0;
    float g = 2.0 - abs(h*6.0-2.0);
    float b = 2.0 - abs(h*6.0-4.0);
    return saturate(vec3(r,g,b));
}

vec3 HSVtoRGB(vec3 hsv)
{
    return vec3(((Hue(hsv.x) - 1.0) * hsv.y + 1.0) * hsv.z);
}

// world
// src : Fabrice Castel
float DistanceToNearestSurface(vec3 p)
{
    float t;
    p.y += sin(p.z * 0.25 + iTime * 5. + sin(p.x + iTime * 10.));

    vec3 q = vec3(mod(p.x, 3.0) - 1.5, p.y, mod(p.z, 3.0) - 1.5);
    float s = 1.0;
    vec3 d = abs(q) - vec3(s);

    t = min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));

    return t;
}

float castRay(vec3 rayOrigin, vec3 rayDir)
{
    float t;
    float anim = fract(iTime);

    for(int i=0; i<128 ; i++)
    {
        float res = 0.0;
        vec3 march = rayOrigin + rayDir * t;
        res = DistanceToNearestSurface(march);

        if(res < (0.0001*t))
        {
            return t;
        }
        t += res;
    }

    return -1.0;
}

vec2 NormalizeScreen(vec2 st)
{
    vec2 uv = (2.0 * st/iResolution.xy - 1.0);
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

vec3 render(vec2 uv, vec3 rayOrigin, vec3 rayDir)
{
    vec3 col;
    float t = castRay(rayOrigin, rayDir);
    float ao = pow(1.-t * 0.005, 1.);
    vec3 rainbow = vec3(HSVtoRGB(vec3(fract(ao+iTime*.5), 1.0, 1.0)));

    if(t == -1.0)
    {
        vec2 st = uv;
        float angle = fract(atan(st.x, st.y)*5.);
        float sun = smoothstep(.3, .31, length(st));
        sun += step(.8, fract(st.y*10.-.3));

        float at = angle+iTime;
        vec3 yolo = rainbow * (smoothstep(.1, .3, fract(at))-smoothstep(.3, .5, fract(at)));

        col = mix(
            vec3(max(mix(vec3(.0), yolo, smoothstep(.29, .30, length(st))), mix(rainbow, vec3(.0), sun))), // 
            vec3(.0),
            vec3(step(0.03, rayDir.y))
            );
    }
    else
    {
        col = rainbow;
    }

    return col;
}

vec3 CameraViewDir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    vec3 viewDir;
    vec3 forward = normalize(camTarget - camPos);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));
    float fPersp = 4.0;

    viewDir = normalize(uv.x * right + uv.y * up + fPersp * forward);

    return viewDir;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(.5);
    float offsetTime = iTime+1000.;
    vec3 camPos = vec3(sin(offsetTime)*5., -5., offsetTime);
    vec3 camTarget = vec3(0.0, 0.0, 0.0);

    vec2 uv = NormalizeScreen(fragCoord);
    vec3 viewDir = CameraViewDir(uv, camPos, camTarget);

    col = vec3(length(uv)-.5);

    col = render(uv, camPos, viewDir);

    fragColor = vec4(col, 1.0);
}

float PI = 3.14159265;
mat2 genRot(float v){
	return mat2(cos(v),-sin(v),sin(v),cos(v));
}
float rand(float p){
	return sin(p) * 3. + cos(p) / 2. + sin(p/2.) * 2.;
}
vec2 pmod(vec2 p, float c){
    p *= genRot(PI/c);
    float r = length(p);
	float at = atan(p.y/p.x);
    at = mod(at , PI * 2./c);
    p = vec2(cos(at) * r,sin(at) * r);
    p *= genRot(-PI/c);
    return p;
}
float map(vec3 p){
    float rc = 0.25 + abs(floor(rand(p.x + iTime/4.) * 5.) + floor(rand(p.y + iTime/4.) * 5.) + floor(rand(p.z + iTime/4.) * 5.)) * 0.015;
    float sp = length(p - vec3(0.,0.,iTime)) - rc * 2.;
    p.z = (fract(p.z/2. + 0.5)-0.5 ) * 2.;
        p.xy = pmod(p.xy,4.);
        p.x -= 4.;
    p.xy = pmod(p.xy,8.);
    float r = 0.25 + abs(floor(rand(p.x + iTime) * 5.) + floor(rand(p.y + iTime) * 5.) + floor(rand(p.z + iTime) * 5.)) * 0.015;
	float sps = length(p - vec3(3.,0.,0.)) - r;
    return min(sp,sps);
}

vec3 getNormal(vec3 p){
	vec3 x = dFdx(p);
    vec3 y = dFdy(p);
    return normalize(cross(x,y));
}

vec4 trace(vec3 o,vec3 r){
	vec4 res;
    float t = 0.;
    for(int i = 0; i < 64; i++){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    vec3 n = getNormal(o + r * t);
    return vec4(n,t);

}
vec3 cam(){
	vec3 c = vec3(2. * sin(iTime),2. * -cos(iTime),-2.5 + iTime);
    return c;
}
vec3 ray(vec2 uv,float z) {
	vec3 r = normalize(vec3(uv,z));
    r.yz *= genRot(-PI/4.);
    r.xz *= genRot(-PI/4.);
    r.xy *= genRot(iTime);
    return r;
}
vec3 getCol(vec3 o,vec3 r,vec4 data){
	float t = data.w;
    vec3 n = data.xyz;
    vec3 bc = vec3(1. - dot(n,r));
    vec3 p = o + r * t;
    vec3 ccol;
    float at = atan(p.y / p.x) * 2.;
    ccol.x = sin(at + iTime + p.z) / 2. + 0.5;
    ccol.y = sin(at + iTime + p.z + PI * 2. /3.) / 2. + 0.5;
    ccol.z = sin(at + iTime + p.z + PI * 4. / 3.) / 2. + 0.5;
    bc *= ccol;
    float fog = 1./(1. + t * t * 0.015);
    vec3 col = mix(bc,vec3(0.),1. - fog);
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy)/iResolution.y;
    vec3 o = cam();
    vec3 r = ray(uv,1.5 * sin(iTime));
    vec4 data = trace(o,r);
    vec3 col = getCol(o,r,data);
    fragColor = vec4(col,1.0);
}

#define PI 3.141592
float hash(float x){
    x = fract(sin(x*416.31434));
    x = fract(sin(x*234.41523));
    x = fract(sin(x*235.51424));
	return x;
}

mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}
float smin(float a, float b, float k) {
    float h = clamp( 0.5 + 0.5*(b - a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 uni(vec2 a, vec2 b) {
    if (a.x < b.x) return a;
    return b;
}

vec3 eye() {
    vec3 p = vec3(-.2,.3,2.5);
    p.yz = rot(.05 * cos(iTime/17.)) * p.yz;
    p.xy = rot(.1 * sin(iTime/8.)) * p.xy;
    return p;
}

vec3 look() {
    return vec3(.2 * cos(iTime/3.9)+.5,.2 * sin(iTime/2.7) -.4,.1 * sin(iTime/11.2));
}

float sph(vec3 p, vec3 c, float r) {
    return length(p - c) - r;
}

float box(vec3 p, vec3 c, vec3 r) {
    vec3 d = abs(p - c) - r;
    return length(max(d, 0.)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float menger(vec3 p) {
   float d = -1000.;

   float s = 1.0;
   for( int m=0; m<3; m++ )
   {
      vec3 a = mod( p*s, 2.0 )-1.0;
      s *= 3.0;
      vec3 r = abs(1.0 - 3.0*abs(a));

      float da = max(r.x,r.y);
      float db = max(r.y,r.z);
      float dc = max(r.z,r.x);
      float c = (min(da,min(db,dc))-1.0)/s;

      d = max(d,c);
   }

   return d;
}

float torus(vec3 p, vec3 c, float r1, float r2) {
    vec3 v = p - c;
    return length(vec2(length(v.xy) - r1, v.z)) - r2;
}
vec2 sdf(vec3 p) {
    float ships = 1000.;
    for (int i = 0; i < 3; i++) {
        float angle = 2.*float(i)*PI/3. + iTime/1.3;
        vec3 q = p - vec3(.2*cos(angle), .2*sin(angle), 1.5);
        q.yz = rot(iTime + angle) * q.yz;
        q.xy = rot(iTime/3.2) * q.xy;
        float shipsize = .1;
        float ship = max(box(q, vec3(0., 0., 0.), vec3(shipsize )), menger(q/shipsize)*shipsize);
        ships = min(ships, ship);
    }
    float z = p.z - iTime;
	p.x += cos(z)* .2;
   	float d =  1. - length(p.xy);
    p.xy = rot(z + sin(z) + iTime/5. + sin(iTime/4.)/6.) * p.xy;
    float s = 1.;
    z /= 2.;
    for (int i = 0; i < 7; i++) {
        p *= 1.9;
        s *= 1.9;
        z *= 2.57;
        d += (sin(z/4.1) + cos(z/.4)) * sin(p.x+cos(p.y)) * .3/s;
    }
    return uni(vec2(d, 0.), vec2(ships, 1.));
}

vec3 march(vec3 start, vec3 ray) {
    float t = 0.0;
    vec3 c = vec3(1., 0.5, 0.);
    for (int i = 0; i < 99; i++) {
        vec3 p = start + t * ray;
        vec2 d = sdf(p);
        if (d.y == 0.) {
            c += vec3(0.05, d.x/20., d.x*sqrt(sqrt(t))/10.);
        } else {
            c += vec3(.0, .03, 0.05);
        }
        t += d.x * .6;
        if (d.x/t < 0.001) {
            if (d.y == 1.) c -= vec3(1., 0., 0.);
            return c;
        }
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.)/iResolution.y;

    vec3 e = eye();
    vec3 l = look();
    vec3 fwd = normalize(l - e);
    vec3 right = normalize(cross(fwd, vec3(0.,1.,0.)));
    vec3 up = cross(right, fwd);

    vec3 ray = normalize(uv.x * right + uv.y * up + fwd);

    float vignette = 1. - length(uv);
    float noise = hash(uv.x + hash(uv.y + hash(iTime)));
    vec3 c = vignette * march(e, ray) + noise * .05;
	float w = c.r + c.g + c.b;

    // Output to screen
    fragColor = vec4(c * (w/(5. + w)),1.0);
}

