//mapping
float map(vec3 pos)
{
    vec3 m = vec3(8.0);
    vec3 p = mod(pos, m) - m/2.0;
    float s1 = sphere(p, 1.2);
    float b1 = box(p, vec3(1.0,1.0,1.0));
    float bss = nsSubtraction(s1,b1);
    float r = 0.3;
    float k = 2.0;
    float d = 1.5;
    vec3 rot = vec3(iTime*0.57, iTime*0.73, iTime*1.12)*0.3;
    bss = sUnion(bss, sphere(translate(rotate(p, rot),vec3(d,d,d)),r),k);
    bss = sUnion(bss, sphere(translate(rotate(p, rot),vec3(d,-d,-d)),r),k);
    bss = sUnion(bss, sphere(translate(rotate(p, rot),vec3(-d,d,-d)),r),k);
    bss = sUnion(bss, sphere(translate(rotate(p, rot),vec3(-d,-d,d)),r),k);

    bss = sUnion(bss, plane(pos, -10.0), k);

    return bss;

}

// calculating normal
vec3 calcNorm(vec3 pos)
{
    float d = 0.0001;
    float center = map(pos);
    vec3 grad = vec3(map(pos+vec3(d,0.0,0.0))-center, map(pos+vec3(0.0,d,0.0))-center, map(pos+vec3(0.0,0.0,d))-center);
    return normalize(grad / d);
}

// lambert model
vec4 lambert(vec3 pos)
{
    vec3 n = calcNorm(pos);
    float b = max(0.0, dot(n, -LIGHT_DIR))*0.9+0.1;
    return vec4(b, b, b, 1.0);
}

vec4 visualizeNormal(vec3 pos)
{
    return vec4(calcNorm(pos),1.0)*0.8 + vec4(1.0,1.0,1.0,1.0)*0.2;
}

// raymarching
Ray raymarch(Ray r, float refTime)
{
    vec3 pos = r.origin;
    vec3 dir = r.direction;

    float d = 0.0;

    for(int i=0;i<STEP;i++)
    {
        d = map(pos);
        if(d < 0.00001)
        {
            if(refTime == REFLECT_LIMIT - 1.0)
            {
                r.frag = true;
            }else{
                vec3 n = calcNorm(pos);
                r.direction = dir - 2.0 * dot(dir, n) * n;
                r.origin = pos + r.direction * 0.001;
            }
            //r.color += vec4(lambert(pos).xyz * pow(0.5, refTime+1.0),0.0);
            r.color += vec4(visualizeNormal(pos).xyz * pow(0.5, refTime+1.0),0.0);
            return r;
        }
        pos = pos + dir * d;
    }
    r.frag = true;
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 fc = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 ray = normalize(vec3(fc, NEAR));
    vec3 pos = vec3(fc, NEAR);

    //camera rotating
    float rx = (iMouse.x/iResolution.x*2.0 - 1.0) * PI;
    float ry = (iMouse.y/iResolution.y*2.0 - 1.0) * PI * 0.5;
    pos = rotate(pos, vec3(ry,-rx,0.0));
    ray = rotate(ray, vec3(ry,-rx,0.0));

    // camera moving
    pos = translate(pos, vec3(0.0,0.0,5.0));

    Ray r;
    r.direction = ray;
    r.origin = pos;
    r.color = vec4(0.0,0.0,0.0,1.0);
    r.frag = false;


    for(float i=0.0;i<REFLECT_LIMIT;i++)
    {
        r = raymarch(r,i);
        if(r.frag) break;
    }
    fragColor = r.color;
}

