    vec2 e = vec2(pix/2.+pix*.2,0.);

    vec3 c = pixel(uv+e.xy);   
   // c+=pixel(uv-e.xy);
   // c+=pixel(uv-e.yx);
   // c+=pixel(uv-e.yx);
    //c/=4.0;
	o = vec4(c,1.0); 
}

float rect(float left,float right, float top, float bottom, vec2 uv){
    float blur=1./iResolution.y;
    float c = 1.;
    c = smoothstep(left-blur,left+blur,uv.x);
    c *= smoothstep(right+blur,right-blur,uv.x);
    c *= smoothstep(top-blur,top+blur,uv.y);
    c *= smoothstep(bottom+blur,bottom-blur,uv.y);
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;
    float ang = iTime/2.;
	// uv=vec2(cos(ang)*uv.x+(-sin(ang)*uv.y),sin(ang)*uv.x+(cos(ang)*uv.y));
    // 
	mat2x2 rot = mat2x2(vec2(cos(ang),-sin(ang)),vec2(sin(ang),cos(ang)));
    uv *= rot;

    float m = rect(-0.1,0.1,-0.1,0.1,uv);
    vec3 col = vec3(1.,1.,1.)*m;
    fragColor = vec4(col,1.0);
}

float noise(float t){
	return 0.5 + 0.5 * (sin(t * 4.) + cos(t * 2.) + sin(t / 2.)) / 3.;
}
mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
vec2 path(float t){
	float x = (2./3. * cos(t * 40.) + 1./3. * cos(t * 40. * 1.5)) * (0.75 + noise(t) * 0.75)/2.;
    float y = (2./3. * sin(t * 40.) - 1./3. * sin(t * 40. * 1.5)) * (0.75 + noise(t) * 0.75)/2.;
    return vec2(x,y);
}
float dist(vec2 p,float t){
    p *= 1. * (1.5 + 0.75 * sin(iTime/2.));
    p *= genRot(sin(iTime/2.) *3. * 3.1415 / 5.);
    p.y += floor((p.x +  0.75) / 1.5) * 0.75;
    p += 0.75;
    p = fract(p / 1.5) * 1.5;
    p -= 0.75;

	float result = 1000000.;
    for(float i = 0.0;i < 1.5; i += 0.0005){
    	float c = length(p - path(t - i)) - 0.0015 * (2.0 - i) / 2.;
        result = min(result,c);
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    //uv -= vec2(cos(iTime/4.),sin(iTime/4.)) * 2.;
	vec3 col = mix(vec3(0.),vec3(1.0),dist(uv,iTime) * 256.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float roundBox(vec2 p, vec2 size, float r){
   return length(p-clamp(p,-size,size))-r;
}

float roundBox(vec3 p, vec3 size, float r){
   return length(p-clamp(p,-size,size))-r;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float lengthN(vec2 v, float n){
vec2 tmp = pow(abs(v), vec2(n));
return pow(tmp.x+tmp.y, 1.0/n);
} 

// https://www.shadertoy.com/view/lssBDM
vec2 solveCubic(float a, float b, float c)
{
    float p = b - a*a / 3.0, p3 = p*p*p;
    float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
    float d = q*q + 4.0*p3 / 27.0;
    float offset = -a / 3.0;
    if(d >= 0.0)
    { 
        float z = sqrt(d);
        vec2 x = (vec2(z, -z) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        return vec2(offset + uv.x + uv.y);
    }
    float v = acos(-sqrt(-27.0 / p3) * q / 2.0) / 3.0;
    float m = cos(v), n = sin(v)*1.732050808;
    return vec2(m + m, -n - m) * sqrt(-p / 3.0) + offset;
}

vec3 opBezier(vec2 p, vec2 a, vec2 b, vec2 c)
{   
    b = mix(b + vec2(1e-4), b, abs(sign(b * 2.0 - a - c)));
    vec2 A = b - a, B = a - b * 2.0 + c, C = A * 2.0, D = a - p;
    vec3 k = vec3(3.*dot(A,B),2.*dot(A,A)+dot(D,B),dot(D,A)) / dot(B,B);      
    vec2 t = clamp(solveCubic(k.x, k.y, k.z), 0.0, 1.0);
    vec2 dp1 = D + (C + B*t.x)*t.x;
    vec2 dp2 = D + (C + B*t.y)*t.y;
    float d1 = dot(dp1, dp1);
    float d2 = dot(dp2, dp2);
    vec2 h = (d1 < d2) ? vec2(d1, t.x) : vec2(d2, t.y);
    vec2 g = normalize(2.*B*h.y + C);
    p -= mix(mix(a, b, h.y), mix(b, c, h.y), h.y);
    float y = g.x*p.y-g.y*p.x;
    float x = sqrt(max(0.0,h.x-y*y))*sign(h.y-0.5);
    return vec3(x,y,h.y);
}

vec4 opBezier(vec3 p, vec3 a, vec3 b, vec3 c)
{
	vec3 w = normalize(cross(a-b, b-c));
	vec3 v = normalize(cross(w, a-b));
	vec3 u = cross(w,v);
    mat3 m = mat3(u,v,w);
    a *= m, b *= m, c *= m, p *= m;
    vec3 bz = opBezier(p.xy,a.xy,b.xy,c.xy);
    return vec4(p.z,bz.yxz);
}

float map(in vec3 p)
{   
    p.xz*=rot(iTime*0.5);
	vec3 a=vec3(-2,0,0);
    vec3 b=vec3(0,2.0+sin(iTime*1.5)*0.8,0);
    vec3 c=vec3(2,1,sin(iTime*0.8)*2.0);
	vec4 bz=opBezier(p,a,b,c);
    float de;
    switch(int(mod(iTime*0.3,4.0)))
    {
		case 0:
        	de = roundBox(bz.xyz,vec3(0.5,0.5*bz.w+0.2,0.0),0.1);
        	break;
        case 1:
        	de = sdTorus(bz.xyz,vec2(1.0,0.3));
        	break;
        case 2:
        	float d2=abs(lengthN(bz.xy,3.0)-(.5+sin(bz.w*100.0)*0.02))-.05;
			de = length(max(vec2(d2,abs(bz.z)),0.0))-0.02;
        	break;
        default:
        	de = length(bz.xyz)-0.5;
        	break;
    }
    return de;
}

vec3 calcNormal(in vec3 pos){
  vec2 e = vec2(1.0, -1.0) * 0.002;
  return normalize(
    e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
    e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx)
  );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
	vec3 col=vec3(0.0);
    vec3 bz =opBezier(uv*2.0,vec2(-2,0),vec2(0,1),vec2(2,-1));    
    col = mix(col, vec3(0.5, 0.7, 1.0), smoothstep(0.08, 0.0, roundBox(bz.xy,vec2(0.1),0.01)));
    vec3 ro = vec3(0, 1, 5);
    vec3 rd = normalize(vec3(uv, -2.0));
	float t = 0.0, d;
 	for(int i = 0; i < 100; i++)
  	{
    	t += d = map(ro + rd * t);
    	if(d < 0.001) break;
  	}
  	if(d < 0.001)
  	{
	  	vec3 p = ro + rd * t;
	 	vec3 n = calcNormal(p);
    	vec3 li = normalize(vec3(1));
        vec3 bg = col;
        col = vec3(1.0, 0.8,0.2);
        float dif = clamp(dot(n, li), 0.3, 1.0);
        float amb = max(0.5 + 0.5 * n.y, 0.0);
        float spc = pow(clamp(dot(reflect(rd, n), li), 0.0, 1.0), 50.0);
        col *= dif * amb ;
        col += spc;
        col = clamp(col,0.0,1.0);
        col = mix(bg, col, exp(-exp2(length(p - ro))*0.002));
  		col = pow(col, vec3(0.6));        
    }
    fragColor = vec4(col, 1.0);
}

///////////////////////////////////////////////////////////////////////////////////
// Trying out some raymarching, and combining it with Schlicks Approximation //////
///////////////////////////////////////////////////////////////////////////////////

#define MARCH_EPSILON 0.01
#define MARCH_ITERATIONS 40

#define SMOOTH_DIST 0.5
#define MAT_CHANGE 0.1

#define TOO_FAR 10000.0

#define REFLECT_ITERATIONS 2
#define EPSILON 0.0001
#define MSAA 3.0

#define NUM_SPHERES 6

#define HURRY_UP 1.5

///////////////////////////////////////////////////////////////////////////////////

struct Material {
    vec3 colour;
    float diffuse;
    float specular;
};

struct Ray {
    vec3 pos;
    vec3 dir;
};

struct Sphere {
    vec3 pos;
    float radius;
    Material mat;
};

struct Light {
    vec3 dir;
    vec3 colour;
};

struct Result {
    vec3 pos;
    vec3 normal;
    Material mat;
};

///////////////////////////////////////////////////////////////////////////////////

Material g_NoMaterial = Material(vec3(1.0, 0.0, 1.0), 0.0, 1.0);

///////////////////////////////////////////////////////////////////////////////////

Sphere g_spheres[NUM_SPHERES];

Light g_light = Light(normalize(vec3(1.0, -1.0, 1.0)), vec3(150.0, 100.0, 50.0));

///////////////////////////////////////////////////////////////////////////////////

const float eps = 0.0000001;
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

vec3 rgb2hsv( in vec3 c)
{
    vec4 k = vec4(0.0, -1.0/3.0, 2.0/3.0, -1.0);
    vec4 p = mix(vec4(c.zy, k.wz), vec4(c.yz, k.xy), (c.z<c.y) ? 1.0 : 0.0);
    vec4 q = mix(vec4(p.xyw, c.x), vec4(c.x, p.yzx), (p.x<c.x) ? 1.0 : 0.0);
    float d = q.x - min(q.w, q.y);
    return vec3(abs(q.z + (q.w - q.y) / (6.0*d+eps)), d / (q.x+eps), q.x);
}

vec3 rgb2hsl( vec3 col )
{
    float minc = min( col.r, min(col.g, col.b) );
    float maxc = max( col.r, max(col.g, col.b) );
    vec3  mask = step(col.grr,col.rgb) * step(col.bbg,col.rgb);
    vec3 h = mask * (vec3(0.0,2.0,4.0) + (col.gbr-col.brg)/(maxc-minc + eps)) / 6.0;
    return vec3( fract( 1.0 + h.x + h.y + h.z ),              // H
                 (maxc-minc)/(1.0-abs(minc+maxc-1.0) + eps),  // S
                 (minc+maxc)*0.5 );                           // L
}

///////////////////////////////////////////////////////////////////////////////////

float blerp(float x, float y0, float y1, float y2, float y3) {
	float a = y3 - y2 - y0 + y1;
	float b = y0 - y1 - a;
	float c = y2 - y0;
	float d = y1;
	return a * x * x * x + b * x * x + c * x + d;
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float perlin(float x, float h) {
	float a = floor(x);
	return blerp(mod(x, 1.0),
		rand(vec2(a-1.0, h)), rand(vec2(a-0.0, h)),
		rand(vec2(a+1.0, h)), rand(vec2(a+2.0, h)));
}

///////////////////////////////////////////////////////////////////////////////////
// SDF's & other spatial query functions

float opSmoothUnion( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float sphereSDF(Sphere sphere, vec3 p) 
{
    return length(sphere.pos - p) - sphere.radius;
}

float sceneSDF(vec3 p)
{
    float mindist = TOO_FAR;
    for (int i=0; i<NUM_SPHERES; i++)
    {
        float dist = sphereSDF(g_spheres[i], p);
        dist = opSmoothUnion(mindist, dist, SMOOTH_DIST);
        if (dist < mindist)
        {
        	mindist = dist;    
        }
    }

	return mindist;
}

Material materialAt(vec3 p)
{
    float mindist = TOO_FAR;
    float weight = 1.0f;
    Material mat = Material(vec3(0.0), 0.0, 0.0);

    for (int i=0; i<NUM_SPHERES; i++)
    {
        float dist = sphereSDF(g_spheres[i], p);
        if (dist < SMOOTH_DIST)
        {
            float scale = (SMOOTH_DIST-dist)/SMOOTH_DIST;

        	mat.colour += g_spheres[i].mat.colour * scale;                    
        	mat.diffuse += g_spheres[i].mat.diffuse * scale;                    
        	mat.specular += g_spheres[i].mat.specular * scale;                    
            mindist = dist;
        }
    }

    if (mindist<SMOOTH_DIST)
        return mat;
    else
		return g_NoMaterial;
}

Result resultSDF(vec3 p)
{
    Result result;
	result.normal.x = sceneSDF(p + vec3(EPSILON, 0.0, 0.0)) - sceneSDF(p);
	result.normal.y = sceneSDF(p + vec3(0.0, EPSILON, 0.0)) - sceneSDF(p);
	result.normal.z = sceneSDF(p + vec3(0.0, 0.0, EPSILON)) - sceneSDF(p);
    result.normal = normalize(result.normal);
    result.mat = materialAt(p);
    result.pos = p;
    return result;
}

///////////////////////////////////////////////////////////////////////////////////
// raymarch query, returning intersection point, normal, surface colour

Result raymarch_query(Ray ray)
{
    Result result = Result(vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, 0.0), g_NoMaterial);
	for (int i=0; i<MARCH_ITERATIONS; i++)
    {
    	float signeddist = sceneSDF(ray.pos);

        ray.pos += signeddist*ray.dir*1.4;       
    }
    result = resultSDF(ray.pos);
    return result;
}

vec3 raymarch(Ray inputray)
{
    const float exposure = 1e-2;
    const float gamma = 2.2;
    const float intensity = 100.0;
    const vec3 ambient = vec3(0.1, 0.1, 0.1) *1.0* intensity / gamma;

    vec3 colour = vec3(0.0, 0.0, 0.0);
    vec3 mask = vec3(1.0, 1.0, 1.0);
    vec3 fresnel = vec3(1.0, 1.0, 1.0);

    Ray ray=inputray;

    for (int i=0; i<REFLECT_ITERATIONS; i++)
    {
        Result result = raymarch_query(ray);

        if (result.mat.diffuse==0.0)
        {
            vec3 spotlight = vec3(1e4) * pow(clamp(dot(ray.dir, -g_light.dir),0.0,1.0), 250.0);

            colour += mask * (ambient + spotlight); 
            break;
        }
        else
        {              
            vec3 r0 = result.mat.colour.rgb * result.mat.specular;
            float hv = clamp(dot(result.normal, -ray.dir), 0.0, 1.0);
            fresnel = r0 + (1.0 - r0) * pow(1.0 - hv, 5.0);
            mask *= fresnel;            

            colour += clamp(dot(result.normal, -g_light.dir), 0.0, 1.0) * g_light.colour
                * result.mat.colour.rgb * result.mat.diffuse
                * (1.0 - fresnel) * mask / fresnel;

            Ray reflectray;
            reflectray.pos = result.pos + result.normal*MARCH_EPSILON*2.0f;
            reflectray.dir = reflect(ray.dir, result.normal);
            ray = reflectray;
        }
    }

    colour.xyz = vec3(pow(colour * exposure, vec3(1.0 / gamma)));    
    return colour;    
}

///////////////////////////////////////////////////////////////////////////////////
// initialise all the primitives

void setupscene()
{   
    // scene definition    
    for (int i=0; i<NUM_SPHERES; i++)
    {
        vec3 origin;
        origin.x = 2.0 - perlin(HURRY_UP*iTime*0.212, float(i) + 1.0)*4.0;
        origin.y = 2.0 - perlin(HURRY_UP*iTime*0.341, float(i) + 2.0)*4.0;
        origin.z = 4.0 + perlin(HURRY_UP*iTime*0.193, float(i) + 3.0)*2.0;
        float radius = 1.0;

        g_spheres[i].pos = origin;
        g_spheres[i].radius = radius;
        g_spheres[i].mat = Material(vec3(1.0), 1.0, 0.1);        
    }    

    g_spheres[0].mat.colour = vec3(1.0, 0.0, 0.0);
    g_spheres[1].mat.colour = vec3(0.0, 1.0, 0.0);
    g_spheres[2].mat.colour = vec3(0.0, 0.0, 1.0);
    g_spheres[3].mat.colour = vec3(1.0, 0.0, 0.0);
    g_spheres[4].mat.colour = vec3(0.0, 1.0, 0.0);
    g_spheres[5].mat.colour = vec3(0.0, 0.0, 1.0);
}

///////////////////////////////////////////////////////////////////////////////////
// main loop, iterate over the pixels, doing MSAA

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{           
	setupscene();

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float factor = 1.0/(MSAA*MSAA);

    for (float x=0.0; x<MSAA; x++)
    {
        for (float y=0.0; y<MSAA; y++)
        {
            vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
            uv.y *= iResolution.y / iResolution.x;

            uv.x += (1.0/(iResolution.x*MSAA))*x;
            uv.y += (1.0/(iResolution.y*MSAA))*y;

            Ray ray;
            ray.pos = vec3(0.0, 0.0, 0.0);
            ray.dir = uv.xyx;
            ray.dir.z = 1.0;
            ray.dir = normalize(ray.dir);

            fragColor.xyz += raymarch(ray)*factor;
        }        
    }

    float amount = 0.5 + sin(iTime)*0.5;

    if ((floor(mod(fragCoord.y, 8.0)) != 0.0) && (floor(mod(fragCoord.x, 8.0)) != 0.0) && amount>0.1)
    {
        fragColor.xyz = rgb2hsv(fragColor.xyz);
        fragColor.y=0.0;
        fragColor.xyz = hsv2rgb(fragColor.xyz);
    }
    else
    {
        fragColor.xyz = rgb2hsv(fragColor.xyz);
        fragColor.y=1.0;
        fragColor.xyz = hsv2rgb(fragColor.xyz);        
    }
}

