#define THRESHOLD 0.000001
#define MAX_ITERATIONS 100
#define ANTI_ALIASING 2

vec2 f(vec2 z) {
    float magnitude = dot(z, z);
    return (2.0 * z + vec2(z.x * z.x - z.y * z.y, -2.0 * z.x * z.y) / (magnitude * magnitude)) / 3.0;
}

vec2 roots[] = vec2[](
    vec2(1.0, 0.0),
    vec2(-0.5, 0.5 * sqrt(3.0)),
    vec2(-0.5, -0.5 * sqrt(3.0))
);

vec3 palette[] = vec3[](
    vec3(1.0, 0.0, 0.0),
    vec3(0.0, 1.0, 0.0),
    vec3(0.0, 0.0, 1.0)
);

vec3 drawFractal(vec2 z) {
    for (int iterations = 0; iterations < MAX_ITERATIONS; ++iterations) {
    	z = f(z);

        for (int root = 0; root < roots.length(); ++root) {
            vec2 difference = z - roots[root];
            float distance = dot(difference, difference);
            if (distance < THRESHOLD) {
    			return palette[root] * (0.75 + 0.25 * cos(0.25 * (float(iterations) - log2(log(distance) / log(THRESHOLD)))));
            }
        }
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float zoom = exp(-5.0 * (0.9 - cos(iTime / 5.0)));
    vec2 center = vec2(0.14918, 0.09001);

    vec3 color = vec3(0);

    for (int x = 0; x < ANTI_ALIASING; ++x) {
        for (int y = 0; y < ANTI_ALIASING; ++y) {
            color += drawFractal(center + zoom * ((2.0 * (fragCoord + vec2(x, y) / float(ANTI_ALIASING)) - iResolution.xy) / iResolution.y - center));
        }
    }

    fragColor = vec4(color / float(ANTI_ALIASING * ANTI_ALIASING), 1.0);
}

float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord/2.0  - 0.1 * iResolution.xy) / iResolution.y+0.35;

    //background gradient colour
    float pct = 1.8-st.y*1.6;

    //the loop repeats 50 times, so there are 50 circles drawn in total
    for(int i=0; i<50;i++){
        //change i to float so I can multiply by it
        float i_float = float(i);
        //here the circle function is called. The location (position) of each circle is 
        //adjusted by the sin of the time value.
        //The 50 circles are in different positions because the incremented i_float 
        //value is used:

        vec2 position = st+(0.2*sin(3.-i_float*st.yx*((sin(iTime/3.)+1.))));
        pct += circle(position,0.001)*st.y*1.5;
    }

	pct=pct-1.;

    // Output to screen, adjust colour
    fragColor = vec4(pct+.1,pct+.1,pct,1.0)*vec4(0.9,0.,0.,0.)+vec4(0.,0.0,0.3,0.);
}

float rect(vec2 uv, float left, float right, float up, float down, float blur)
{
    float l1 = smoothstep(uv.x, uv.x-blur, left);
    float r1 = smoothstep(uv.x, uv.x+blur, right);
    float u1 = smoothstep(uv.y, uv.y+blur, up);
    float d1 = smoothstep(uv.y, uv.y-blur, down);
    float result = l1*r1*u1*d1;
    return result;
}
