    fragColor = vec4(vec3(col),1.0);
}

//
// SOME SDF FUNCTIONS
//

float spiralSDF(vec2 uv, float t)
{
    float r = dot(uv,uv);
    float a = atan(uv.y, uv.x);
    return abs(sin(fract(log(r)*t+a*0.159)));
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}


//
// RAYMARCH SCENE
//

float sceneSDF(vec3 pos)
{
    float a, b, c;
    float result = 0.0;
    vec3 aPos = pos;
    vec3 bPos = pos;
    vec3 cPos;

    a = sdSphere(aPos, 1.0);
    aPos.y -= 1.5;
    a = opSmoothUnion(a, sdSphere(aPos, 1.0), .5);

    bPos.y -= 1.5;
    bPos.x += .25;
    bPos.z += .75;
    b = sdSphere(bPos, .5);
    bPos.x -= .5;
    b = max(b, sdSphere(bPos, .5));
    bPos = pos + vec3(0.5, -1.75, 0.75);
    b = min(b, sdSphere(bPos, .25));
    bPos = pos + vec3(-0.5, -1.75, 0.75);
    b = min(b, sdSphere(bPos, .25));

    cPos = pos + vec3(0.0, -2.7, 0.0);
    c = sdSphere(cPos, .5);
    cPos = pos + vec3(0.0, -2.3, 0.7);
    c = opSmoothUnion(c, sdSphere(cPos, .4),.25);
    cPos = pos + vec3(0.0, -2.3, -0.7);
    c = opSmoothUnion(c, sdSphere(cPos, .4),.25);

    result = min(min(a, b), c);


    return result;
}

float castRay(vec3 rayOrigin, vec3 rayDir)
{
    float t = 0.0;

    for(int i=0; i<64 ; i++)
    {
        float res = sceneSDF(rayOrigin + rayDir * t);
        if(res < (0.0001*t))
        {
            return t;
        }
        t += res;
    }

    return -1.0;
}

vec3 render(vec3 rayOrigin, vec3 rayDir)
{
    float t = castRay(rayOrigin, rayDir);
    vec3 col;
    if(t == -1.0)
    {
        col = vec3(1., .1, .2) * -rayDir.y + .2;
    }
    else
    {
        float ao = pow(1.0-t*.1, 8.0) * 256.0;
        col = vec3(.1, 1., .2) * ao;
    }

    return col;
}

vec3 CameraViewDir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    vec3 viewDir;
    vec3 forward = normalize(camTarget - camPos);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = normalize(cross(forward, right));
    float fPersp = 2.0;

    viewDir = normalize(uv.x * right + uv.y * up + fPersp * forward);
    return viewDir;
}

vec2 NormalizeScreen(vec2 st)
{
    vec2 uv = (2.0 * st/iResolution.xy - 1.0);
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(.0);
    vec3 camPos = vec3(sin(iTime), .25 + sin(iTime)*.25, cos(iTime)) * 6.0;
    vec3 camTarget = vec3(0.0, 1., .0);

    vec2 uv = NormalizeScreen(fragCoord);
    vec3 viewDir = CameraViewDir(uv, camPos, camTarget);

    col.xy = uv;

    col = render(camPos, viewDir);

    fragColor = vec4(col, 1.);
}

#define MAX_MARCH 640
#define MAX_DST 100.
#define MIN_DST .01
#define AMBIENT_DST .05

#define OBJ_DELTA .004
#define OBJ_PLANE .5
#define OBJ_SPHERE .51
#define OBJ_INFIG .52

#define OBJECTS 9

mat4 obj[OBJECTS];

// globals
vec3 p;
int o;
float deltaDst;

vec3 getNor(int o, vec3 p) {
    if(obj[o][3][3] < OBJ_SPHERE - OBJ_DELTA) {
        return obj[o][2].xyz;
    }else if(obj[o][3][3] < OBJ_INFIG - OBJ_DELTA) {
    	return normalize(p - obj[o][0].xyz);
    }else{
        return normalize(p - obj[o][0].xyz);
    }
}

float getDistance(vec3 p) {
    float min = 255.;
    float res;
    for(int c = 0; c < OBJECTS; c++) {

        if(obj[c][3][3] < OBJ_SPHERE - OBJ_DELTA) {
            res = abs(p.y - obj[c][0][1]); // plane
        }else if(obj[c][3][3] < OBJ_INFIG - OBJ_DELTA) {
            res = length(p - obj[c][0].xyz) - obj[c][0].w; // sphere
        }else{
         	res = length(cos(p) * 1.6 + sin(obj[c][0].xyz) * 1.99) - obj[c][0].w; // infinite figure
        }

        if(min > res) {
            deltaDst = min - res;
            min = res;
            o = c;
        }

    }
    return min;
}

float rayMarch(vec3 start, vec3 dir) {
    float ret = 0.;
    int i;
    float dst;

    for(i = 0; i < MAX_MARCH; i++) {
        p = start + dir * ret;
        dst = getDistance(p);
        ret += dst;
        if(ret > MAX_DST || dst < MIN_DST) {
           break; 
        }
    }
    return ret;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    // light
    vec3 light = vec3(sin(iTime) * 10., 6., cos(iTime) * 10. + 10.);
    vec3 light2 = vec3(sin(iTime + 3.14) * 10., 6., cos(iTime + 3.14) * 10. + 10.);

    // camera
    vec3 cam = vec3(0., 1., 0.);
    vec3 dir = normalize(vec3(uv.x, uv.y, 1.));

    // objects
    obj[0][0] = vec4(0., -5.2, 0., 0.); // infinite plane at zero Y
    obj[0][1] = vec4(.5, .5, .5, 1.);
    obj[0][2] = vec4(0., 1., 0., 0.);
    obj[0][3][3] = OBJ_PLANE;

    obj[1][0] = vec4(0., 1., 6., 1.); // red sph
    obj[1][1] = vec4(1., 0., 0., 1.);
    obj[1][3][3] = OBJ_SPHERE;

    obj[2][0] = vec4(1.5, 0.8, 6., 0.8); // blue sph
    obj[2][1] = vec4(0., 0., 1., 1.);
    obj[2][3][3] = OBJ_SPHERE;

    obj[3][0] = vec4(-1.5, 0.8, 6., 0.8); // green sph
    obj[3][1] = vec4(0., 1., 0., 1.);
    obj[3][3][3] = OBJ_SPHERE;

    obj[4][0] = vec4(0., .4, 2., .4); // yellow sph
    obj[4][1] = vec4(1., 1., 0., 1.);
    obj[4][3][3] = OBJ_SPHERE;

    obj[5][0] = vec4(1.5, 0.6, 3., 0.6); // magenta sph
    obj[5][1] = vec4(1., 0., 1., 1.);
    obj[5][3][3] = OBJ_SPHERE;

    obj[6][0] = vec4(-1.5, 0.6, 3., 0.6); // cyan sph
    obj[6][1] = vec4(0., 1., 1., 1.);
    obj[6][3][3] = OBJ_SPHERE;

    obj[7][0] = vec4(0., 17., 0., 0.); // infinite plane at 7 Y
    obj[7][1] = vec4(.5, 1., .5, 1.);
    obj[7][2] = vec4(0., -1., 0., 0.);
    obj[7][3][3] = OBJ_PLANE;

    obj[8][0] = vec4(sin(iTime), 0.6, 3., 0.6); // infinite figure
    obj[8][1] = vec4(1., .5, 0., 1.);
    obj[8][3][3] = OBJ_INFIG;

    for(int i = 0; i < OBJECTS; i++) {
        //obj[i][0].x *= cos(iTime);
        //obj[i][0].z *= sin(iTime);
    }

    float dst = rayMarch(cam, dir);

    dir = normalize(light - p);
    vec3 nor = getNor(o, p);

    vec3 dir2 = normalize(light2 - p);

    float colMul = clamp(dot(dir, nor), 0.4, 0.5);
    float colMul2 = clamp(dot(dir2, nor), 0.4, 0.5);
    float ambient = 1.;

    if(deltaDst < AMBIENT_DST) {
        ambient = clamp(deltaDst / AMBIENT_DST, 0.8, 1.);
    }

    vec4 c = obj[o][1];
    vec3 collisionP = p;
    int collisionO = o;

    float shadow = rayMarch(collisionP + (nor * MIN_DST * 2.), dir);

    if(shadow < length(light - collisionP)) {
        colMul *= .9;
    }


    shadow = rayMarch(collisionP + (nor * MIN_DST * 2.), dir2);

    if(shadow < length(light2 - collisionP)) {
        colMul2 *= .9;
    }

    nor = normalize(reflect(collisionP - cam, nor));
    if(rayMarch(collisionP + (nor * MIN_DST * 2.), nor) < 10.) {
    	c = (obj[o][1] * .2) + obj[collisionO][1];
    }

    vec4 c2 = c;
    c2.rgb *= colMul2;
    c.rgb *= colMul;

    fragColor = (c + c2) * ambient;
}

