vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

void mainImage( out vec4 o, vec2 C)
{
    vec2 R = iResolution.xy,
    u=C/R*2.;
    u.x *= R.x / R.y;
    float t = iTime*.4;
    for (float i = 1.; i <= 6.; ++ i ){
        vec4 c = hash42(floor(u));
        u +=u+t+u.yx*(c.xy-.5);// skew + scale
        o = i<2.? c+.5 : c.w>.5? o: o * c / (i-1.);// selectively accept color
    }
    C=fract(C/3.);
    o = mix(step(.1,o),o,C.y)*C.y*2.;
}

