}

const float scale = 1.0;

vec4 ComputeWaveGradientRGB(float t, vec4 bias, vec4 scale, vec4 freq, vec4 phase)
{
	vec4 rgb = bias + scale * cos(6.28 * (freq * t + phase));
	return vec4(clamp(rgb.xyz,0.0,1.0), 1.0);
}


vec4 ComputeWaveGradientPattern(
	vec2 p,
	int hv,
	float bias, float scale, float freq, float phase)
{
	float ts = (float(hv) * p.x) + (1.0 - float(hv) * p.y);
	float tc = (float(hv) * p.y) + (1.0 - float(hv) * p.x);
	float svalue = bias + (scale * sin(6.28 * (freq * ts + phase)));
	float cvalue = bias + (scale * cos(6.28 * (freq * tc + phase)));
	float angle = atan(p.y, p.x);
	float angleBand = (0.5 * cos(10.0 * 6.28 * angle)) + 1.0;
	return vec4(svalue,cvalue,angle,angleBand);
}




