{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= float(iResolution.x) / float(iResolution.y);

    uv *= 1.0 / scale;

    float maxRes = max(float(iResolution.x), float(iResolution.y));

    float tm = iTime *0.31;



    vec4 bias = vec4(0.350,0.906,0.689,1.0);
    vec4 scale = vec4(0.772,0.114,0.263,1.0);
    vec4 freq = vec4(0.077,0.368,1.016,1.0);
    vec4 phase = vec4(3.859,3.252,5.857,1.0);

    vec4 pattern = ComputeWaveGradientPattern(uv + (iTime), 
                                              0,
                                              (bias.x *tm),
                                              (scale.x * tm),
                                              freq.x,
                                              phase.x);



    vec4 baseColor = ComputeWaveGradientRGB((pattern.x * tm),bias,scale, freq, phase);
	vec4 accent0 = ComputeWaveGradientRGB((pattern.y * tm),bias,scale, freq, phase);
    vec4 accent1 = ComputeWaveGradientRGB(fract(pattern.z*tm),bias,scale, freq, phase);
    vec4 accent2 = ComputeWaveGradientRGB(fract(pattern.w*tm),bias,scale, freq, phase);


    vec4 finalColor = (baseColor + accent0 + accent1 + accent2) / 4.0;
    fragColor = finalColor;
}

#define MAX_MARCH 640
#define MAX_DST 100.
#define MIN_DST .01
#define AMBIENT_DST .05

#define OBJ_PLANE .5
#define OBJ_SPHERE .51
#define OBJ_INFIG .52

#define OBJECTS 9

mat4 obj[OBJECTS];

// globals
vec3 p;
int o;
float deltaDst;

vec3 getNor(int o, vec3 p) {
    if(obj[o][3][3] < OBJ_SPHERE) {
        return obj[o][2].xyz;
    }else if(obj[o][3][3] < OBJ_INFIG) {
    	return normalize(p - obj[o][0].xyz);
    }else{
        return normalize(p - obj[o][0].xyz);
    }
}

float getDistance(vec3 p) {
    float min = 255.;
    float res;
    for(int c = 0; c < OBJECTS; c++) {

        if(obj[c][3][3] < OBJ_SPHERE) {
            res = abs(p.y - obj[c][0][1]); // plane
        }else if(obj[o][3][3] < OBJ_INFIG) {
            res = length(p - obj[c][0].xyz) - obj[c][0].w; // sphere
        }else{
         	res = length(sin(p) - obj[c][0].xyz) - obj[c][0].w; // infinite figere
        }

        if(min > res) {
            deltaDst = min - res;
            min = res;
            o = c;
        }

    }
    return min;
}

float rayMarch(vec3 start, vec3 dir) {
    float ret = 0.;
    int i;
    float dst;

    for(i = 0; i < MAX_MARCH; i++) {
        p = start + dir * ret;
        dst = getDistance(p);
        ret += dst;
        if(ret > MAX_DST || dst < MIN_DST) {
           break; 
        }
    }
    return ret;
}

