    }

    float dst = rayMarch(cam, dir);

    dir = normalize(light - p);
    vec3 nor = getNor(o, p);

    vec3 dir2 = normalize(light2 - p);

    float colMul = clamp(dot(dir, nor), 0.3, 0.5);
    float colMul2 = clamp(dot(dir2, nor), 0.3, 0.5);
    float ambient = 1.;

    if(deltaDst < AMBIENT_DST) {
        ambient = clamp(deltaDst / AMBIENT_DST, 0.8, 1.);
    }

    vec4 c = obj[o][1];
    vec3 collisionP = p;
    int collisionO = o;

    float shadow = rayMarch(collisionP + (nor * MIN_DST * 2.), dir);

    if(shadow < length(light - collisionP)) {
        colMul *= .9;
    }


    shadow = rayMarch(collisionP + (nor * MIN_DST * 2.), dir2);

    if(shadow < length(light2 - collisionP)) {
        colMul2 *= .9;
    }

    nor = normalize(reflect(collisionP - cam, nor));
    if(rayMarch(collisionP + (nor * MIN_DST * 2.), nor) < 10.) {
    	c = (obj[o][1] * .2) + obj[collisionO][1];
    }

    vec4 c2 = c;
    c2.rgb *= colMul2;
    c.rgb *= colMul;

    fragColor = (c + c2) * ambient;
}

#define PI 3.14
#define SETP_MAX 32

#define NEAR 0.02
#define FAR 50.0

#define OFFSET_TO_NORMAL 0.01



vec3 Background (vec2 uv) {
    uv.x -= iTime * 0.5 + sin(iTime * 0.2);
    uv.y -= iTime * 0.1 + sin(iTime * 0.7);

	float offsetR = sin(iTime * 2.0 + PI * 0.0);
    float offsetG = sin(iTime * 2.0 + PI * 0.33333333) ;
    float offsetB = sin(iTime * 2.0 + PI * 0.66666666) ;

    vec3 col = vec3(abs(sin(uv.x * 50.0)) * offsetR,
                    abs(sin(uv.x * 50.0 + PI * 0.33333333)) * offsetG,
                    abs(sin(uv.x * 50.0 + PI * 0.66666666)) * offsetB) * 0.5 ;

    col += vec3(abs(sin(uv.y * 50.0)) ,
                abs(sin(uv.y * 50.0 + PI * 0.33333333)),
                abs(sin(uv.y * 50.0 + PI * 0.66666666))) * 0.5;

    return col;
}



float DstSphere (vec3 currentStep, vec3 origin, float radius) {
	return length(origin - currentStep) - radius;
}




float World (vec3 currentStep) {

    float world = 0.0;




    float sphere0 = DstSphere (currentStep , vec3(0.6 + 1.2 * sin(iTime * 0.5), 0.0, 3.0), 0.8);
    float sphere1 = DstSphere (currentStep , vec3(-0.6, 0.0, 3.0), 0.8);






    world = min(sphere0, sphere1);

    return world;
}



vec3 raymarching (vec3 dir) {
	float dist = 0.0;
    int currentStepCount = 0;

    while(true) {
        float world = World(dir * dist);
        dist += world;


        if(world < NEAR || currentStepCount > SETP_MAX)
            break;

        if(dist >  FAR)
            return vec3(0.0, 0.0, 0.0);

        currentStepCount++;
    }

    return dir * dist;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;

    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x = uv.x * iResolution.x / iResolution.y;


    vec3 cameraPos = vec3(0.0, 0.0, -2.5 + 0.2 * sin(iTime));
    vec3 cameraDir = vec3(0.0, 0.0, 1.0);
    vec3 screen = vec3(uv, 0.0);
    vec3 dir = normalize(screen - cameraPos);


    vec3 world0 = raymarching(dir);



    dir = normalize(screen + vec3(OFFSET_TO_NORMAL, OFFSET_TO_NORMAL, 0.0) - cameraPos);
    vec3 world1 = raymarching(dir);

    dir = normalize(screen + vec3(-OFFSET_TO_NORMAL, OFFSET_TO_NORMAL, 0.0) - cameraPos);
    vec3 world2 = raymarching(dir);

    dir = normalize(screen + vec3(OFFSET_TO_NORMAL, -OFFSET_TO_NORMAL, 0.0) - cameraPos);
    vec3 world3 = raymarching(dir);

    dir = normalize(screen + vec3(-OFFSET_TO_NORMAL, -OFFSET_TO_NORMAL, 0.0) - cameraPos);
    vec3 world4 = raymarching(dir);





    /*
	(world1 - world0) + (world2 - world0) + (world3 - world0) + (world4 - world0)
	*/

    //vec3 worldNormal = (world1 + world2 + world3 + world4) * 0.25 ;
    //worldNormal = normalize(worldNormal);

    vec3 normal1 = normalize(world1 - world0);
    vec3 normal2 = normalize(world2 - world0);
    vec3 normal3 = normalize(world3 - world0);
    vec3 normal4 = normalize(world4 - world0);
	vec3 normal0 = normalize(normal1 + normal2 + normal3 + normal4);


    vec3 light0_dir = normalize(vec3(-1.0, -1.0, 0.8));
    vec3 light0_color = vec3(0.0, 2.0, 1.0);

    col = light0_color * dot(normal0, light0_dir);
    col = clamp(col, 0.0, 1.0);
    col = length(col) > 0.0 ? col : Background(uv);

    fragColor = vec4(col,1.0);
}

