// Intersection of a ray with a rounded cube, testing a single
// corner (sphere) instead of 8, and only 3 edges (cylinders)
// instead of 12. There might be a better (more compact and
// efficient) way to do it, but this is where I landed. However
// the code to compute the surface normal is particularly
// elegant.

// Other intersectors: http://iquilezles.org/www/articles/intersectors/intersectors.htm
//
// Box:             https://www.shadertoy.com/view/ld23DV
// Triangle:        https://www.shadertoy.com/view/MlGcDz
// Capsule:         https://www.shadertoy.com/view/Xt3SzX
// Ellipsoid:       https://www.shadertoy.com/view/MlsSzn
// Sphere:          https://www.shadertoy.com/view/4d2XWV
// Capped Cylinder: https://www.shadertoy.com/view/4lcSRn
// Disk:            https://www.shadertoy.com/view/lsfGDB
// Capped Cone:     https://www.shadertoy.com/view/llcfRf
// Rounded Box:     https://www.shadertoy.com/view/WlSXRW
// Rounded Cone:    https://www.shadertoy.com/view/MlKfzm
// Torus:           https://www.shadertoy.com/view/4sBGDy
// Sphere4:         https://www.shadertoy.com/view/3tj3DW
// Goursat:         https://www.shadertoy.com/view/3lj3DW

#define AA 2  // reduce this to 1 if you have a slow machine

#define BoundOr(a,b,c) (abs(b-.5*a)*2. c a)
#define BoundAnd(a,b,c) (a c abs(b-.5*a)*2. ) 
//(0.<a&&a<b) === boundAnd(a,b,<) === boundOr (a,b,>=) 
//(0.>a||a>b) === boundOr (a,b,>) === boundAnd(a,b,<=) 
//(0.<a&&a<b) === (0.<a&&a-b<0.) === (0.<a&&0<b-a) === (0.<max(a,b-a)) ???
//(0.<a||a<b) === (0.<a||a-b<0.) === (0.<a||0<b-a) === (0.<min(a,b-a)) ???

//original code by iq, vectorized by ollj, parent code is in: https://www.shadertoy.com/view/WlSXRW
float roundedboxIntersect( in vec3 ro, in vec3 rd, in vec3 size, in float rad){
	// bounding box
    vec3 m=1./rd;//assert nonzero RayDirection, seems fair.
    vec3 n=m*ro;
    vec3 d=abs(m)*(size+rad);
    vec3 p=-d-n;d=d-n;
	float t =max(max(p.x,p.y),p.z);
	float tF=min(min(d.x,d.y),d.z);
    if (0.<min(tF,t-tF)) return -1.; 	//if( tN > tF || tF < 0.0) //ollj edit
    //end of bounding box
    // convert to first octant
    //7 intersection cases (3planes+3roundedSides+1RoundedCorner) planes first:
    p=ro+t*rd;//p and d change meaning (short scope vars)
    d=sign(p);
    ro*=d;
    rd*=d;    
    p *=d;p-=size;p=max(p,p.yzx);if(min(min(p.x,p.y),p.z)<0.)return t;//type bvec3 not needed, replaced if(any()) with min() by ollj
    //planar intersections done, roundedCorners are trickier, 3 sides +1 corner.
    p=ro-size;//p and d change meaning (short scope vars)
    d=p*rd;
	p*=p;//some wars renamed or reapropriated (p&&d have had a short scope, now means something else)
    t =1e20; 
    // some precomputation
    //of the vec4 types below,
    //.xyz tests 3 rounded sides intersections
    //.w   tests rounded corner intersection
    vec4 a=vec4(rd*rd,1);
    a.xyz=a.yzx+a.zxy;
    vec4 b=d.yzxx+d.zxyy+vec4(0,0,0,d.z);
    vec4 c=p.yzxx+p.zxyy+vec4(0,0,0,p.z)-rad*rad;
    vec4 h=b*b-a*c;//vectorization requires more precalculation (all cases are precalculated in vectors at once, instead of just one at a time, this needs more minimum memory)
    h.xyz=(-b.xyz-sqrt(h.xyz))/a.xyz;
    a.xyz=size.xyz-abs(ro+rd*h.xyz); //note that [a] is negated to simplify conditionals below.
	if(h.w>0.0)t=-b.w-sqrt(h.w);//rounded corner(s) must be done before 3 sides 
    //because of culling order, sides go over inside-intersections of spheres in corners.
    a=min(a,h);  //this line simplifies conditionals a lot
    if(a.x>0.)t=min(t,h.x);    //   t=mix(min(t,h.x),t,step(a.x,0.));
    if(a.y>0.)t=min(t,h.y);
    if(a.z>0.)t=min(t,h.z);    //these 3 if() lines CAN BE substituted by the 2 lines below.
 //vec3 r=mix(min(vec3(t),h.xyz),vec3(t),step(a.xyz,vec3(0)));//this line can likely be simplified
    //a branchlessing mix(step()) is indeed likely slower
    //,except it maybe faster where you can handle multiple domains at once, instead of a list of if():
//t=min(min(t,r.x),min(r.y,r.z));    
    //this should end in a min(min(),min()) and not be a list of 4 conditions
    //i leave the benchmarking of this to others    
    if( t>1e19 )t=-1.;//>zFar error case.  
	return t;} //end of vectorization by ollj

// normal of a rounded box
vec3 roundedboxNormal( in vec3 pos, in vec3 siz, in float rad )
{
    return sign(pos)*normalize(max(abs(pos)-siz,0.0));
}


