mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
float PI = 3.14159265;
vec3 path(float t){
	float x = clamp(sin(t) * pow(2.0,0.5),-1.0,1.0);
    float y = clamp(cos(t) * pow(2.0,0.5),-1.0,1.0);
    float z = 0.2;
    return vec3(x,y,z);
}
float grid(vec3 p, float a){
    //p.xz *= genRot(iTime);
    //p = fract(p / a) * a;
    p = fract(p ) ;


    p -= vec3(a / 1.5);



    float d1 = max(abs(p.x),abs(p.y)) - a/(15.0);
	float d2 = max(abs(p.y),abs(p.z)) - a/(15.0);
	float d3 = max(abs(p.z),abs(p.x)) - a/(15.0);

	return (min(min(d1,d2),d3));
}
float map(vec3 p){
	float k = grid(p,1.0);

    for(float i = 2.0; i < 6.0; i++  ){
        k = max(k,-grid(p,1.0/i));
    }
    return k;
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.3,0.12,0.10);

    for(int i = 0; i < 128; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.7;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float stime;
    stime=iTime*0.1;

    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,1.5));

    r.xz *= genRot(stime);


    vec3 o = vec3(  1.2,  0.5 + stime * 1.0 , cos(stime * PI / 4.) * 1.2);

    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.05);
    vec3 fc = mix(vec3(0.3 - data.x,0.6 -data.y,0.5-data.z),vec3(0.),1.0 - fog);
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

