mat2 rotate2d(float _angle){
    _angle *= 3.1415/180.;
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec4 box(vec2 uvNew, vec2 scale, float orientation, vec2 position)
{
    uvNew += vec2(-0.5-position.x,(-0.5-position.y)*iResolution.y/iResolution.x);
    uvNew *= rotate2d(orientation);
    float a = 1.-step(scale.x*0.2,abs(uvNew.x));
    float b = 1.-step(scale.y*0.2,abs(uvNew.y));
    vec4 col= vec4(vec3(a*b),1.0);

	return col;
}

vec4 crossA(vec2 uvNew, float orientation, vec2 position, vec2 scale)
{
	vec4 crossAComp = box(uvNew, scale, 0.+orientation, position) + box(uvNew, scale, 90.+orientation, position);
    return clamp(crossAComp,0.,1.);
}

vec4 circle(vec2 uvNew, float radius, vec2 position)
{
    uvNew += vec2(-0.5,(-0.5)*iResolution.y/iResolution.x);
    float c = pow(uvNew.x-position.x,2.)+pow(uvNew.y-position.y,2.);
    c = 1. - step(0.01*radius,c);
	return vec4(c);
}

vec2 grid(vec2 uvNew, float density)
