#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define hash(h) fract(sin(h) * 43758.5453123)
#define PI 3.141592653589793

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

vec4 combine(vec4 val1, vec4 val2 ){
    if ( val1.w < val2.w ) {
        return val1;
    }
    return val2;
}

// This formula is from:https://en.wikipedia.org/wiki/Triangle_wave
float triangleWave(vec3 p) {
    float val = 0.0;
    float amp = 1.2;
    float period =12.0;
    val = (2.0 * amp /PI) * acos(sin(2.0 * (PI / period) *p.x));
    val += (2.0 * amp /PI) * asin(cos(2.0 * (PI / period) *p.z));
    return val;
}

vec4 map(vec3 p){    
    vec3 pref = p;
    vec2 uv = p.xy;
    p.z += iTime*20.0;
	p.y+= triangleWave(p);

    vec4 f = vec4(vec3(1.2,1.0,0.3),p.y+5.0);
    p = pref;

    float a1 = sdCappedCylinder((p+vec3(0.0,-0.9,0.0))*matRotateY(radians(90.0))*matRotateZ(radians(90.0)), 0.8,0.2);
    float a2 = sdCappedCylinder((p+vec3(0.0,-0.9,0.0))*matRotateY(radians(90.0))*matRotateZ(radians(90.0)), 0.4,0.3);
    float a3 = sdBox((p+vec3(-0.65,-1.0,0.0)),vec3(0.2,0.7,0.2));
    float ad = min(max(-a2,a1),a3);

    float c1 = sdCappedCylinder((p+vec3(0.9,0.7,0.0))*matRotateY(radians(90.0))*matRotateZ(radians(90.0)), 0.8,0.2);
    float c2 = sdCappedCylinder((p+vec3(0.9,0.7,0.0))*matRotateY(radians(90.0))*matRotateZ(radians(90.0)), 0.4,0.3);
    float c3 = sdBox((p+vec3(0.2,0.7,0.0)),vec3(0.9,0.2,0.3));
    float cd = max(-c3,max(-c2,c1));

	float g1 = sdCappedCylinder((p+vec3(-0.9,0.7,0.0))*matRotateY(radians(90.0))*matRotateZ(radians(90.0)), 0.8,0.2);
    float g2 = sdCappedCylinder((p+vec3(-0.9,0.7,0.0))*matRotateY(radians(90.0))*matRotateZ(radians(90.0)), 0.4,0.3);
    float g3 = sdBox((p+vec3(-1.6, 0.25,0.0))*matRotateZ(radians(-45.0)),vec3(0.7,0.25,0.3));
    float g4 = sdBox((p+vec3(-1.6, 0.7,0.0)),vec3(0.7,0.2,0.3));
    float g5 = sdBox((p+vec3(-1.37, 0.8,0.0)),vec3(0.3,0.15,0.3));
    float gd = min(max(-g4,max(-g3,max(-g2,g1))),g5);

    float chard = min(min(ad,cd),gd);

    float bg0 = sdTriPrism(p,vec2(3.2,0.1));
    vec4 bg = vec4(vec3(1.2,0.5,0.6),max(-chard,bg0));

    return combine(bg,f);
}

vec3 normalMap(vec3 p){
    float d = 0.0001;
    return normalize(vec3(
        map(p + vec3(  d, 0.0, 0.0)).w - map(p + vec3( -d, 0.0, 0.0)).w,
        map(p + vec3(0.0,   d, 0.0)).w - map(p + vec3(0.0,  -d, 0.0)).w,
        map(p + vec3(0.0, 0.0,   d)).w - map(p + vec3(0.0, 0.0,  -d)).w
    ));
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = map(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

// from simon green and others
float ambientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - map(p + n*d).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 uv = p;

    float time = iTime*2.0;

    vec3 ro = vec3( 3.5*cos(0.1*time + 6.0), 0.0, -0.5+5.5*sin(0.1*time + 6.0) );
    vec3 ta = vec3( 0.0, -0.4, -0.7 );
    mat3 ca = setCamera( ro, ta, 0.0 );
    float zoom = 1.5;
    vec3 rd = ca * normalize( vec3(p.xy,zoom) );

    float t, dist;
    float lastDistEval = 1e10;
    float edge = 0.0;
    t = 0.0;
    vec3 distPos = ro+rd;
    vec4 distCl = vec4(0.0);
    for(int i = 0; i < 64; i++){
        distCl = map(distPos);
        dist = distCl.w;
        t += dist;
        distPos = ro+rd*t;

        if (dist < lastDistEval) lastDistEval = dist;
        if(dist < 0.01 || dist > 60.0) break;
    }

    vec3 color;
    float shadow = 1.0;
    if(dist < 1.0){
        // lighting
        vec3 lightDir = vec3(0.0, 1.0, 0.0);
        vec3 light = normalize(lightDir + vec3(0.5, 0.0, 0.9));
        vec3 normal = normalMap(distPos);

        // difuse color
        float diffuse = clamp(dot(light, normal), 0.5, 1.0);
        float lambert = max(.0, dot( normal, light));

        // ambient occlusion
        float ao = ambientOcclusion(distPos,normal);

        // shadow
        shadow = shadowMap(distPos + normal * 0.001, light);

        // result
        color += vec3(lambert);
        color = ao*diffuse*(distCl.xyz+(.1-length(p.xy)/3.))*vec3(1.0, 1.0, 1.0);

    }else{
        color =.84*max(mix(vec3(0.6,0.6,0.6)+(.1-length(p.xy)/3.),vec3(1),.1),0.);
    }

    // rendering result
    float brightness = 1.5;
    vec3 dst = (color * max(0.8, shadow))*brightness;

    fragColor = vec4(dst, 1.0);
}

vec4 ComputeRegularBrickTilePattern(vec2 pos, vec2 box, float gap, float brickLum, float gapLum)
{
	float width = box.x;
	float height = box.y;

	float brickAndMortarWidth = width + gap;
	float brickAndMortarHeight = height + gap;
	float mwf = (gap * 0.5 / brickAndMortarWidth);
	float mhf = (gap * 0.5 / brickAndMortarHeight);


	float row = pos.y / height;
	float x = 0.0;
	float y = fract(row);
	if (fract(row * 0.5) > 0.5)
	{
		x = fract((pos.x + width / 2.0) / width);
	}
	else
	{
		x = fract(pos.x / width);
	}

	float horizontal = step(mwf, x) - step(1.0 - mwf, x);
	float vertical = step(mhf, y) - step(1.0 - mhf, y);
	float lum = mix(gapLum, brickLum, horizontal * vertical);
	vec4 retval = vec4(lum, x, y, floor(row));
	return retval;
}


