// Simple Mandelbulb - by @SyntopiaDK
// (The camera code and lightning was taken from an example by @mrdoob)

float PI=3.14159265;
#define Power 8.0
#define Bailout 4.0

void powN1(inout vec3 z, float r, inout float dr) {
	// extract polar coordinates
	float theta = acos(z.z/r);
	float phi = atan(z.y,z.x);
	float power = Power*cos(iTime*0.001+length(iMouse)*0.009);
	dr =  pow( r, power-1.0)*power*dr + 1.0 + pow((1.-cos(iTime*.3)*cos(iTime*.2)), -2.5);

	// scale and rotate the point
	float zr = pow( r,power);
	theta = theta*power;
	phi = phi*power;

	// convert back to cartesian coordinates
	z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
}


// Compute the distance from `pos` to the Mandelbox.
float DE(vec3 pos) {
	vec3 z=pos;
	float r;
	float dr=1.0;
	r=length(z);
	for(int i=0; (i < 5); i++) {
		powN1(z,r,dr);
		z+=pos;
		r=length(z);
		if (r>Bailout) break;
	}

	return 0.5*log(r)*r/dr;
}

vec3 DEColor(vec3 pos) {
	vec3 z=pos;
	float r;
	float dr=1.0;
	r=length(z);
	float minR = 1000.0;
	for(int i=0; (i < 2); i++) {
		powN1(z,r,dr);
		z+=pos;
		r=length(z);
		minR = min(r,minR);
		if (r>Bailout) break;
	}
	float i = minR*minR*minR*minR*0.70;
	return vec3(clamp(i,0.0,1.0));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
//void main(void)
{
  vec2 vPos=-1.0+2.0*fragCoord.xy/iResolution.xy;

