  vec3 vrp=vec3(0,0,0); //Change camere view here
  float mx=iMouse.x*PI*0.009;
  float my=iMouse.y*PI*0.009;
  vec3 prp=vec3(cos(my)*cos(mx),sin(my),cos(my)*sin(mx))*2.0; //Trackball style camera pos


  //Camera setup
  vec3 vpn=normalize(vrp-prp);
  vec3 u=normalize(cross(vuv,vpn));
  vec3 v=cross(vpn,u);
  vec3 vcv=(prp+vpn);
  vec3 scrCoord=vcv+vPos.x*u*iResolution.x/iResolution.y+vPos.y*v;
  vec3 scp=normalize(scrCoord-prp);

  //Raymarching
  const vec3 e=vec3(0.001,0,0);
  const float maxd=5.0; //Max depth
  float s=0.0;
  vec3 c,p,n;

  float f=0.010;
  for(int i=0;i<512;i++){
    f+=s;
    p=prp+scp*f;
    s=DE(p);
     if (abs(s)<.000525||f>maxd) break;

  }

  if (f<maxd){
    n=normalize(
      vec3(s-DE(p-e.xyy),
           s-DE(p-e.yxy),
           s-DE(p-e.yyx)));
    c = DEColor(p);
    c.yz = mix(c.yz, n.yz, 0.3);
    float b=dot(n,normalize(prp-p));  
    fragColor = mix(vec4((b*c+pow(b,16.0))*(1.0-f*.01),1.0), vec4(c,1.0),0.58);
  }
  else fragColor=vec4(.9,.9,.9,1); //background color
}

