mat2 rotate2D(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float sdWater(vec3 p)
{
    p.z += 10.;
    p.y += 5.;
    float u = length(p.xz);
    float c = sin(.5 * u - iTime);
   	p.y += c * 0.5 * u * exp(1. - sqrt(u)*0.9);
    float d = abs(p.y)-0.5;
    return d;
}


vec2 min2(vec2 a, vec2 b)
{
    return a.x < b.x ? a : b;
}

float rnd(float t)
{
    return fract(sin(t*794.535)*567.562);
}

float curve(float t, float d)
{
    float g = t/d;
    return mix(rnd(floor(g)), rnd(floor(g)+1.), pow(smoothstep(0., 1., fract(g)), 10.));
}

vec2 sdCool(vec3 p)
{
    p.y -= 7.;
    p.xz = rotate2D(iTime + curve(iTime, 0.4) * 1.5) * p.xz;
    p.xy = rotate2D(iTime * 0.4 + curve(iTime, 0.6) * 1.5) * p.xy;
    vec2 res = vec2(sdOctahedron(p, 5.), OCTA_I);
    return res;
}  

vec2 sdScene(vec3 p)
{
    float t = mod(iTime, 0.8);
    float fac = 1.0 + 5.0 * t * exp(1.0 - 10.0 * t);
    float k = 4.0 * sin(2.0*iTime);

    vec2 res = vec2(sdWater(p), WATER_I);

    res = min2(res, sdCool(p));

    return res;
}

vec3 gradient(vec3 p)
{
    vec3 dx = vec3(GRAD_STEP, 0.0, 0.0);
    vec3 dy = vec3(0.0, GRAD_STEP, 0.0);
    vec3 dz = vec3(0.0, 0.0, GRAD_STEP);

    return vec3(sdScene(p + dx).x - sdScene(p - dx).x,
                sdScene(p + dy).x - sdScene(p - dy).x,
                sdScene(p + dz).x - sdScene(p - dz).x);
}

vec3 normal(vec3 p)
{
    return normalize(gradient(p));
}

float haloScale(float h)
{
	return 0.2 * pow(h, .4);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
