    vec3 bg = vec3(.5, .6, .7);
    vec3 waterCol = vec3(.1, 0., .1);

    float t = 0.0;
    float halo = 0.;

    vec3 c = vec3(1.);

    for (int i = 0; i < NUM_STEPS && t <= MAX_DIST; i++)
    {
        vec3 p = ro + t * rd;
        vec2 res = sdScene(p);
        float dst = res.x;

        halo += 0.2 / (0.2 + sdCool(p).x);

        if (dst < EPSILON + rnd(sin(uv.x + iTime)*sin(uv.y + iTime))* 0.001) {
            break;
        }
    	t += dst;
    }

    if (halo == 0.) {
        fragColor = vec4(0., 0., 1., 1.);
        return;
    }
    // Output to screen
    float noise = rnd(sin(uv.x + iTime) * sin(uv.y + iTime));
    halo = haloScale(halo);
    fragColor = vec4(c * (halo + noise * 0.02), 1.);
    //fragColor = vec4(bg,1.0);
}

