// Symplectic Integrator, motivated by this tweet by MMalex
// and Fabian Giesen's explanation:
// https://twitter.com/mmalex/status/1165757266601099276
//
// The shader compares different ways to integrate sin/cos in
// order to compute cos/sin without trigonometrics.
//
// Choose a METHOD for comparison
//
// 0 Red:    naive Euler way. Explodes quickly
// 1 Yellow: normalizing the naive way. Stable, but drifts
// 2 Blue:   symplectic integrator, pretty close to perfect
// 3 Green:  rotation based, perfect
//   Grey:   ground truth

#define METHOD 2

//---------------------------------------------



// Ground truth cs for reference.
vec2 waveExact( int x, const float w )
{
    return vec2(cos(w*float(x)),sin(w*float(x)));
}


// The naive method is to integrate each component
// sequentially. It's equivalent to rotating the vector
// cs by M = {1, -w, w, 1}, which is a poor approximation
// to the rotation and more importantly has determinant
// |M| = 1+w > 1, which means it's going to explore rapidly.
vec2 waveEulerNaive( int x, const float w )
{
    vec2 cs = vec2(1.0,0.0);
    for( int i=0; i<x; i++ )
    {
        cs += w*vec2(-cs.y,cs.x);
    }
    return cs;
}


// The naive fix to the naive method is to renormalize
// the vector cs after each tansformation. This doesn't
// work neither in the long run, since it doesn't fix the
// drifting. See yellow graph.
vec2 waveEulerNormalized( int x, const float w )
{
    vec2 cs = vec2(1.0,0.0);
    for( int i=0; i<x; i++ )
    {
        cs += w*vec2(-cs.y,cs.x);
        cs = normalize( cs );
    }
    return cs;
}

// Symplectic integration - instead of updating the value
// and its derivative at the same time (like in a normal
// Euler integration), it updates the derivative and then
// the value based on the updated derivative (Verlet style).
// See https://en.wikipedia.org/wiki/Symplectic_integrator,
// and make p=cos, q=sin, H=1, T(x)=x^2, V(x)=x^2, and use the
// first order integrator.
//
// It is equivalent to transforming the vector cs each time
// with M = {1, -w, w, 1-w}, which has determinant |M|=1
// (won't explode). Sometimes it will produce a vector a bit
// longer than the true cs wave, and sometimes shoter, but
// it oscillates regularly around the true length.
vec2 waveSymplectic( int x, const float w )
{
    vec2 cs = vec2(1.0,0.0);
    for( int i=0; i<x; i++ )
    {
        cs.x-=w*cs.y;
        cs.y+=w*cs.x; 
    }
    return cs;

    // measure deviation from unity length
    // return vec2( (4.0/w)*(length(cs)-1.0), 0.0 );

    // measure deviation from true cs coordinates
    // return vec2( (4.0/w)*length(cs-waveExact(x,w)), 0.0 );
}

// The most intuitive method is probably to rotate
// cs by the right amount each time.
vec2 waveRotations( int x, const float w )
{
    mat2 M = mat2(cos(w),-sin(w),sin(w),cos(w));
    vec2 cs = vec2(1.0,0.0);
    for( int i=0; i<x; i++ )
    {
        cs = M*cs;
    }
    return cs;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.y;
    float px = 1.0/iResolution.y;

    const float pi = 3.1415926536;
    const float w = 10.0*(2.0*pi)/800.0;
    const float a = 0.1;

    int x = int(fragCoord.x + min(pow(mod(iTime,15.0)/15.0,2.0)*4000.0,3000.0));


    vec3 col = vec3(0.15);

    // axis
    {
    float d = abs(p.y-0.5);
    col = mix( col, vec3(0.5), 1.0-smoothstep(0.5*px,1.5*px,d) );
    }

    // 2PI periods
    {
    float d = abs(mod(float(x)*w+pi,2.0*pi)-pi);
    col = mix( col, vec3(0.5), 1.0-smoothstep(0.5*px,1.5*px,d) );
    }

    // naive: red
    #if METHOD==0
    {
	vec2 cs = a*waveEulerNaive( x, w );
    float d = abs( 0.5+cs.x - p.y ) / sqrt(1.0+cs.y*cs.y);
    col = mix( col, vec3(1.0,0.0,0.0), 1.0-smoothstep(1.0*px,4.0*px,d) );
    }
    #endif

	// naive normalized: yellow
    #if METHOD==1
    {
	vec2 cs = a*waveEulerNormalized( x, w );
    float d = abs( 0.5+cs.x - p.y ) / sqrt(1.0+cs.y*cs.y);
    col = mix( col, vec3(1.0,0.7,0.0), 1.0-smoothstep(1.0*px,4.0*px,d) );
    }
    #endif

    // symplectic: blue
    #if METHOD==2
    {
	vec2 cs = a*waveSymplectic( x, w );
    float d = abs( 0.5+cs.x - p.y ) / sqrt(1.0+cs.y*cs.y);
    col = mix( col, vec3(0.1,0.4,1.0), 1.0-smoothstep(1.0*px,4.0*px,d) );
    }
    #endif


    // rotations: green
    #if METHOD==3
    {
	vec2 cs = a*waveRotations( x, w );
    float d = abs( 0.5+cs.x - p.y ) / sqrt(1.0+cs.y*cs.y);
    col = mix( col, vec3(0.1,1.0,0.0), 1.0-smoothstep(1.0*px,4.0*px,d) );
    }
    #endif

    // exact : white
    {
	vec2 cs = a*waveExact( x, w );
    float d = abs( 0.5+cs.x - p.y ) / sqrt(1.0+cs.y*cs.y);
    float e = smoothstep(-0.1,0.1,sin(iTime*3.1415927));
    col = mix( col, vec3(1.0,1.0,1.0), e*(1.0-smoothstep(1.0*px,4.0*px,d)) );
    }

    fragColor = vec4(col,1.0);
}

