const float maxIterations = 150.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = .9995 + sin(iTime * .2);

    uv *= 2. - t;
    uv += vec2(-1.5 + t * 0.9, -1. + t * 0.789);

    float realComponentOfResult = uv.x;
    float imaginaryComponentOfResult = uv.y;
	float tempRealComponent = 0.;
	float tempImaginaryComponent = 0.;

    fragColor =  texture(iChannel0, uv * 3.);

    for(float i = 0.; i < maxIterations; i++) {

         tempRealComponent = realComponentOfResult * realComponentOfResult
                                 - imaginaryComponentOfResult * imaginaryComponentOfResult
                                 + uv.x;
         tempImaginaryComponent = 2. * realComponentOfResult * imaginaryComponentOfResult
                                 + uv.y;
         realComponentOfResult = tempRealComponent;
         imaginaryComponentOfResult = tempImaginaryComponent;

        if(realComponentOfResult * imaginaryComponentOfResult > 2.) {
            float a = sqrt(i/maxIterations);
            fragColor = vec4(a, 0., a, 1.0);
            break;
        }
    }


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
