    float thickness = 0.01;
    float radius = 0.1;
    float gradientThickness = 0.02;

    float dist = distance(uv, mUv);
    float col1 = smoothstep(radius, radius + gradientThickness, dist);
    float col2 = smoothstep(radius + gradientThickness + thickness, radius + (2.0f * gradientThickness) + thickness, dist);
    float col = (col1 - col2) * colorFactor;

    // Output to screen
    fragColor = vec4(col, 0.0, 0.0, 1.0);
}

#define BLACK_COL vec3(16,22,26)/255.
