float PI = 3.14159265;
mat2 genRot(float v){
	return mat2(cos(v),-sin(v),sin(v),cos(v));
}
float gear(vec3 p, float inner_1,float inner_2,
          float outer_1,float outer_2,
           float width,float gearCount){
	float at = atan(p.z/p.x) / (2. * PI);
    float inner = fract(at * 2. * gearCount) > 0.5 ? inner_1 : inner_2;
    float outer = fract(at * 2. * gearCount) > 0.5 ? outer_1 : outer_2;
    return max(max(length(p.xz) - outer,-length(p.xz) + inner),p.y + 0.75 - width);
}
float map(vec3 p){
    p.xz = fract((p.xz + vec2(0.8)) / 1.6) * 1.6 - 0.8;
    p.y = -abs(p.y);
    p.xz *= genRot(-iTime);
    float vol1 = 0.;
    float vol2 = 0.;
    float vol3 = 0.;
    for(float i = 0.0; i < 1.0 / 3.0; i+= 0.05){
    	int tx = int(i*512.0);
        	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 
        vol1 += fft;
    }
    for(float i = 1./3.; i < 2.0 / 3.0; i+= 0.05){
    	int tx = int(i*512.0);
        	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 
        vol2 += fft;
    }
    for(float i = 2./3.; i < 3.0 / 3.0; i+= 0.05){
    	int tx = int(i*512.0);
        	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 
        vol3 += fft;
    }
	float gear1 = gear(p,0.7,0.6,0.9,0.8,vol1 * 0.1,6.);
    float gear2 = gear(p,0.4,0.3,0.7,0.6,vol2 * 0.2,6.);
	float gear3 = gear(p,0.,0.,0.4,0.3,vol3 * 0.4,6.);
    return min(gear1,min(gear2,gear3));

}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 128; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.75;
    }
    return vec4(getNormal(p),t);
}


