#define ZOOM 1.5
#define MAX_STEPS 100
#define MIN_DISTANCE 0.0001
#define MAX_DISTANCE 50.
const vec3 _lightPos = vec3(-2., 5., -1.);
const vec3 _lightPos2 = vec3(2., 5., 1.);
const vec3 _lightDir = vec3(0., -.5, 1.);

//const vec3 _lightColor = vec3(.92, 0.85, 0.99);
//const vec3 _lightColor = vec3(.92, 0.54, 0.79);
const vec3 _lightColor = vec3(.92, 0.8, 0.93);


const float _pointIntense = .3;
const float _parallelIntense = 0.4;
const float _shadowMin = .01;
const float _shadowMax = 50.;
const float _shadowIntense = 1.;
const float _sphereSmooth = .15;
const float _k = 15.;

vec3 getRay(in vec3 ro, in vec3 lookAt, in vec2 uv) {
    vec3 f = normalize(lookAt - ro);
    vec3 r = cross(vec3(0., 1., 0.), f);
    vec3 u = cross(f, r);
    return normalize(uv.x * r + uv.y * u + f * ZOOM);
}

float sdPlane(in vec3 p) {
    return p.y;
}

float sdSphere(in vec3 p, in vec3 c, in float r) {
    return length(c - p) - r;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float opSU ( float d1, float d2, float k ) {
  float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
  return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float opSS( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

float sdf(in vec3 p) {

    //vec3 c1 = vec3(0., 1. + .25 * sin(4. * iTime) + 0.25 * sin(iTime * 4.), 0.);
    //vec3 c2 = vec3(0., .5 - .25 * sin(4. * iTime) + 0.25 * sin(iTime * 4.), 0.);
    //vec3 c3 = vec3(.25 * sin(2. * iTime), .75 + 0.25 * sin(iTime * 4.), 0.);
    //vec3 c4 = vec3(-.25 * sin(2. * iTime), .75 + 0.25 * sin(iTime * 4.), 0.);
    //vec3 b = vec3(0., .75 + 0.25 * sin(iTime * 4.), 0.);


    vec3 c1 = vec3(-1.0,1.0,-1.0);
    vec3 c2 = vec3(2.0,1.0,0.0);
    vec3 c3 = vec3(-2.0,1,-1.0);
    vec3 c4 = vec3(1.0,0.5,-2);
    vec3 b = vec3(0., .75 + 0.25 * sin(iTime * 4.), 0.);


    float r = .5;
    float sphere1 = sdSphere(p, c1, r);
    float sphere2 = sdSphere(p, c2, r);
    float sphere3 = sdSphere(p, c3, r);
    float sphere4 = sdSphere(p, c4, r);
    float plane = sdPlane(p);
    float box = sdBox(b - p, vec3(.5, .5, .5)); 


    /*
	float d = opSU(sphere1, sphere2, _sphereSmooth);
    d = opSU(d, sphere3, _sphereSmooth);
    d = opSU(d, sphere4, _sphereSmooth);
    d = opSS(d, box, .1);
    d = opSU(d, plane, .8);
    */

    float d;
    d = min(sphere1, sphere2);
    d = min(d, sphere3);
    d = min(d, sphere4);
    d = min(d, box);
    d = min(d, plane);


    return d;
}

float rayMarching(in vec3 ro, in vec3 rd) {
    float dO = 0.;
    for (int i = 0; i < MAX_STEPS; i ++) {
        vec3 p = ro + rd * dO;
        float dS = sdf(p);
        dO += dS;
        if(dS < MIN_DISTANCE || dO > MAX_DISTANCE) break;
    }
    return dO;
}

vec3 getNormal(in vec3 p) {
    float d = sdf(p);
    vec2 e = vec2(MIN_DISTANCE, 0);
    return normalize(vec3(
        sdf(p + e.xyy) - d,
        sdf(p + e.yxy) - d,
        sdf(p + e.yyx) - d
        ));
}

float shadow(in vec3 ro, in vec3 rd, in float mint, in float maxt, in float k) {
    float res = 1.0;
    for(float h = mint;h<maxt;){
        vec3 p = ro + rd * h;
        float s = sdf(p);
        h += s;
        if (s <= MIN_DISTANCE) {
            return 0.0;
        }
        res = min(res, k * s / h);
    }
    return res;
}

vec3 getLight(in vec3 p) {
    vec3 n = getNormal(p);
    vec3 ld = normalize(_lightPos - p);
    vec3 ld2 = normalize(_lightPos2 - p);
    float lParallel = clamp(dot(n, -_lightDir), 0., 1.) * _parallelIntense;
    float lPoint = clamp(dot(n, ld), 0., 1.) * _pointIntense;
    float lPoint2 = clamp(dot(n, ld2), 0., 1.) * _pointIntense;
    lParallel = lParallel * pow(shadow(p, -_lightDir, _shadowMin, _shadowMax, _k), _shadowIntense);
    lPoint = lPoint * pow(shadow(p, ld, _shadowMin, _shadowMax, _k), _shadowIntense);
    lPoint2 = lPoint2 * pow(shadow(p, ld2, _shadowMin, _shadowMax, _k), _shadowIntense);

    return (lPoint + lParallel + lPoint2) * _lightColor;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3(5. * sin(iTime / 2.), 4. , 5. * cos(iTime / 2.));
    vec3 lookAt = vec3(0., .5, 0.);
    vec3 col = vec3(0., 0., 0.);
    vec3 rd = getRay(ro, lookAt, uv);
	float d = rayMarching(ro, rd);
    vec3 p = ro + d * rd;
    // Output to screen
    fragColor = vec4(getLight(p), 1.);
}

float PI = 3.14159265;
mat2 genRot(float a){
	return mat2(cos(a),-sin(a),sin(a),cos(a));
}
vec2 pmod(vec2 p,float count){
	p *= genRot(PI/count);
	float at = atan(p.y/p.x);
	float r = length(p);
	at = mod(at,2. * PI / count);
	p = vec2(r * cos(at),r * sin(at));
	p *= genRot(-PI/count);
	return p;
}
float map(vec3 p){
	vec3 q = p;
	p.xy *= genRot(p.z/4.);
	p = (fract(p/1.5 + 0.5) - 0.5) * 1.5;
	p.xy = pmod(p.xy,8.);
	float string = length(p.xy - vec2(0.25 + 0.05 * sin(q.z + iTime * PI),0.)) - .01 + 0.005 * sin(q.z);
	float sphere = length(p - vec3(0.25 + 0.05 * sin(q.z + iTime * PI),0.,0.)) - .03;
	string = min(string,sphere);
	return string;
}
vec3 getNormal(vec3 p){
	vec3 x = dFdx(p);
	vec3 y = dFdy(p);
	return normalize(cross(x,y));
}
vec4 trace(vec3 o,vec3 r){
	float t = 0.;
	vec3 p;
	for(int i = 0; i < 128; i++){
		p = o + r * t;
		float d = map(p);
		t += d * 0.75;
	}
	vec3 n = getNormal(p);
	return vec4(n,t);
}

vec3 cam(){
	vec3 c = vec3(0.,0.,-1.5);
	c += vec3(.5 * sin(iTime/2.),.5 * cos(iTime/2.),iTime * 2.);
	return c;
}
vec3 ray(vec2 uv,float z){
	vec3 r = normalize(vec3(uv,z));
	r.xz *= genRot(PI / 8.);
	r.xy *= genRot(-iTime/4.);
	//r.xz *= genRot(time/8.);
	//r.yz *= genRot(time/8.);
	return r;
}

vec3 getColor(vec3 o, vec3 r, vec4 d){
	vec3 light = normalize(vec3(r.xy,0.));
	vec3 p = o + r * d.w;
	vec3 n = d.xyz;
	float a = dot(n,r);
	vec3 cc = (vec3(sin(p.x),sin(p.y),sin(p.z)) * 0.5 + 0.5);
	vec3 bc = vec3(1.-a);
	cc = fract(p.z / 1.5) < 0.9 && fract((p.z/2. - iTime * 4.) / 8.) < 0.95  ? vec3(0.) : cc;
	bc += cc * 2.;
	float t = d.w;
	float fog = 1./(1. + t * t * 0.05);
	return mix(bc,vec3(0.),1. - fog);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

	vec2 uv = ( fragCoord.xy * 2. - iResolution.xy) / iResolution.y ;
	vec3 c = cam();
	vec3 r = ray(uv,1.5);
	vec4 d = trace(c,r);
	vec3 color = getColor(c,r,d);

	fragColor = vec4( color, 1.0 );

}

