// by Nikos Papadopoulos, 4rknova / 2019
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define PI  3.14159265359

#define BG_COLOR  vec3(0.0745, 0.0862, 0.1058)
#define AMPLITUDE (0.1)
#define PERIOD    (10.) 
#define PHASE     (iTime * 10.)
#define DELTA     (PI)
#define DECAY     (2.0)
#define SCALE     (1.1)
#define RING_MIN  (0.98)
#define RING_MAX  (1.00)

float ring(float r) 
{ 
    return step(r, RING_MAX) - step(r, RING_MIN); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    p.x *= iResolution.x / iResolution.y;

    // Polar Coordinates
    float r = SCALE * length(p);
    float theta = atan(p.y, p.x);

    // remap [0,1] to [0,1]->[1,0]
    float decay     = pow(abs(theta/PI),DECAY);
    float amplitude = AMPLITUDE * decay;
    float period    = theta * PERIOD + PHASE;

    float w0 = amplitude * cos(period)
        , w1 = amplitude * cos(period + DELTA);

    vec3 f = ring(r-w0) + ring(r-w1) + BG_COLOR;    
    fragColor = vec4(vec3(f), 1);
}

// Robotic Arm. Created by Reinder Nijhoff 2019
// Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International License.
// @reindernijhoff
//
// https://www.shadertoy.com/view/tlSSDV
//
// This shader is a proof of concept to find out if I could 
// create a typical Shadertoy shader, i.e. a shader that renders 
// a non-trivial animated 3D scene, by using a ray tracer instead 
// of the commonly used raymarching techniques. 
//
// Some first conclusions:
// 
// - It is possible to visualize an animated 3D scene in a single 
//   shader using ray tracing.
// - The compile-time of this shader is quite long.
// - The ray tracer is not super fast, so it was not possible to cast
//   enough rays per pixel to support global illumination or soft
//   shadows. Here I miss the cheap AO and soft shadow algorithms that
//   are available when raymarching an SDF.
// - Modelling a 3D scene for a ray tracer in code is verbose. It was
//   not possible to exploit the symmetries in the arm and the domain
//   repetition of the sphere-grid that would have simplified the
//   description of an SDF.
// - I ran in GPU-dependent unpredictable precision problems. Hopefully,
//   most problems are solved now. Im not sure if they are inherent
//   to ray tracing, but I didnt have these kinds of problems using
//   raymarching before.
//

#define AA 1 // Set AA to 1 if you have a slow GPU
#define PATH_LENGTH 3
#define MAX_DIST 60.
#define MIN_DIST .001
#define ZERO (min(iFrame,0))

// Global variables
float time;
vec2[2] activeSpheres;
vec2[3] joints;
float joint0Rot;
float jointYRot;

//
// Hash by Dave_Hoskins: https://www.shadertoy.com/view/4djSRW
//
vec2 hash22(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

//
// Ray-primitive intersection routines: https://www.shadertoy.com/view/tl23Rm
//
float dot2( in vec3 v ) { return dot(v,v); }

// Plane 
float iPlane( const in vec3 ro, const in vec3 rd, in vec2 distBound, inout vec3 normal,
              const in vec3 planeNormal, const in float planeDist) {
    float a = dot(rd, planeNormal);
    float d = -(dot(ro, planeNormal)+planeDist)/a;
    if (a > 0. || d < distBound.x || d > distBound.y) {
        return MAX_DIST;
    } else {
        normal = planeNormal;
    	return d;
    }
}

// Sphere: https://www.shadertoy.com/view/4d2XWV
float iSphere( const in vec3 ro, const in vec3 rd, const in vec2 distBound, inout vec3 normal,
               const float sphereRadius ) {
    float b = dot(ro, rd);
    float c = dot(ro, ro) - sphereRadius*sphereRadius;
    float h = b*b - c;
    if (h < 0.) {
        return MAX_DIST;
    } else {
	    h = sqrt(h);
        float d1 = -b-h;
        float d2 = -b+h;
        if (d1 >= distBound.x && d1 <= distBound.y) {
            normal = normalize(ro + rd*d1);
            return d1;
        } else {
            return MAX_DIST;
        }
    }
}

// Capped Cylinder: https://www.shadertoy.com/view/4lcSRn
float iCylinder( const in vec3 oc, const in vec3 rd, const in vec2 distBound, inout vec3 normal,
                 const in vec3 ca, const float ra, const bool traceCaps ) {
    float caca = dot(ca,ca);
    float card = dot(ca,rd);
    float caoc = dot(ca,oc);

    float a = caca - card*card;
    float b = caca*dot( oc, rd) - caoc*card;
    float c = caca*dot( oc, oc) - caoc*caoc - ra*ra*caca;
    float h = b*b - a*c;

    if (h < 0.) return MAX_DIST;

    h = sqrt(h);
    float d = (-b-h)/a;

    float y = caoc + d*card;
    if (y >= 0. && y <= caca && d >= distBound.x && d <= distBound.y) {
        normal = (oc+d*rd-ca*y/caca)/ra;
        return d;
    } else if(!traceCaps) {
        return MAX_DIST;
    } else {
        d = ((y < 0. ? 0. : caca) - caoc)/card;

        if( abs(b+a*d) < h && d >= distBound.x && d <= distBound.y) {
            normal = normalize(ca*sign(y)/caca);
            return d;
        } else {
            return MAX_DIST;
        }
    }
}

// Capped Cone: https://www.shadertoy.com/view/llcfRf
float iCone( const in vec3 oa, const in vec3 rd, const in vec2 distBound, inout vec3 normal,
             const in vec3 pb, const in float ra, const in float rb ) {
    vec3  ba = pb;
    vec3  ob = oa - pb;

    float m0 = dot(ba,ba);
    float m1 = dot(oa,ba);
    float m2 = dot(ob,ba); 
    float m3 = dot(rd,ba);

    //caps - only top cap needed for scene
    if (m1 < 0. && dot2(oa*m3-rd*m1)<(ra*ra*m3*m3) ) {
        float d = -m1/m3;
        if (d >= distBound.x && d <= distBound.y) {
            normal = -ba*inversesqrt(m0);
            return d;
        }
    }

    // body
    float m4 = dot(rd,oa);
    float m5 = dot(oa,oa);
    float rr = ra - rb;
    float hy = m0 + rr*rr;

    float k2 = m0*m0    - m3*m3*hy;
    float k1 = m0*m0*m4 - m1*m3*hy + m0*ra*(rr*m3*1.0        );
    float k0 = m0*m0*m5 - m1*m1*hy + m0*ra*(rr*m1*2.0 - m0*ra);

    float h = k1*k1 - k2*k0;
    if( h < 0. ) return MAX_DIST;

    float t = (-k1-sqrt(h))/k2;

    float y = m1 + t*m3;
    if (y > 0. && y < m0 && t >= distBound.x && t <= distBound.y) {
        normal = normalize(m0*(m0*(oa+t*rd)+rr*ba*ra)-ba*hy*y);
        return t;
    } else {   
        return MAX_DIST;
    }
}

// Box: https://www.shadertoy.com/view/ld23DV
float iBox( const in vec3 ro, const in vec3 rd, const in vec2 distBound, inout vec3 normal, 
            const in vec3 boxSize ) {
    vec3 m = sign(rd)/max(abs(rd), 1e-8);
    vec3 n = m*ro;
    vec3 k = abs(m)*boxSize;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

    if (tN > tF || tF <= 0.) {
        return MAX_DIST;
    } else {
        if (tN >= distBound.x && tN <= distBound.y) {
        	normal = -sign(rd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
            return tN;
        } else if (tF >= distBound.x && tF <= distBound.y) {
        //	normal = sign(rd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
            return tF;
        } else {
            return MAX_DIST;
        }
    }
}

//
// Ray tracer helper functions
//
vec3 FresnelSchlick(vec3 SpecularColor, vec3 E, vec3 H) {
    return SpecularColor + (1. - SpecularColor) * pow(1.0 - max(0., dot(E, H)), 5.);
}

vec2 randomInUnitDisk(const vec2 seed) {
    vec2 h = hash22(seed) * vec2(1.,6.28318530718);
    float phi = h.y;
    float r = sqrt(h.x);
	return r*vec2(sin(phi),cos(phi));
}

//
// Sphere functions
//
vec2 activeSphereGrid(float t) {
  vec2 p = randomInUnitDisk(vec2(floor(t),.5));
  return floor(p * 8.5 + 1.75*normalize(p));
}

vec3 sphereCenter(vec2 pos) {
    vec3 c = vec3(pos.x, 0., pos.y)+vec3(.25,.25,.25);
    c.xz += .5*hash22(pos);
	return c;
}

vec3 sphereCol(in float t) {
    return normalize(.5 + .5*cos(6.28318530718*(1.61803398875*floor(t)+vec3(0,.1,.2))));
}

//
// Inverse Kinematics
//
// Very hacky, analytical,  inverse kinematics. I came up with the algorithm myself;
// igo Qulez can probably implement it without using trigonometry:
// http://www.iquilezles.org/www/articles/noacos/noacos.htm
//
void initDynamics() {
    time = iTime * .25;

    activeSpheres[0] = activeSphereGrid(time);
    activeSpheres[1] = activeSphereGrid(time+1.);

    vec3 ta0 = sphereCenter(activeSpheres[0]);
    vec3 ta1 = sphereCenter(activeSpheres[1]);

    float taa0 = atan(-ta0.z, ta0.x);  
    float taa1 = atan(-ta1.z, ta1.x);

    if (abs(taa0-taa1) > 3.14159265359) {
        taa1 += taa1 < taa0 ? 2. * 3.14159265359 : -2. * 3.14159265359;  
    }
    jointYRot = mix(taa0, taa1, clamp(fract(time)*2.-.5,0.,1.));    

    float tal = mix(length(ta0), length(ta1), clamp(fract(time)*2.5-1.,0.,1.));

    vec2 target = vec2(tal,.5-.5*smoothstep(.35,.4,abs(fract(time)-.5)));  

    float c0 = length(target);
    float b0 = min(11., 4. + 2. * c0 / 11.);

    vec2 sd = normalize(target);
    float t0 = asin(sd.y)+acos(-(b0*b0-25.-c0*c0)/(10.*c0));

    joints[0] = vec2(5. * cos(t0), 5.* sin(t0));
    joint0Rot = t0;

    sd = normalize(target-joints[0]);  
    float c1 = min(6., distance(joints[0], target));
    const float b1 = 2.;  

    float t1 = asin(sd.y) * sign(sd.x) + acos(-(b1*b1-16.-c1*c1)/(8.*c1));
    t1 += sd.x < 0. ? 3.1415 : 0.;
    joints[1] = joints[0] + 4. * vec2(cos(t1),sin(t1));
    joints[2] = target;
}

//
// Scene description
//
vec3 opU( const in vec3 d, const in float iResult, const in float mat ) {
	return (iResult < d.y) ? vec3(d.x, iResult, mat) : d;
}

vec3 iPlaneInt(vec3 ro, vec3 rd, float d) {
    d = -(ro.y - d) / rd.y;
    return ro + d * rd;
}

vec3 traceSphereGrid( in vec3 ro, in vec3 rd, in vec2 dist, out vec3 normal, const int maxsteps ) {  
	float m = 0.;
    if (ro.y < .5 || rd.y < 0.) {
        vec3 ros = ro.y < .5 ? ro : iPlaneInt(ro, rd, .5);
        if (length(ros.xz) < 11.) {
            vec3 roe = iPlaneInt(ro, rd,rd.y < 0. ?0.:.5);
            vec3 pos = floor(ros);
            vec3 rdi = 1./rd;
            vec3 rda = abs(rdi);
            vec3 rds = sign(rd);
            vec3 dis = (pos-ros+ .5 + rds*.5) * rdi;
            bool b_hit = false;

            // traverse grid in 2D
            vec2 mm = vec2(0);
            for (int i = ZERO; i<maxsteps; i++) {
                float l = length(pos.xz+.5);
                if (pos.y > .5 || pos.y < -1.5 || l > 11.) {
                    break;
                }
                else if ( l > 2. && pos.y > -.5 && pos.y < 1.5 ) {
                    float d = iSphere(ro-sphereCenter(pos.xz), rd, dist, normal, .25);
                    if (d < dist.y) {
                        m = 2.;
                        dist.y = d;
                        break;
                    }
                }	
                vec3 mm = step(dis.xyz, dis.yxy) * step(dis.xyz, dis.zzx);
                dis += mm*rda;
                pos += mm*rds;
            }
        }
    }
	return vec3(dist, m);
}

vec3 rotateY( const in vec3 p, const in float t ) {
    float co = cos(t);
    float si = sin(t);
    vec2 xz = mat2(co,si,-si,co)*p.xz;
    return vec3(xz.x, p.y, xz.y);
}

vec3 worldhit( const in vec3 ro, const in vec3 rd, const in vec2 dist, out vec3 normal ) {
    vec3 d = vec3(dist, 0.);

    d = traceSphereGrid(ro, rd, d.xy, normal, 10);

    d = opU(d, iPlane   (ro, rd, d.xy, normal, vec3(0,1,0), 0.), 1.);
    d = opU(d, iCone    (ro-vec3(0,.2,0), rd, d.xy, normal, vec3(0,.2,0), 1.5, 1.4), 4.);
    d = opU(d, iCylinder(ro, rd, d.xy, normal, vec3(0,.2,0), 1.5, false), 4.);

    float dmax = d.y;
    vec3 roa = rotateY(vec3(ro.x, ro.y-1., ro.z), jointYRot);    
    vec3 rda = rotateY(rd, jointYRot); 

    vec3 bb = vec3(.5*max(joints[1].x,joints[2].x), joints[0].y*.5, .0);
    vec3 bbn;

    if (iBox(roa-bb, rda, vec2(0,100), bbn, bb+vec3(.75,.75,.8)) < 100.) {
	    vec3 dr = vec3(-sin(joint0Rot), cos(joint0Rot), 0);
        vec2 j21 = joints[2]-joints[1];

        for (int axis=0; axis<=1; axis++) {
            float a = axis == 0 ? -1. : 1.;
            d = opU(d, iCylinder(roa-vec3(0,0,a*.67), rda, d.xy, normal, vec3(0,0,-a*.2),.55, true), 3.);
            d = opU(d, iCylinder(roa-vec3(0,0,a*.58)-.4*dr, rda, d.xy, normal, vec3(joints[0],-a*.24)-.24*dr,.07, false), 4.);
            d = opU(d, iCylinder(roa-vec3(0,0,a*.58)+.4*dr, rda, d.xy, normal, vec3(joints[0],-a*.24)+.24*dr,.07, false), 4.);
            d = opU(d, iCylinder(roa-vec3(joints[0],a*.45), rda, d.xy, normal, vec3(0,0,-a*.2),.35, true), 3.);
            d = opU(d, iCylinder(roa-vec3(joints[1],a*.29), rda, d.xy, normal, vec3(0,0,-a*.08),.25, true), 3.);
            d = opU(d, iCylinder(roa-vec3(joints[1],a*.24), rda, d.xy, normal, vec3(j21,a*.08),.03, false), 4.);
        }

        vec2 j10 = joints[1]-joints[0];
        d = opU(d, iCylinder(roa-vec3(0,0,-.72), rda, d.xy, normal, vec3(0,0,1.44),.5, true), 5.);
        d = opU(d, iBox     (roa+vec3(0,.5,0), rda, d.xy, normal, vec3(.5,.5,.47)), 5.);
        d = opU(d, iCone    (roa-vec3(joints[0],0), rda, d.xy, normal, vec3(j10,0),.25, .15), 5.);
        d = opU(d, iCylinder(roa-vec3(joints[0],-.5), rda, d.xy, normal, vec3(0,0,1.),.3, true), 5.);
        d = opU(d, iCylinder(roa-vec3(joints[1],-.35), rda, d.xy, normal, vec3(0,0,.7),.2, true), 5.);
        d = opU(d, iCylinder(roa-vec3(joints[2],-.4), rda, d.xy, normal, vec3(0,0,.8),.2, true), 3.);
        d = opU(d, iSphere  (roa-vec3(joints[2],0), rda, d.xy, normal, .32), 5.);
        d = opU(d, iCylinder(roa-vec3(joints[2],0), rda, d.xy, normal, vec3(0,-.5,0),.06, true), 3.);

        if (d.y < dmax) {
            normal = rotateY(normal, -jointYRot);
        }
    }    
    return d;
}

float shadowhit( const vec3 ro, const vec3 rd, const float dist) {
    vec3 normal;
    float d = traceSphereGrid( ro, rd, vec2(.3, dist), normal, 4).y;
    d = min(d, iCylinder(ro, rd, vec2(.3, dist), normal, vec3(0,.2,0), 1.5, false));
    return d < dist-0.001 ? 0. : 1.;
}

//
// Simple ray tracer
//
float getSphereLightIntensity(float num) {
    return num > .5 ?
        clamp(fract(time)*10.-1., 0., 1.) :
		max(0., 1.-fract(time)*10.); 
}

float getLightIntensity( const vec3 pos, const vec3 normal, const vec3 light, const float intensity) {
    vec3 rd = pos - light;
    float i = max(0., dot(normal, -normalize(rd)) / dot(rd,rd));
    i = i > 0.0001 ? i * intensity * shadowhit(light, normalize(rd), length(rd)) : 0.;
    return max(0., i-0.0001);              
}

vec3 getLighting( vec3 p, vec3 normal ) {
    vec3 l = vec3(0.);

    float i = getSphereLightIntensity(0.);
    if (i > 0.) {
	    l += sphereCol(time) * (i * getLightIntensity(p, normal, sphereCenter(activeSpheres[0]), .375));
    } else {    
        i = getSphereLightIntensity(1.);
        if (i > 0.) {
            l += sphereCol(time+1.) * (i * getLightIntensity(p, normal, sphereCenter(activeSpheres[1]), .25));
        }
    }

    vec3 robot = mix(sphereCol(time), sphereCol(time-1.), getSphereLightIntensity(0.));
    vec3 lp = rotateY(vec3(joints[2].x, joints[2].y+1.,0), -jointYRot);
    i = getLightIntensity(p, normal, lp, .5);
    i += getLightIntensity(p, normal, vec3(0,2,0), .25);
    l += i * robot;

    return l;
}

vec3 getEmissive( in vec2 pos, in float mat ) {
    if (mat > 2.5 ) {
	   return mix(sphereCol(time), sphereCol(time-1.), getSphereLightIntensity(0.));
    } else if (mat > 1.5 ) {
        float li0 = getSphereLightIntensity(0.);
        float li1 = getSphereLightIntensity(1.);
        if (li0 > 0. && pos == activeSpheres[0]) {
            return sphereCol(time) * li0 * 1.25;
        } else if (li1 > 0. && pos == activeSpheres[1]) {
            return sphereCol(time+1.) * li1;
        } else {
            return vec3(0);
        }
    } else {
        return vec3(0);
    }
}

vec3 render( in vec3 ro, in vec3 rd) {
    vec3 col = vec3(1);
    vec3 emitted = vec3(0);
    vec3 normal;

    for (int i=ZERO; i<PATH_LENGTH; ++i) {
    	vec3 res = worldhit( ro, rd, vec2(MIN_DIST, MAX_DIST-1.), normal );
		if (res.z > 0.) {
			ro += rd * res.y;

            if (res.z < 3.5) { 
               	vec3 F = FresnelSchlick(vec3(0.4), normal, -rd);
                emitted += (col * (getEmissive(floor(ro.xz), res.z) + .5 * getLighting(ro, normal))) * (1.-F);
                col *= .5 * F;
            } else {
                col *= res.z < 4.5 ? vec3(.7,.75,.8) : vec3(.9,.6,.2);   
            } 

            rd = normalize(reflect(rd,normal));
        } else {
			return emitted;
        }
    }  
    return emitted;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr ) {
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    initDynamics();

    vec2 mo = iMouse.xy == vec2(0) ? vec2(.4,-.1) : abs(iMouse.xy)/iResolution.xy - .5;

    vec3 ro = vec3(10.5*cos(1.5+6.*mo.x), 6.+10.*mo.y, 8.5*sin(1.5+6.*mo.x));
    vec3 ta = vec3(ro.x*ro.y*.02, .8, 0);
    mat3 ca = setCamera(ro, ta, 0.);    

    vec3 col = vec3(0);

#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ ) {
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif
        vec3 rd = ca * normalize( vec3(p.xy,1.6) );  
        col += pow(8. * render(ro, rd), vec3(1./2.2));
#if AA>1
    }
    col /= float(AA*AA);
#endif

    col = clamp(col + ((hash22(fragCoord).x-.5)/64.), vec3(0), vec3(1));

	fragColor = vec4(col , 1);
}

// thank you https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    uv -= .5;

    vec3 col = vec3( (sin(uv.x + iTime)), sin(iTime * 5.), (sin(-uv.y - .6) + 1.) / 2.);
    col = hsv2rgb(col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Colder tweak of 'Cold Amoebas', based on 'Flow of cells' (https://www.shadertoy.com/view/WtSSRR)
#define FIELD 35.0
#define HEIGHT 0.7
#define ITERATION 2.
#define TONE vec4(0.0,0.2,0.6,0)
#define SPEED 0.6

float eq(vec2 p,float t){
	float x = sin( p.y-t +cos(t+p.x*.2) ) * cos(p.x-t);
	x *= acos(x);
	return - x * abs(x-0.0) * p.x/p.y;
}

void mainImage( out vec4 O, vec2 U ) {
	vec4 X=O;
	vec2  p = FIELD*(U / iResolution.xy  +.5);
	float t = iTime*SPEED,i,
         hs = 20.*(HEIGHT+cos(t)*.1),
	      x = eq(p,t), y = p.y-x;

	for(float i=0.; i<ITERATION; ++i)
		p.x *= 2.,
        X = x + vec4(0, eq(p,t+i+1.), eq(p,t+i+2.) ,0),
        x = X.z += X.y,
        O += TONE / abs(y-X-hs);
}

const float PI = 3.141592654;

vec3 hash32(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
vec4 disco(vec2 uv) {
    float v = abs(cos(uv.x * PI * 2.) + cos(uv.y *PI * 2.)) * .5;
    uv.x -= .5;
    vec3 cid2 = hash32(vec2(floor(uv.x - uv.y), floor(uv.x + uv.y))); // generate a color
    return vec4(cid2, v);
}
mat2 rot2D(float r){
    float c = cos(r), s = sin(r);
    return mat2(c, s, -s, c);
}
vec2 warp(vec2 uv, float f, out float sd) {
    float d = length(mod(uv+1.,2.)-1.);
    sd = d-1.;
    return uv*f*(1.-d);
}

vec4 purty(vec2 V, float t, float f) {
    float t2 = t*.2;
    V *= rot2D(t2*.25);
    V += vec2(sin(t2), cos(t2));

    float sd;
    vec2 uvP = warp(V, f, sd);

    uvP += t*.2;

    vec4 d = disco(uvP);
    vec3 col = d.rgb * pow(d.a, .15);

    col.rg *= rot2D(.1);
    col.br *= rot2D(-.2);

    float a = pow(smoothstep(0.,1.,-sd), .4);
    return vec4(col, a);
}
void mainImage(out vec4 o, vec2 O)
{
    float t = iTime+1e3;
    vec2 R = iResolution.xy
        ,V=(O-.5*R)/R.y
        ,N=O/R-.5
        ,P=O-R*.5;
    V *= 4.;

    vec4 p =purty(V, t, 4.);
    V += 1.;
    V = -V * 2.;
    vec4 p2 = purty(V, t, 1.);
    p2.rgb *= .15;
    p2.rgb = mix(p2.rgb,vec3(p2.r+p2.g+p2.b)/3.,.9);//desaturate

    o = mix(p2,p,smoothstep(0.,.05,iMouse.z > 0. ? 0. : p.a));//mix the lower layer where a = 0

    o=clamp(o,0.,1.);
    o = pow(o,o-o+.5);
    o *= 1.-dot(N,N*2.);
    o *= 1.-step(.42,abs(N.y));
    o.a= 1.;
}

#define PI 3.14159
#define SOFT 0.0035

vec2 hash( vec2 p ) 
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    float v = mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
              	             dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
              	        mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
              	             dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
              	   mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
              	             dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
              	        mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
              	             dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
	return v;
}

float noise( vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    float v = mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                        dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                   mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                        dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
	return v;
}

float fbm (vec2 p)
{
	return noise(p)+0.5*noise(p*2.);
}

float fbm (vec3 p)
{
    float n =0.;
    float s = 1.;
    for (int i = 0; i<6; i++)
    {
	 	n+= noise (p*s)/s;
    	s*=2.;
	}
    return n;
}
float band(float x,float mid, float thickness)
{
	return smoothstep (mid-thickness,mid,x)-smoothstep(mid, mid+thickness,x);
}

float ring(float rMin, float rMax, float l, float soft)
{
    return  smoothstep (rMin-soft,rMin,l)-smoothstep(rMax, rMax+soft,l);    
}

float sat(float x){return clamp(x,0.,1.); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    vec2 uvNorm = uv*2.;

    uv *= 0.0035;
    uv *= iResolution.xy;
    vec2 oldUv = uv;

    uv += vec2(cos(0.5*iTime),sin(0.33*iTime))*0.045;

    float pol = atan(uv.y,uv.x)+PI;
    float rad = length(uv); 

    float deform = 0.02 * noise(vec3(7.*oldUv, iTime));

    float rMin = 0.06 + deform +sin(iTime*0.5)*0.015;;
    float rMax = 0.35 + deform;
    float limit = 0.18 + deform ;
    float bands = step(limit,rad);
    float thickness = 0.0018*3.;   
	float sp = 0.25;
    float nSlice = mix(7.,9.,bands);
   	float slice = 2.*PI / nSlice;

    pol+=iTime*mix(0.05,-0.05,bands);

    uv.x = cos(pol)*rad;
    uv.y = sin(pol)*rad;

	// Main shape   
    float t1 =  1.-(smoothstep(rMin, rMin +0.5*(limit-rMin),rad)-smoothstep(rMin +0.5*(limit-rMin),limit,rad));
    float t2 =  1.-(smoothstep(limit, limit +0.5*(rMax-limit),rad)-smoothstep(limit +0.5*(rMax-limit),rMax,rad));
    float nMap = mix(t1,t2,step(limit,rad));
    float reshape = 1.0;
    float nStr = 0.03 / pow(rad,0.85);  
    float nFq = 7.;
    float offsetStr = 0.2;

    //(pass1)
    float pol1 = pol;       
    float rot = slice*3.5*bands;
    float s = step(rad,limit);
    pol1 += rot;    
    float cellPol =-rot+(floor((pol+rot+0.5*slice)/(slice))+0.5)*(slice);
    float cellId = floor((mod(pol1+0.5*slice,2.*PI)/slice));
    float offset = hash(vec2(cellId,0.)).x*rad;
    vec2 cp = normalize(vec2(cos(cellPol),sin(cellPol)));
    pol1 = mod(pol1+0.5*slice,slice)-0.5*slice;   
    float n = nStr*4.*(fbm(uv*nFq-cp*iTime*sp+rad*2.));
    float li1 = band(pol1+n+offsetStr*offset,0.,thickness/(rad));
    n = mix(n, nStr*4.*(fbm(uv*nFq-cp*iTime*sp+rad*2.+5000.)),nMap);
    li1 = max(li1,band(pol1+n+offsetStr*offset,0.,thickness/(rad)));   

    //(pass2)
    pol1 = pol;       
    rot = slice*0.5+slice*3.5*bands;
    s = step(rad,limit);
    pol1 += rot;    
    cellPol =-rot+(floor((pol+rot+0.5*slice)/(slice))+0.5)*(slice);
    cellId = floor((mod(pol1+0.5*slice,2.*PI)/slice));
    offset = hash(vec2(cellId,1.)).x*rad;
    cp = normalize(vec2(cos(cellPol),sin(cellPol)));
    pol1 = mod(pol1+0.5*slice,slice)-0.5*slice;   
    n = nStr*4.*(fbm(uv*nFq-cp*iTime*sp+rad*2.+2000.));
    float li2 = band(pol1+n+offsetStr*offset,0.,thickness/(rad));
    n = mix(n,nStr*4.*(fbm(uv*nFq-cp*iTime*sp+rad*2.+5000.)),nMap);
    li2 = max(li2,band(pol1+n+offsetStr*offset,0.,thickness/(rad)));

    float li = max (li1,li2);

    float zoneMid = ring(0.,rMin,rad,SOFT);
    float mid =  (fbm(vec3(oldUv*150.,iTime*0.5)))-0.2;
	float ring0 = ring(rMin,rMin+0.002,rad,SOFT);
    float ringlim = ring(limit,limit+0.002,rad,SOFT);
    float zone = ring(rMin,rMax,rad,SOFT);

    li = max(max(li,ring0),ringlim);
	li = min (li,zone);
    li = max(li,mid*zoneMid);

    zone = ring(0.,rMax,rad,SOFT+0.01);

    vec3 liCol = mix(vec3(0.4), 1.5*vec3(0.16,0.14,0.025) , ring(0.,limit-SOFT,rad,SOFT));
    vec3 bgCol = mix(vec3(0.001,0.0045,0.1429),vec3(0.0087),zoneMid);
    vec3 mainShape = mix(bgCol*zone,liCol,li);

    //Stars
    pol = atan(oldUv.y,oldUv.x)+PI;
    rad = length(oldUv);    
    slice = 2.*PI/50.;
    float radSlice = 40.*pow(rad,0.24);

    //(pass1)
    cellPol = floor(pol/slice);
    float cellRad = floor(-0.25*iTime+radSlice);
    vec2 cellSpace = vec2(fract(pol/slice), fract(-0.25*iTime+radSlice));
    vec3 cellH = hash(vec3(cellRad,cellPol,50.))*0.5+0.5;

    float starRad = (cellH.y*0.032+0.02)/rad;
    float starProb = cellH.x;
    float starOs = cellH.z-0.5;
    float starLum = cellH.z;
    float stars = starProb>0.4 ? 0. : starLum*smoothstep(starRad+0.01,0.01,length(-starOs+cellSpace-0.5));

    //(pass2)
    pol +=0.5*slice;
    cellPol = floor(pol/slice);
    cellRad = floor(-0.5*iTime+150.+radSlice);
    cellSpace = vec2(fract(pol/slice), fract(-0.5*iTime+radSlice));
    cellH = hash(vec3(cellRad,cellPol,50.))*0.5+0.5;

    starRad = (cellH.y*0.032+0.02)/rad;
    starProb = cellH.x;
    starOs = cellH.z-0.5;
    starLum = cellH.z;
    stars = max(stars, starProb>0.4 ? 0. : starLum*smoothstep(starRad+0.01,0.01,length(-starOs+cellSpace-0.5)));
    stars = sat(stars);

    //Background
    float bgNoise = fbm(vec3(2.*oldUv/pow(rad,0.35),iTime*0.175))*0.5+0.5;
    bgNoise = sat (bgNoise);
	vec3 col = vec3(0);// 
    vec3 cbg1 = 0.25*vec3(0.00450,.0049,0.0138); vec3 cbg2 = vec3(0.0052,0.0055,0.03);
    col = mix(cbg1,cbg2,bgNoise);
    col = mix(col, 1.*vec3(0.003,0.003,0.035), smoothstep(0.5,0.71,bgNoise));
    col = mix(col*0.75,col,  smoothstep(0.3,0.35,bgNoise));
	col *=0.75;
    stars += (0.2*sat(mid) *(smoothstep(0.5,0.7,bgNoise)-smoothstep(0.9,1.,bgNoise)));
    col += mix (col, vec3(0.3),stars);

    col = mix (col, mainShape, zone);

    //shadow
    pol = atan(uv.y,uv.x) + PI;
    rad = length(uv); 

    pol += noise(vec2(rad*50.,0.5*iTime));
    pol = mod(pol, 2.*PI);   
    float v = sat((1.-zone)*(1.-(rad-rMax )/ (0.05+sin(pol*2.)*0.025)));
    col = mix(col, col*0.2, v);

    //vignette and noise
    col = mix(col,col*0.5,hash(oldUv).x);
    col *= 1./pow((pow(sqrt(dot(uvNorm,uvNorm)*.4),2.)+1.),2.5);

    //texture
    float pap = 1.-length((fract(oldUv*70.+vec2(0.,noise(0.5*oldUv*100.+5000.)))-0.5)/0.7);
	pap = sat(pap+0.5);
	col *= pap;

    //the end
    col = pow(col,vec3(0.45));
    fragColor = vec4(col,1.0);
}

#define COUNT 5
#define SIZE 4

#define LIGHT_POWER 80.
#define SPECULAR_POWER 20.
#define AMBIENT .3

vec4 spheres[] = vec4[COUNT * SIZE](
    // Spheres: center and radius, color, movement speed, movement amplitude
    // Light source
    vec4(-7.5, 0., 31., 0.1), vec4(1., 1., 1., 1.),
    vec4(2., 1., 0, 0), vec4(0, 2., 0, 0),
    // 1
    vec4(-4., 0., 39., 2), vec4(.2, .9, .2, 1.),
    vec4(1., 1.1, 2., 0), vec4(2, 3, 5.1, 0),
    // 2
    vec4(3., 0., 42., 3.), vec4(.9, .4, .5, 1.),
    vec4(1.3, 1.7, 1.5, 0), vec4(1, 4, 5.1, 0),
    // 3
    vec4(2, -1., 43., 1.5), vec4(.2, .3, 1., 1.),
    vec4(2., 1., 2., 0), vec4(7, 1, 7, 0),
    // "Floor"
    vec4(0, -800006, 0, 800000), vec4(1.7, 1.0, .7, 1),
    vec4(0, 0, 0, 0), vec4(0, 0, 0, 0)
);

// Calculate a moving sphere center
vec3 sphereCenter(int sphere) {
    vec4 sD = spheres[sphere * SIZE];
    vec4 sS = spheres[sphere * SIZE + 2];
    vec4 sA = spheres[sphere * SIZE + 3];
    vec3 c = vec3(
        sD.x + sin(iTime * sS.x) * sA.x,
        sD.y + sin(iTime * sS.y) * sA.y,
        sD.z + cos(iTime * sS.z) * sA.z);
    return c;    
}

// Find a nearest ray-sphere intersection
// o - ray origin
// l - ray direction (normalized)
// ignore - sphere to ignore when detecting intersection (used for shadows)
// d - distance output
// Returns sphere number, -1 if no intersection found
int findIntersection(vec3 o, vec3 l, int ignore, out float d) {
    int sphere = -1;
    d = 1e5;

    for (int i = 0; i < COUNT; i++) {
        if (i == ignore) {
            continue;
        }
        vec3 c = sphereCenter(i);
        float r = spheres[i * SIZE].w;

        // Ray-sphere intersection formula
        vec3 t1 = o - c;
        float t1l = length(t1);
        float t2 = dot(l, t1);
        float s = t2 * t2 - t1l * t1l + r * r;
        if (s >= 0.) {
            float ss = sqrt(s);
            float sd = min(-t2 + ss, -t2 - ss);
            if (sd >= 0. && sd < d) {
                sphere = i;
                d = sd;
            }
        }
    }
    return sphere;
}

// Trace a single ray
// camO - camera origin
// camL - camera ray direction (normalized)
// Returns a fragment color
vec3 trace(vec3 camO, vec3 camL) {
    float d = 0.;
    int sphere = findIntersection(camO, camL, -1, d);

    if (sphere == -1) {
        // There was no intersection, return background color
        return vec3(0, 0, 0);
    }

    vec3 lightColor = spheres[1].xyz;

    if (sphere == 0) {
        // It's a light source, don't need to shade it
        return lightColor;
    }

    vec3 lightPoint = sphereCenter(0);

    // Sphere color
    vec3 sColor = spheres[sphere * SIZE + 1].xyz;
    vec3 aColor = sColor * vec3(AMBIENT, AMBIENT, AMBIENT);

    // Intersection point
    vec3 iPoint = camO + camL * d;
    vec3 iNormal = normalize(iPoint - sphereCenter(sphere));

    // Light direction vector
    vec3 lightDir = normalize(lightPoint - iPoint);

    // Check if there's another sphere between this one and the light source
    float dShadow = 0.;
    int shadowedBy = findIntersection(iPoint, lightDir, sphere, dShadow);
    dShadow = float(shadowedBy + 1) / 5.0;
    if (shadowedBy != 0) {
        // We're under shadow, use ambient color
        return aColor;
    }

    // Lighting (diffusion and specular)
    float cosA = clamp(dot(iNormal, lightDir), 0., 1.);
    float cosS = clamp(dot(-camL, reflect(-lightDir, iNormal)), 0., 1.);

    float dSquared = pow(length(iPoint - lightPoint), 2.);

    return aColor +
        sColor * lightColor * cosA * LIGHT_POWER / dSquared +
        lightColor * pow(cosS, SPECULAR_POWER) * LIGHT_POWER / dSquared;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Camera
    vec3 camO = vec3(0, 0, 0);
    vec3 camL = normalize(vec3(uv.x, uv.y, 7));

    // Ray-tracing
    fragColor = vec4(trace(camO, camL), 1.);      
}

float sphereDE(vec3 position, vec3 center, float radius)
{
    return max(0.0, length(position - center) - radius);
}
float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sceneDE(vec3 position)
{
    float answer = min(sphereDE(position, vec3(sin(iTime)*5.0, 0.0, 0.0), 2.0),
                       sphereDE(position, vec3(-sin(iTime)*5.0, 0.0, 0.0), 2.0));
    answer = min(answer, sphereDE(position, vec3(cos(iTime*2.0)*10.0, 0.0, -sin(iTime*2.0)*10.0), 1.0));
    answer = min(answer, sdPlane(position, normalize(vec4(0.0, 1.0, 0.0, 2.0))));
    return answer;
}

vec3 estimateNormal(vec3 p) {
    const float EPSILON = 1e-4;
    return normalize(vec3(
        sceneDE(vec3(p.x + EPSILON, p.y, p.z)) - sceneDE(vec3(p.x - EPSILON, p.y, p.z)),
        sceneDE(vec3(p.x, p.y + EPSILON, p.z)) - sceneDE(vec3(p.x, p.y - EPSILON, p.z)),
        sceneDE(vec3(p.x, p.y, p.z  + EPSILON)) - sceneDE(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 raymarching(vec3 eye, vec3 dir, vec3 inf)
{
    const float MAX_STEPS = 200.0;
    const float MIN_DIST = 1e-5;

    float steps = 0.0;
    float totalDist = 0.0;
    while (steps++ < MAX_STEPS)
    {
        vec3 pos = eye + totalDist * dir;
        float delta = sceneDE(pos);
        totalDist += delta;

        if (delta < MIN_DIST) break;
    }

    vec3 finalPos = eye + totalDist * dir;
    // if finalPos > MIN_DIST, infinity
    return finalPos + step(MIN_DIST, sceneDE(finalPos)) * inf;  
}

vec3 phongLightningModel(float coeffDiffuse,
                         vec3 eye,
                         vec3 pos,
                         vec3 normalInPos,
                         vec3 lightPos, 
                         vec3 lightColor)
{
    vec3 lightDir = normalize(lightPos - pos);
   	float angleLightDir = dot(normalInPos, lightDir);

    return lightColor * (coeffDiffuse * angleLightDir) * step(0.0, angleLightDir);
}

vec3 computeLightning(vec3 eye, vec3 pos)
{
    const vec3 light1Pos = vec3(0, 10, 0);
    const vec3 light1Color = vec3(0.8, 0.8, 0.8);
    const vec3 ambient = vec3(0.08, 0.08, 0.08);
    vec3 normalInPos = estimateNormal(pos);

    return ambient + phongLightningModel(0.8,
                                         eye,
                                         pos,
                                         normalInPos,
                                         light1Pos,
                                         light1Color);
}


vec4 rayDir(vec2 uv, float fov, float aspect)
{
    vec2 coeffs = ((uv - 0.5) * 2.0) * tan(fov/2.0);
    coeffs.x *= aspect;
    vec4 dir = vec4(coeffs, -1.0, 0.0);

    return normalize(dir);
}

mat4 lookAt(vec3 eye, vec3 on, vec3 up)
{
    vec3 f = normalize(on - eye);
    vec3 r = cross(f, up);
    vec3 u = cross(r, f);

    return mat4(
        vec4(r, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float aspect = iResolution.x / iResolution.y;

    // rayDir
    vec3 eye = vec3(cos(iTime/2.3)*20.0, 2.0, sin(iTime/2.3)*20.0);
    vec3 center = vec3(0.0, 0.0, 0.0);
    mat4 worldToCam = lookAt(eye, center, vec3(0.0, 1.0, 0.0));
    vec3 dir = (worldToCam * rayDir(uv, radians(45.0f), aspect)).xyz;

    vec3 pos = raymarching(eye, dir, vec3(1e4, 1e4, 1e4));
    vec3 color = computeLightning(eye, pos);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

#define SIN(t) (.5*sin(t) + .5)
#define ss(a,b,c) smoothstep(a,b,c)
#define tt iTime


float eye(vec2 uv, vec2 center, float rad, float falloff)
{
    return smoothstep(rad + falloff + .001, rad - falloff, length(uv-center));
}

vec3 eyeRGB(vec2 uv, vec2 center, float rad, float falloff, vec2 modul)
{
    float eyer = eye(uv, center, rad, falloff);
    falloff = falloff*modul.x + modul.y;
    float eyeg = eye(uv, center, rad, falloff);
    falloff = falloff*modul.x + modul.y;
    float eyeb = eye(uv, center, rad, falloff);

    // Time varying pixel color
    return vec3(eyer, eyeg, eyeb);

}

float smile(vec2 uv, vec2 center, float amount)
{

    float rad = .4;
    float falloff = .01;
    float smile0 = eye(uv, center, rad, falloff);
    float smile1 = eye(uv, center+vec2(0.,.1 *amount), rad, falloff);

    return smoothstep(0., 1., smile0 - smile1);
}

vec2 rot(vec2 uv, float ang)
{
    vec2 i = vec2(cos(ang), sin(ang));
    return vec2(uv.x*i.x - uv.y*i.y, uv.x*i.y + uv.y*i.x);
}

float maskT(float t, vec2 inter, float falloff)
{
    float mid = (inter.x + inter.y)/2.;
    float amp = abs(inter.x - mid);
    return ss(amp+falloff, amp-falloff, abs(t - mid)); 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    // eyes

    float tm10 = mod(tt,10.);
    float teyeslide = ss(2. + 8.*float(tm10>9.5),
                   9. + .5*float(tm10>9.5),
                   tm10);

    vec2 eyeloc = vec2(.4);
    vec3 eye = eyeRGB(uv, eyeloc, .3, .1, vec2(1., 0.1));
    //blinking
    float t0 = maskT(mod(tt, 4.), vec2(0.4, .5), .01);
    t0 = max(t0,maskT(mod(tt, 4.), vec2(0.2, .3), .01));
    t0 = max(t0,maskT(mod(tt, 4.), vec2(2., 2.3), .1));
    eye *= 1. - 200.*t0*abs(uv.y - .4);

    vec2 eyeloc2 = vec2(-.4, .4 + teyeslide*(-.04));
    vec3 eye2 = eyeRGB(uv, eyeloc2 + vec2(0., -.3*teyeslide), .3, .1, vec2(1., 0.1));

    // eyebrow
    eye *= ss(0.3,.0, dot(uv-eyeloc + vec2(0., -.3*teyeslide), vec2(1.,1.)));
    eye2 *= ss(0.3,.0, dot(uv-eyeloc2, vec2(-1.,1.)));

    vec3 iris = eyeRGB(uv, vec2(.4), .1, .1, vec2(.2, 0.));
    vec3 iris2 = eyeRGB(uv, vec2(-.4,.4 + teyeslide*(-.2)), .1, .1, vec2(.2, 0.));

    // Time varying pixel color
    vec3 col = eye + eye2 - iris - iris2;

    float t1 = maskT(mod(tt, 3.), vec2(0.2, 1.2), .3);
    t1 = max(t1, maskT(mod(tt, 5.), vec2(2., 2.7), .2));
    float t2 = maskT(mod(tt, 2.), vec2(0.5, 0.7), .1);
    float smil = smile(uv*vec2(1. + t2*.1,1.+ t1*.2), vec2(0.,-.2), .5);
    smil *= ss(0.,.1, SIN(uv.x * 80.));
    float smil2 = smile(rot(uv*1.8,.5), vec2( .7,.4), .2 + .05*sin(35.*tt));
    float smil3 = smile(rot(uv*1.8,-.5), vec2(-.7,.4), .2);

    col += smil;
    col += smil2;
    col += smil3;

    col = max(col, .6*eyeRGB(uv, vec2(.0), .9, .05, vec2(1., 0.45)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

/**

I wanted to see if I could make a 2D "kamehameha" like
blast with 3D SDFs

I probably don't need ALL the code that's here
for a simple scene like this, but I enjoyed setting
this whole "engine" up and play around with it.

First time I try this approach from scratch, I mostly
wanted to see how much I understand..

*/

#define eps 0.001
#define max_dist 1000.
#define PI 3.141592654

#define BACKGROUND 0.
#define DOUBLE_RING 0.
#define CRYSTAL 1.
#define KAMEHAMEHA 2.

float time;


mat2x2 rot(float deg)
{
    float ang = deg * PI / 180.;
    float s = sin(ang), c = cos(ang);
    return mat2x2(c, s, -s, c);
}

float torus(vec3 p, vec3 pos, vec2 width, float radius)
{
    p += pos;

    vec2 q = vec2(length(p.xz)-width.x - radius,p.y);
    return length(q)-width.y;
}

float doubleRing(vec3 p)
{
    float dtime = sqrt(sqrt(2.*time));
    vec3 pivot = vec3(0, 0, 0.);

    //p-= pivot;
    //p += pivot;

    float compl;

    compl= sin((dtime));

    // Bottom ring
    vec3 pos1 = pivot;
    float rad1 = .5 * compl;
    vec2 w1 = vec2(.01) + .02 * vec2(0., sin(dtime*3.25));
    float t1 = torus(p, pos1, w1, rad1);

    // Top ring
    vec3 pos2 = pos1 + vec3(0, -.2, 0.);
    float rad2 = .35 * compl;
    vec2 w2 = vec2(.01) + .02 * vec2(0., sin(3.25*dtime));
    float t2 = torus(p, pos2, w2, rad2);
    //time = tt;

    return min(t1, t2);
}

float ball(vec3 p, vec3 pos, float rad)
{
    return length(p - pos) - rad;
}

float kamehameha(vec3 p, vec3 pos, vec3 c )
{
    p += pos;
    float r = .1;

    p.y -= clamp( p.y, 0.,.3)+.07;
    float l = length(p) - r - 1.1*sin(p.y * max(.0, .9 + .05*sin(time*50.)));

    return l+time/10.;

}

vec2 blast(vec3 p)
{
    p.xz *= rot(55.);
    p.yz *= rot(-55.);
    float d = doubleRing(p);
    float b = ball(p, vec3(0, -.1, 0), .04);

    float k = kamehameha(p, vec3(0, .2, 0), vec3(.1));
    float obj = 0.;

    if(k < d)
    {
        d = k;
        obj = KAMEHAMEHA;
    }

    if(b < d)
    {
        d = b;
        obj = CRYSTAL;
    }

    return vec2(obj, d);
}

vec2 map(vec3 p)
{    
    return blast(p);
}

vec3 deriv(vec3 p)
{
    return normalize(vec3(
        map(p + vec3(eps, 0, 0)).y - map(p - vec3(eps, 0, 0)).y,
        map(p + vec3(0, eps, 0)).y - map(p - vec3(0, eps, 0)).y,
        map(p + vec3(0, 0, eps)).y - map(p - vec3(0, 0, eps)).y
        ));
}

vec3 lighting(vec3 newRay, vec3 light, vec2 data)
{
    vec3 col;

    if(data.x == KAMEHAMEHA)
    {
        vec3 kLight = vec3(0, 0, -5);
        kLight.yz *= rot(-time*2500.);
        kLight.xz *= rot(-time*1500.);
        float dt = dot( deriv(newRay), kLight );

        if(dt > .5 * sin(newRay.x*10. + 10.*time))
            col = vec3(1., .2, 0.);
        else col = vec3(1., 0, 0);

        vec3 kLight2 = vec3(0, 1, 0);
        kLight2.yz *= rot(-time*2000.);        

        dt = dot( deriv(newRay), kLight2 );

        if(dt < .5 * sin(newRay.x*10. + 10.*time))
            col *= vec3(1., .5, .9);
        else if(dt < .7)
            col = vec3(1., 1., 0);



        kLight = vec3(-5.*sin(time*100.), -sin(time), -7);
        //kLight.yz *= rot(-time*2500.);
        //kLight.xz *= rot(-time*1500.);
        dt = dot( deriv(newRay), kLight )/5.;

        if(dt > .4 * cos(newRay.x*time))
            col = vec3(1.);

        //col = vec3(dt);
    }else
    {
        float dt = dot( deriv(newRay), light );

        if(data.x == DOUBLE_RING)
            col = vec3(240., 0., 56.)/255.;

        else if(data.x == CRYSTAL)
            col = vec3(0, 0, 1);

        if(dt > .5 + .2 * sin(newRay.x*10. + 10.*time))
            col = vec3(0.);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 camPos = vec3(.2, 0., 0);
    vec3 ro = camPos + vec3(0., 0, -1.);

    vec3 ta = camPos + vec3(0., 0, 1);

    // Don't really need cam vectors since I'm not moving
    // the camera around..

    /*vec3 fw = normalize(ta - ro);
    vec3 ri = normalize(vec3(fw.z, 0, -fw.x));
    vec3 up = cross(fw, ri);*/
    vec3 rd = /*mat3x3(ri, up, fw) **/ vec3(uv, 1);


    // THIS line is what sets the pace of the animation
    //time = mod(iTime, PI/2. - .1);

    if(iMouse.z> 0.)
		time = mod(iMouse.x/iResolution.y, PI-.5);
    else
    //if(time > PI/2.-5.)
        time = mod(iTime, PI-.5);

    float dist, t;
    vec3 newRay;
    vec2 data;
    for(int i = 0; i < 256; i++)
    {
        newRay = ro + t * rd;
        data = map(newRay);
        dist = data.y;
        if(dist < 0.01)
            break;
        else if(t > max_dist)
        {
            newRay = vec3(max_dist);
            break;
        }
        t += dist *.7;
    }

    // Time varying pixel color

    vec3 col = vec3(0);
    if(dist < max_dist)
    {
        vec3 light = normalize(vec3(1, 1, -2));
        col = lighting(newRay, light, data);
        col *= lighting(newRay, vec3(0, sin(time*5.), -1. + sin(time)), data);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Hash function by David Hoskins: https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

#define CYCLE 1.
