#define THRESHOLD .5

float ease(float t){
	//return t<.5 ? 2.*t*t : -1.+(4.-2.*t)*t;
    return t<.5 ? 8.*t*t*t*t : 1.-8.*(--t)*t*t*t;
    //return 1.-(--t)*t*t*t;
    //return t;
}

float complicatedNoise(float t, vec2 uv){
	float f1 = float(hash12(uv+floor(t)*10.) < THRESHOLD);
    float f2 = float(hash12(uv+floor(t+1.)*10.) < THRESHOLD);
    float f3 = float(hash12(uv+floor(t+2.)*10.) < THRESHOLD);
    return ((f1+f2)*.5+f3)*.5;
}

float getColor(vec2 uv){
    float tP = iTime/CYCLE+hash12(uv*10.);
    float tN = (iTime/CYCLE+1.)+hash12(uv*10.);
    // Output to screen
	float prev = complicatedNoise(tP, uv);
    float next = complicatedNoise(tN, uv);
    return mix(prev, next, ease(fract(tP)));
    //return vec4(prev);
}

