vec2 hash( vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}


float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float opDisplace( vec3 p, float noiseColor )
{
    vec4 plo = vec4(.0,.10,.0,.2);
    float d1 = sdPlane(p, plo);
    float d2 = noiseColor;
    return d1+d2;
}

/* ROTATIONS */

void rX(inout vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    p.y = c * q.y - s * q.z;
    p.z = s * q.y + c * q.z;
}

void rY(inout vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    p.x = c * q.x + s * q.z;
    p.z = -s * q.x + c * q.z;
}

void rZ(inout vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    p.x = c * q.x - s * q.y;
    p.y = s * q.x + c * q.y;
}

float nearestSurface(vec3 rp, float nclr) {

    float msd = 99999.;
    msd = min(msd, opDisplace(rp, nclr));

    return msd;
}

/* RAY MARCHING */

//march a single ray
vec4 marchRay(vec3 ro, vec3 rd) {

    float d = 0.0; //distance marched
    vec4 pc = vec4(0.); //pixel colour

    for (int i = 0; i < MAXIMUM_STEPS; ++i) {

        vec3 rp = ro + rd * d; //ray position
        rp *= .5f;
        float f = noise(vec2(rp.x, rp.z));
		vec2 uv = vec2(rp.x*0.5f, rp.z*0.5f);
        mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );

        float hills = 0.36;


        f  = 0.5000*noise( uv ); uv = m*uv;
        f += 0.2500*noise( uv ); uv = m*uv;
        f += 0.1250*noise( uv ); uv = m*uv;
        //f += 0.0625*noise( uv ); uv = m*uv;
        f = 0.3 + hills*f;

        float ns = nearestSurface(rp, f);
        d += ns;

        if (abs(ns) < DISTANCE_THRESHOLD) {
            float clf = 1.-f * 3.;

            if(clf > 0.3f){
            	pc = vec4(clf, clf, clf, 1.)*2.7;
            }else{
            	pc = vec4(clf*0.3, clf*0.4, clf*0.2, 1)*5.;
            }
            pc *= d*0.06;
            break;
        }

        if (d > FAR_CLIP) {
            pc = vec4(0.1, 0.3, 0.9, 1.);
            pc *= 1.-smoothstep(0.02, 0.33, rd.y) + 0.8;
            break;
        }
    }

    return pc;
}

