    vec3 nor = calcNormal(uv);
    nor.xy *= .4;
    nor = normalize(nor);

    vec3 mat = getColor(h);
    mat = clamp(mat, 0., 1.);
    vec3 ld = normalize(vec3(1,-1.,1));
    vec3 ha = normalize(ld - vec3(0.,0,-1));

    vec3 col = vec3(0);
    col += mat * .8;
    col += .2 * mat * pow(max(dot(normalize(nor + vec3(.0,0,0)), -ld), 0.), 3.);
    col += .3 * h * pow(dot(normalize(nor + vec3(.0,0,0)), ha), 20.);

    //if(h > 1.) col = vec3(0,1,0);
    //if(h < 0.) col = vec3(0,0,1);

	//col = mix(col, getColor(fragCoord.x/iResolution.x), step(abs(uv.y), .1));

    fragColor = vec4(col, 1.);
}

#define PI 3.14159265359
#define PI2 6.28318530718
#define S01(t) sin(t) * .5 + .5
#define C01(t) cos(t) * .5 + .5
#define S(a,b,t)  mix(a, b, S01(t))
#define C(a,b,t)  mix(a, b, C01(t))
#define BPM 130.
#define BPS BPM/60.

float lineDF(vec2 a, vec2 b, vec2 p){
    vec2 ab = b-a;
    vec2 ap = p-a;
    float t = dot(ap,ab)/dot(ab,ab);
    t = clamp(t,0.,1.);
    vec2 c = a + t*ab;
    return length(p-c);
}


