float CastRay(vec3 rayOrigin, vec3 rayDir)
{
    float t;

    for(int i=0; i<128; i++)
    {
        float res = map(rayOrigin + rayDir * t);

        if(res < (0.0001*t))
        {
            return t;
        }

        t += res;
    }

    return -1.0;
}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
    return normalize(   e.xyy*map(  pos+e.xyy   )   +
                        e.yyx*map(  pos+e.yyx   )   +
                        e.yxy*map(  pos+e.yxy   )   +
                        e.xxx*map(  pos+e.xxx   ));
}

float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        t += h;
    }
    return 1.0;
}

vec3 render(vec3 ro, vec3 rd)
{
    float t = CastRay(ro, rd);

    vec3 col = vec3(0.0);

    if(t == -1.0)
    {
        col = vec3(0.0);
    }
    else
    {
        vec3 pos=ro+rd*t;
        vec3 N = calcNormal(pos);
        vec3 L = vec3(-1.0, 1.0, 1.0);

        float light = clamp(dot(N, L), 0.0, 1.0);
        vec3 spec = vec3(pow(clamp(dot(N, L), 0.0, 1.0),8.0));
        float fog = 0.2 * t; // is it ?

        float shad = shadow(pos, L, 0.02, 2.5);

        col = palette(
            light * fog, 
            vec3(.5),
            vec3(.5),
            vec3(.12),
            vec3(.5, .2, .9));

        col = mix(col*.33, col, shad);
    }

    return col;
}

vec3 CameraViewDir(vec2 uv, vec3 camPos, vec3 camTar)
{
    vec3 viewDir;
    vec3 forward = normalize(camTar - camPos);
    vec3 right = normalize(cross(vec3(.0, -1., .0), forward));
    vec3 up = normalize(cross(right, forward));
    float fPersp = 1.0;

    viewDir = normalize(uv.x * right + uv.y * up + fPersp * forward);

    return viewDir;
}

vec2 NormalizeScreen(vec2 st)
{
    vec2 uv = 2. * st/iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

