// Staircase function y=f(x)
//
// k>1 : flat horizontals
// k<1 : flat verticals
//
// The inverse function x=f^-1(x) is just the function itselft
// with parameter 1/k instead of k.
float staircase( in float x, in float k )
{
    float i = floor(x);
    float f = fract(x);

    float a = 0.5*pow(2.0*((f<0.5)?f:1.0-f), k);
    f = (f<0.5)?a:1.0-a;

    return i+f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    // coordinates    
    float dx = 3.0/iResolution.y;
    vec2  p = fragCoord.xy*dx;

    // animate staircase curve
    float k = pow( 2.0, 4.0*sin(3.1415927*iTime));

    // background
    vec3 col = vec3( 0.2 + 0.02*mod(floor(p.x)+floor(p.y),2.0) );

    // draw curve y=f(x)
    float y = staircase( p.x, k );
    col = mix( col, vec3(1.0,0.8,0.3), 1.0-smoothstep(0.0, 2.0*dx, abs(p.y-y) ) );

    // draw curve x=f^-1(x)
    float x = staircase( p.y, 1.0/k );
    col = mix( col, vec3(1.0,0.8,0.3), 1.0-smoothstep(0.0, 2.0*dx, abs(p.x-x) ) );

    fragColor = vec4( col, 1.0 );
}

// A little SDF ray-marching "framework".
// Much of the techniques are copied from IQ's demos and articles.

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Constants

const float tmin = 0.01;								// Ray minimum distance
const float tmax = 500.0;								
const int raySteps = 256;								// Max iterations when resolving a ray
const int shadowSteps = 50;								// Max iterations when resolving a shadow ray
const float limit = 0.0005;								// Convergence to surface limit when ray tracing
const float screenDist = 0.5;							// Screen distance as a fraction of screen width
const vec3 light = normalize(vec3(-0.5, -1.0, 0.6));	// Light direction
const float ambient = 0.2;								// Ambient light
const vec3 fog = vec3(0.5,0.7,0.9);						// Fog colour
const int bounces = 3;									// Max reflection bounces (actually bounces + 1, as includes initial ray)

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Data structures

struct lightingParams {
 	float diffuse;
    float specular;
    float shiny;
};

struct material {
    vec3 col;											// Surface colour
    lightingParams lighting;
    float reflective;									// Reflectiveness. 0 = not reflective. 1 = completely reflective (e.g. mirror).
};

struct rayInfo {
    vec3 origin;
    vec3 dir;
};

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Functions (implemented at the bottom)

// Get camera ray for specified pixel
rayInfo getRay(in vec2 fragCoord);

// Get VR ray
rayInfo getVRRay(in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir);

// Get sample point.
// Format is vec4:
//	x,y		= Material parameter (e.g. uv)
//	z		= Distance to nearest surface
//	w		= Material index (will be passed to getMaterial())
vec4 map(in vec3 p);

// Get material properties by index
material getMaterial(float index, vec2 uv);

// Get colour for ray that misses
vec3 getRayMissCol(in rayInfo ray);

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Trig

const float PI = 3.14159265;

vec3 rotatex(in vec3 p, float a) {
	return vec3(p.x,
                cos(a) * p.y - sin(a) * p.z,
                cos(a) * p.z + sin(a) * p.y);
}

vec3 rotatey(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.z,
                p.y,
                cos(a) * p.z - sin(a) * p.x);
}

vec3 rotatez(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

vec3 rotate(in vec3 p, in vec3 euler) {
 	return rotatey(rotatex(rotatez(p, euler.z), euler.x), euler.y);   
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Helper functions

bool checkerboard(in vec2 p) {
	float x = mod(p.x, 2.0);
    if (x < 0.0) x += 2.0;
    float y = mod(p.y, 2.0);
    if (y < 0.0) y += 2.0;
    return (x < 1.0) == (y < 1.0);
}

// Pinched from: http://iquilezles.org/www/articles/smin/smin.htm
float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Combining shapes
//
// Sample format is vec3:
//	x,y		= Material parameter (e.g. texture coordinates)
//	z		= Distance

// Union of two shapes
vec3 combine(in vec3 p1, in vec3 p2) { 
    return p1.z < p2.z ? p1 : p2;
}

// Smoothed union of two shapes
vec3 combine(in vec3 p1, in vec3 p2, float k) {
    return vec3(p1.z < p2.z ? p1.xy : p2.xy, sminCubic(p1.z, p2.z, k));
}

// Subtract shape p2 from p1
vec3 carve(in vec3 p1, in vec3 p2) { 
    return p1.z > -p2.z ? p1 : vec3(p2.xy, -p2.z);
}

// Intersection of two shapes
vec3 intersect(in vec3 p1, in vec3 p2) { 
    return p1.z > p2.z ? p1 : p2;
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Shape primitives

vec3 sphere(in vec3 p, in vec3 center, float rad) { 
    p -= center;
    return vec3(atan(p.x, p.y) * rad, atan(length(p.xy), p.z) * rad, length(p) - rad); 
}
vec3 box(in vec3 p, in vec3 center, in vec3 size) {
    p -= center;
    vec3 x = vec3(p.yz, abs(p.x) - size.x);
    vec3 y = vec3(p.xz, abs(p.y) - size.y);
    vec3 z = vec3(p.xy, abs(p.z) - size.z);
    return intersect(x, intersect(y, z));
}
vec3 cube(in vec3 p, in vec3 center, float size) 	{ return box(p, center, vec3(size)); }
vec3 planeLeft(in vec3 p, float x)					{ return vec3(p.yz, x-p.x); }
vec3 planeRight(in vec3 p, float x)					{ return vec3(p.yz, p.x-x); }
vec3 planeBottom(in vec3 p, float y)				{ return vec3(p.xz, y-p.y); }
vec3 planeTop(in vec3 p, float y) 					{ return vec3(p.xz, p.y-y); }
vec3 planeFront(in vec3 p, float z)					{ return vec3(p.xy, z-p.z); }
vec3 planeBack(in vec3 p, float z)					{ return vec3(p.xy, p.z-z); }
vec3 cylinder(in vec3 p, in vec3 center, float radius, float height) { 
    p -= center;
    vec3 tube = vec3(atan(p.x, p.z) * radius, p.y, length(p.xz) - radius);
    vec3 top = planeTop(p, height);
    vec3 bottom = planeBottom(p, -height);
    return intersect(tube, intersect(top, bottom));
}
vec3 torus(in vec3 p, in vec3 center, float radius, float thickness) { 
    p -= center;
    vec2 circleOffs = vec2(length(p.xz) - radius, p.y);
    return vec3(atan(p.x, p.z) * radius, atan(circleOffs.x, circleOffs.y) * thickness, length(circleOffs) - thickness);
}
vec3 plane(in vec3 p, in vec3 center, in vec3 n) { 
    return vec3(0, 0, dot(p - center, n)); 
}
vec3 pyramid(in vec3 p, in vec3 center, float width, float height) {
    vec3 top = center + vec3(0, height, 0);
    vec3 r = planeBottom(p, center.y);
    r = intersect(r, plane(p, top, normalize(vec3( height,width, 0))));
    r = intersect(r, plane(p, top, normalize(vec3(-height,width, 0))));
    r = intersect(r, plane(p, top, normalize(vec3( 0,width, height))));
    r = intersect(r, plane(p, top, normalize(vec3( 0,width,-height))));
    return r;
}

// Creating and combining full sample including material index
vec4 makes(in vec3 d, float material) {
    // Like a distance sample, but with w = material index.
    // Specifically:
    //	x,y	= material parameter (e.g. texture coord)
    //	z	= distance
    //	w	= material index
 	return vec4(d, material);   
}

vec4 merges(in vec4 result, in vec3 d, float material) {
 	return d.z < result.z ? makes(d, material) : result;
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Rendering

// Calculate ray direction for a given pixel
vec3 screenRay(in vec2 s) {
    s -= iResolution.xy / 2.0;
    return vec3(s / (screenDist * iResolution.x), 1);
}

// Simple lighting
float shading(in vec3 e, in vec3 n, in lightingParams p) {

    // Diffuse term
    float d = dot(n, -light) * p.diffuse;

    // Specular term
    vec3 h = -(normalize(e) + light) / 2.0;
    float s = pow(dot(n, h), p.shiny) * p.specular;

    return max(d + s, 0.0);
}

// Calculate normal at a point
vec3 normal(in vec3 p, float t) {

	// Calculate pixel size at point distance. 
    // This will be the distance of the normal samples from the original point.
    float s = 0.1 / t;

    // Sample relative distance along each axis
    float d = map(p).z;			// Need original distance to compare it to
    vec3 r = vec3(
        map(p + vec3(s,0,0)).z - d,
        map(p + vec3(0,s,0)).z - d,
        map(p + vec3(0,0,s)).z - d);

    return normalize(r);
}

// Cast regular ray
vec4 raycast(in vec3 from, in vec3 dir) {

    float t = tmin;
    vec4 d;
    for (int i = 0; i < raySteps && t < tmax; i++) {
        vec3 p = from + dir * t;
        d = map(p);
        if (abs(d.z) <= limit * t) {
            d.z = t;
            return d;
        }
        t += d.z;
    }    

    // Avoid flicker when polygons are edgewise to the camera.
    // In this case the ray cannot quite find the surface and runs out of steps.
    // Just return the last sample point.
    if (t < tmax) {
        d.z = t;
        return d;
    }

    return vec4(0,0,-1.0,0);    
}

// Cast shadow ray
float shadow(in vec3 from, in vec3 dir, float k) {
    float res = 1.0;
    float t = tmin;    
    for (int i = 0; i < shadowSteps && t < tmax; i++) {
     	vec3 p = from + dir * t;
        float d = map(p).z;
        if (d <= 0.0)
            return 0.0;
        res = min(res, k*d/t);
        t += d;
    }

    return res;
}

vec3 drawPixel(in rayInfo ray) {
    ray.dir = normalize(ray.dir);

    // Sum up reflection bounces    
    vec3 sumCol = vec3(0);
    float alpha = 1.0;
    for (int bounce = 0; bounce < bounces && alpha > 0.0002; bounce++) {

	    // Ray cast
	    vec4 r = raycast(ray.origin, ray.dir);

        // Distance is returned in r.z
	    // Miss?
	    if (r.z == -1.0)
	    {
            vec3 missCol = getRayMissCol(ray);
            sumCol += missCol * alpha;
	        break;
	    }

	    // Position and normal
	    vec3 p = ray.origin + r.z * ray.dir;
	    vec3 n = normal(p, r.z);

        // Get material info
        material mat = getMaterial(r.w, r.xy);        

        // Cancel reflection if on last bounce
        if (bounce == bounces - 1)
            mat.reflective = 0.0001;

        // Apply lighting
        float i = shading(ray.dir, n, mat.lighting);
        if (i > 0.0) 
            i *= shadow(p, -light, 16.0);    
        i = clamp(i + ambient, 0.0, 1.0);
        mat.col *= i;

        // Apply fog
        float f = clamp(r.z * r.z / 50000.0, 0.0, 1.0);
        sumCol += fog * f * alpha;
        alpha *= (1.0 - f);

        // Add to sum of colours
        mat.col *= (1.0 - mat.reflective);
        sumCol += mat.col * alpha;
        alpha *= mat.reflective;

        // Calculate reflection vector
        ray.origin = p;
        ray.dir = normalize(reflect(ray.dir, n));
    }    

    return sumCol;
}

// Main entrypoints

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rayInfo ray = getRay(fragCoord);
    vec3 col = drawPixel(ray);
    fragColor = vec4(col, 1);
}

void mainVR(out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir) 
{
 	rayInfo ray = getVRRay(fragCoord, fragRayOri, fragRayDir);
    vec3 col = drawPixel(ray);
    fragColor = vec4(col, 1);
}

//-----------------------------------------------------------------------------------------------------------------------------------

rayInfo getRay(in vec2 fragCoord) {
    rayInfo ray;

    vec3 ang = vec3(0.65, iTime * 0.05, 0);
    ray.origin = vec3(0,-2,0) + rotate(vec3(0, 0, -30), ang);
    ray.dir = rotate(screenRay(fragCoord), ang);
    return ray;
}

rayInfo getVRRay(in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir) {
 	rayInfo ray;
    ray.origin = fragRayOri * 50.0 + vec3(0,40,10);
    ray.dir = fragRayDir;
    return ray;
}

vec3 getRayMissCol(in rayInfo ray) {
    vec3 skycol = fog;
    if (ray.dir.y > 0.1) {
        vec2 t = ray.dir.xz / ray.dir.y;
        float cloud = texture(iChannel3, t * 0.1 + iTime * vec2(0.01, 0.007)).x 
            + texture(iChannel3, t * 0.3 + iTime * vec2(0.005, -0.003)).x * 0.3
            + texture(iChannel3, t * 1.0 + iTime * vec2(-0.004, -0.006)).x * 0.1;
        cloud = clamp(cloud - 0.6, 0.0, 1.0);
        skycol += vec3(cloud);            
    }

    return skycol;
}

// Material types.
// Returned by map(). Then passed into getMaterial() to get colour, lighting etc.
const float redMat 		= 1.0;
const float blueMat 	= 2.0;
const float mirrorMat 	= 3.0;
const float marbleMat 	= 4.0;
const float woodMat 	= 5.0;
const float checkerMat 	= 6.0;
const float metalMat 	= 7.0;

// Lookup material colour etc
material getMaterial(float matType, vec2 uv) {

    // Set defaults
    material mat;
    mat.lighting.diffuse = 0.6;
    mat.lighting.specular = 0.8;
    mat.lighting.shiny = 32.0;
    mat.reflective = 0.0001;

    if (matType == redMat) {        
        mat.col = vec3(0.8, 0.3, 0.2);
        mat.reflective = 0.05;
        mat.lighting.specular = 2.5;
    }
    else if (matType == blueMat) {        
        mat.col = vec3(0.3, 0.5, 0.9);
    }        
    else if (matType == marbleMat) {
        mat.reflective = 0.15;
        mat.col = texture(iChannel0, uv * 0.05).xyz;
    }
    else if (matType == mirrorMat) {
        mat.reflective = 0.6;
        mat.col = vec3(0.7);
    }
    else if (matType == woodMat) {
        mat.col = texture(iChannel1, uv * 0.05).xyz;  
    }
    else if (floor(matType) == checkerMat) {
        float p = (matType - checkerMat) * PI * 2.0;
        mat.col = vec3(sin(p), sin(p + 2.0), sin(p + 4.0)) * 0.4 + vec3(0.6);
        if (checkerboard(uv * PI / 2.0))
            mat.col = mix(mat.col, vec3(1), 0.4);
    }
    else if (matType == metalMat) {
        mat.reflective = 0.15;
        mat.col = texture(iChannel2, uv * 0.05).xyz * 2.0;              
    }

    return mat;
}

vec3 coffeeCup(in vec3 p, in vec3 center) {

    // Create a vec3 distance sample by combining distance samples for primative shapes.    
	p -= center;

    float d = length(p - vec3(0, 2.75, 0.6));
    if (d > 5.5) 
        return vec3(0,0,d - 5.0);    

    // Component primatives
    vec3 base = sphere(p, vec3(0, 2, 0), 3.0);
    base = intersect(base, planeBottom(p, 0.0));        
    vec3 body = cylinder(p, vec3(0, 3, 0), 3.0, 2.0);    
    vec3 hole = cylinder(p, vec3(0, 4, 0), 2.8, 3.0);    
    vec3 rim = torus(p, vec3(0, 5, 0), 2.9, 0.1);    
    vec3 handle = torus(p.zxy, vec3(3, 0, 2.85), 1.5, 0.3);

    // Combine together
    return combine(carve(combine(combine(base, body), handle, 0.5), hole), rim);
}

vec4 map(in vec3 p) {

    // Combine shapes with materials
    vec4 r = makes(torus(p, vec3(16, 1.0, 0), 5.0, 1.0), checkerMat + 0.55);
    r = merges(r, cube(p, vec3(-18, 3.0, 3), 3.0), woodMat);
    r = merges(r, cylinder(p, vec3(-3, 4, 10), 2.0, 4.0), checkerMat + 0.0);
    r = merges(r, sphere(p, vec3(sin(iTime * 0.3 * 2.0) * 15.0, 4, cos(iTime * 0.3) * 20.0), 4.0), mirrorMat);
    r = merges(r, pyramid(p, vec3(-18, 0, -4), 4.0, 6.0), redMat);    
    vec3 cupp = rotatey(p - vec3(0, 0, -10), -PI*0.5);
    r = merges(r, coffeeCup(cupp, vec3(0)), metalMat);
    r = merges(r, planeTop(p, 0.0), marbleMat);

    return r;
}

float rnd(vec2 p)
{
    return fract(sin(dot(p, vec2(12.9898, 78.45262)))*43256.53);
}
mat2 rot(float a)
{
 	return mat2 (cos(a), -sin(a), sin(a), cos(a));   
}
//unused
float square(vec2 p, float ss, float b)
{
	p = abs(p);
	float sq = max(p.x, p.y)-b; 
    return sq;
}

float S(vec2 p, float r)
{    
float ss = 0.05;
    //shift so it's not directly under
    vec2 shift = vec2(0.03)*rot(iTime/2.);
    float c = length(p+shift);
    return 1.0-smoothstep(r-ss, r+ss, c);
}    
//rim lighting circle
float R(vec2 p, float r)
{    
float ss = 0.002;
    //shift so it appears to be boucing off one side
    vec2 shift = vec2(0.003)*rot(iTime/2.);   
    float c = length(p-shift);
    return 1.0-smoothstep(r-ss, r+ss, c);
} 
//used for the black inner circle
float R2(vec2 p, float r)
{    
float ss = 0.005; 
    float c = length(p);
    return 1.0-smoothstep(r-ss, r+ss, c);
}   
//circle used for both gold and red
float circle(vec2 p, float r)
{
	float ss = 0.002;
    float c = length(p);
    return 1.0-smoothstep(r-ss, r+ss, c);
}

//for gold rim texture
float SIN(vec2 p, float r)
{

float ss = 0.001;
    float c = length(p);
    return abs(sin(c*340.))*(1.0-smoothstep(r-ss, r+ss,c));
}
//unused
float H(vec2 p, float r)
{    
float ss = 0.1;
    float c = length(p);
    return 1.0-c;//1.0-smoothstep(r-ss, r+ss, c);
}

//unused
float Nring(vec2 p, float r, float i)
{

 return circle(p, r)-circle(p, r-0.005);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
